@-moz-document domain("www.leitstellenspiel.de") {

	body {
		background-color: #fff !important;
		font-family: Titillium Web, 'Ubuntu', Tahoma !important;
	}

	#map_adress_search_form  {
		margin-left: 20% !important;
		width: 200px;
	}

	.logo {
		margin-bottom: -80px;
	}

	.logo img[alt="Leitstellenspiel.de"] {
		content:url("http://i.imgur.com/voZsCJM.png") !important;
		margin-bottom: -80px;
	}

	.building_list_fms {
		width: 60px; 
		height: 20px;
		font-weight: normal !important;
		font-size: 15px !important;
	}

	.building_list_vehicle_element {
		font-weight: normal;
	}

	.navbar .nav>li>a {
		border: 0;
	}

	.navbar-inner {
		background-image: none;
		background-color: #e74c3c;
		min-height: 50px;
		padding-top: 10px;
	}

	#map {
		margin-top: -20px;
	}
	.span6 {
		width: 74% !important;
	}

	.span3 {
		width: 23% !important;
	}

	.footer {
		display: none;
	}

	#buildings_outer {
		margin-top: 20px;
                margin-left: 77% !important;
	}

	#missions_outer {
		margin-top: -20px;
	}

	.well {
		border: 0;
		box-shadow: inset 0 0px 0px rgba(0,0,0,0.05);
		background-color: #fff;
		
	}

	#radio_outer {
		width: 37% !important;
		margin-top: -490px;
		border-radius: 0;
		background-color: #fff;
		margin-left: 36%;
		height: 400px !important;
		
	}

	#radio {
		height: 400px;
	}

	#missions_outer {
		border-radius: 0;
		border: 0;
		
	}

	#missions {
		margin-top: 20px;
	}

	#buildings {
		height: 400px;
	}

	.missionSideBarEntry {
		margin-top: 10px;
	}

	#radio_messages {
		max-height: 400px;
	}

	#chat_outer {
		margin-top: -450px; 
		width: 34% !important;
	}

	.mission_list_caption {
		font-size: 15px;
		font-weight: 500;
	}

	a#building_list_li {
		font-size: 18px !important;
		font-weight: 700 !important;
		text-transform: uppercase;
	}

	.btn-success {
		background-image: none; 
		background-color: #2ecc71 !important;
		border-radius: 0;
		
	}

	.btn-success:hover {
		background-image: none; 
		background-color: #2ab253 !important;
		border-radius: 0;
		
	}

	.btn-danger {
		background-image: none;
		background-color: #e74c3c !important;
		border: 0;
		
	}

	.btn-danger:hover {
		background-image: none;
		background-color: #fc5342 !important;
		border: 0;
		
	}

	.btn {
		background-image: none;
		background-color: #ecf0f1;
		border: 0;
		font-size: 13px !important;
	}

	.btn-group>.btn:first-child {
		-webkit-border-top-right-radius: 0 !important; 
		border-top-right-radius: 0 !important; 
		-webkit-border-bottom-right-radius: 0 !important; 
		border-bottom-right-radius: 0 !important; 
		-moz-border-radius-topright: 0 !important; 
		-moz-border-radius-bottomright: 0 !important; 
	}

	.btn-group>.btn:last-child {
		-webkit-border-top-right-radius: 0 !important; 
		border-top-right-radius: 0 !important; 
		-webkit-border-bottom-right-radius: 0 !important; 
		border-bottom-right-radius: 0 !important; 
		-moz-border-radius-topright: 0 !important; 
		-moz-border-radius-bottomright: 0 !important; 
	}

	.btn-group>.btn:first-child {
		-webkit-border-bottom-left-radius: 0 !important; 
		border-bottom-left-radius: 0 !important; 
		-webkit-border-top-left-radius: 0 !important; 
		border-top-left-radius: 0 !important; 
		-moz-border-radius-bottomleft: 0 !important; 
		-moz-border-radius-topleft: 0 !important; 
	}

	.alarm_bottom {
		background-color: #e74c3c; 
	}
}
