css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aretegaming.com") {

    div.above {
        background: #2b2b2b

    }
    div.above {
        box-shadow: none

    }
    div.above {
        border-bottom: none

    }
    div#header {
        background:#2b2b2b

    }
    div#wrap {
        max-width:12000px
    }
    #wrap {
        padding: inherit
    }
    img#logo {
        background:transparent url(http://i.imgur.com/48jYw5E.png) no-repeat !important; padding:0px 0px 172px 676px !important; height:0px !important; width:0px !important; border: 0px !important;
    }
    div.navbar.clearit {
        background: #1E8BCE
    }
    div.navbar.clearit {
        border-color: #1E8BCE
    }
    div.navbar.clearit {
        box-shadow: 0px 1px 0px #1E8BCE inset
    }
    div.navbar.clearit {
        margin-top: 0px
    }
    div.navbar.clearit {
        padding: 0px 0px 0px 0px
    }
    div#header {
        border-bottom: none
    }
    div#search-box.searchback {
        margin-top: 164px
    }
    div#search-box.searchback {
        margin-right: -30px
    }
    div.navbar.clearit {
        font-size: 16px
    }
    ul#nav {
        margin-left: 40px
    }
    ul#nav li {
        padding: 6px 16px 6px 6px
    }
    img#logo {
        margin-left: 320px
    }
    img#logo {
        margin-top: -2px
    }
    div#content {
        background: #171717
    }
    div.breadcrumbs {
        background-color: #171717
    }
    div.breadcrumbs {
        border: none
    }
    div.breadcrumbs {
        box-shadow: none
    }
    ul.linklist.navlinks {
        border-bottom: none
    }
    ul.linklist.navlinks {
        overflow: unset
    }
    li.icon-home {
        margin-left: 29px
    }
    ul.linklist.leftside {
        margin-left: 1px
    }
    div.breadcrumbs {
        padding: 0px
    }
    div.breadcrumbs {
        text-shadow: none
    }
    div.breadcrumbs {
        text-decoration-color: #F8F8F8
    }
    #search-box a:link {
        display: none !important
    }
    input.button2 {
        display: none !important
    }
    ul#nav li {
        margin-left: 133px
    }
    li.icon-home {
        display: none !important
    }
    image.nicon {
        background:transparent url(http://i.imgur.com/48jYw5E.png) no-repeat !important; padding:0px 0px 10px 10px !important; height:0px !important; width:0px !important; border: 0px !important;
    }
    span.corners-top {
        background-image: none
    }
    span.corners-top span {
        background-image: none
    }
    div.forabg {
        background: #1E8BCE
    }
    div.forabg {
        box-shadow: 0px 0px 0px #1E8BCE inset
    }
    div.mChatBodyFix {
        background-color: #1E8BCE !important
    }
    div.mChatPanel div{
        display: none !important
    }
    div#mChatStats.mChatStats{
        margin-left: 0px
    }
    div#mChatStats.mChatStats{
        color: #ffffff
    }
    div.mChatPanel {
        padding: 0px
    }
    div#mChatStats.mChatStats{
        padding-left: 435px
    }
    ul.topiclist.forums{
        background-color: #fff
    }
    div.cathead{
        background: #1E8BCE
    }
    div.cathead{
        border: none
    }
    div.cathead{
        box-shadow: none
    }
    ul.linklist li a {
        color: #fff
    }
}
