css:
@-moz-document domain("androidpolice.com") {
.band.header {background: #393939 !important;}
h1.logo {background: #393939 !important;}
.band.sub-nav {background: #393939 !important;}
.band.content.main {background: #292929 !important;}
.band.sub-nav {border-bottom: 1px solid #656565 !important;}
.ap-new-articles-btn {    background: #2B2B2B !important;}
ul.sub-nav li a {    color: #A2A2A2 !important;}
.post {background: #393939 !important;border: 1px solid #656565 !important; }
.post-header h2 a {color: #fff !important;}
a {    color: #E6E6E6 !important;}
.post-date-time {color: #929292 !important;}
.share-box .shares {color: #929292 !important;}
ul.share-btns li {border: 1px solid #656565 !important; }
.categories {border-top: 1px solid #656565 !important; }
.categories li a {border-top: 1px solid #656565 !important; }
.categories ul.post-categories {border-left: 1px solid #656565 !important; }
.share-box .shares span.count {border: 1px solid #656565 !important; }
body {    color: #EAEAEA !important;}
.wp-polls .Buttons, input[type="submit"], input[type="reset"], input[type="button"], a.read-more, a.primary-btn, .wp-polls-ans a { background: #777777 !important;}
.banner-update {background-color: #757575 !important;border: 1px solid #656565 !important; }
.post {box-shadow: none !important;}
.wp-pagenavi {background: #393939 !important;border: 1px solid #656565 !important; box-shadow: none !important;}
.EmbeddedTweet {background: #292929 !important;border: 1px solid #656565 !important;}
.FollowButton {background: #191919 !important;border: 1px solid #656565 !important;}
.note-tip {background-color: #292929 !important;border: 1px solid #656565 !important;}
.note-tip .note-heading {background-color: #848484 !important;}
.categories li a {border: 1px solid #656565 !important;}
.note-warning {background-color: #292929 !important;border: 1px solid #656565 !important;}
.note-warning .note-heading {background-color: #848484 !important;}
strong {color: #fff !important;}
layout .ob-widget-header {color: #9E9E9E !important;}
#comments {background: #292929 !important;}
.section-header {border-bottom: 1px solid #656565 !important;}
.primary .post-footer {border: 1px solid #656565 !important;box-shadow: none !important; }
.nav-primary>ul>li.active>a {    color: #C7C7C7!important;}
h3, strong, strong a {color: #C7C7C7!important;}
.post-content .post-message p:last-child {color: #D6D6D6 !important;}
.textarea-wrapper {background: #393939 !important;border: 2px solid #656565 !important; box-shadow: none !important;}
.post .post-content tr:nth-child(2n) td {background: #292929 !important;}
.post .post-content th, .post .post-content td {border: 1px solid #656565 !important; }
.section-header {background: #292929 !important;}
blockquote.pr section.open {background: #292929 !important;}
blockquote.pr section {border-left: 1px solid #656565 !important; border-right: 1px solid #656565 !important; }
blockquote.pr section p {    color: #C7C7C7 !important;}
blockquote.pr {background: #292929 !important;}
blockquote.pr footer {border-left: 1px solid #656565 !important; border-right: 1px solid #656565 !important; border-bottom: 1px solid #656565 !important; }
blockquote.pr .fade-text {background:none !important;}
.btn {background: #6D6D6D !important;}
.alert.realtime {background: #393939 !important;}
.tablepress .even td {background: #393939 !important;}
.note-note {background: #292929 !important;border: 1px solid #656565 !important;}
.note-note .note-heading {background-color: #848484 !important;}
.pb-app-widget-discover .pb-app-widget-table {background: #292929 !important;}
.pb-app-widget-discover .pb-wd-icon {background: #292929 !important;}
.pb-app-widget-discover .pb-wd-footer {background: #292929 !important;}
.pb-app-widget-discover {border: 1px solid #656565 !important; }


blockquote p {
    text-shadow: 0px 0px 0 #191919;
    color: #EAEAEA;
    font-style: normal;
    font-size: 15px;
}

 .EmbeddedTweet-ancestor {background: #191919 !important;border: 1px solid #656565 !important;} 


.band.content.main .container.feature { 
  display:none;
}
          
.band.content.main .container .secondary.column {
  display:none;
}

.band.footer {
display:none
}

.band.content.main .container .primary.column {
width: auto;
}

.single-post-widget {
display:none
}

.article-nav {
display:none;
}

.bo-wrapper {
display: none;
}
}
