css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.soup.io/friends"),
               url("http://www.soup.io/fof"),
               url("http://www.soup.io/stalkers"),
               url("http://www.soup.io/everyone"),
               url("https://www.soup.io/friends"),
               url("https://www.soup.io/fof"),
               url("https://www.soup.io/stalkers"),
               url("https://www.soup.io/everyone")
     {
html
{
    height: 100%;
    width: 100%;
    margin: 0;
}

body {
    background-color: #000000 !important;
    height: 100%;
    width: 100%;
    font-size: 14px;
    color: white;
    margin: 0;
}

#pagebody
{
    width: 1024px;
    background-color: #121212;
    margin: 0 auto !important;
    display: block;
}

a { 
    color: white;
}

#barcontainer
{
    width: 1024px;
    display: block;
    margin: 0 auto !important;
}

#barfade
{
    display: none;
}

#bar
{
    width: 1024px;
    display: block;
    margin: 0 auto !important;
}


#menu
{
    background-color: #141414;
    margin: 0 auto !important;
}

.tv
{
    display: none !important;
}

.dropdown_body
{
   background: rgba(20, 20, 20, 1) !important;
}

.dropdown_body a
{
    color: #D6D6D6 !important;
}

div#logo
{
    display: none;
}

#head
{
    padding: 0px 20px 0px 20px !important;
    background-color: #4A4A4A;
    width: 1000px;
    margin: 0px !important;
    margin-top: 20px !important;
}

/* Content for the head. */

div.container
{
    width: 100%;
    display: block;
}

div.container_inner
{
    height: 100px !important;
    width: 400px;
}

div.container h2
{
    display: none;
}

.switchable1
{
    width: 100%;
    display: block;
}

.switchable2
{
    width: 100%;
    display: block;
}

a.admin-button
{
    border-radius: 0px;
    margin: 0 auto !important;
    display: block;
    width: 150px;
    text-align: center;
}

.search
{
    padding: 0px;
    margin: 0px;
    width: 500px;
    display: block;
}

.searchform h2
{
    height: 100px !important;
}

div.content-container
{
    width: 500px;
    padding: 5px;
    display: block;
    height: 100%;
}

#placement
{
    margin: 0;
    clear: both;
}

#main
{
    margin: 0 auto !important;
    width: 1024px !important;
}

/* Change content inside of posts. */

#posts
{
    margin: 0 auto !important;
}
#posts .content
{
    color: #E0E0E0;
    
}

#posts .content a
{
    color: #7DCFE8 !important;
    background-color: transparent !important;
}

#posts .content a:hover
{
    color: #BDE4F0 !important;
}

#posts
{
    padding: 5px;
    display: block;
}
.post
{
    background-color: #333333;
    width: 530px;
    border: 0px !important;
}

.post p
{
    margin: 0 auto !important;
}

#posts .post
{
    margin: 0 auto !important;
    margin-bottom: 20px !important;
}
.post_quote
{
     color: #CFCFCF !important;
}

span.body
{
    color: #EDEDED !important;
}

#posts h3 
{
    background-color: #3D3D3D;
    display: block;
    width: 520px !important;
    font-family: georgia;
    text-align: center;
}

div.description
{
    padding: 5px;
}

.caption {
    left: 2px !important;
    border-radius: 3px;
    margin-right: 2px;
    margin-left: -2px;
    margin-bottom: 0px;
}

.post .content blockquote
{
    color: #CFCFCF !important;
}

blockquote
{
    padding: 5px !important;
    border-left: 2px solid black !important;
}

img {
    margin: 0 auto !important;
    opacity: 0.8;
}

img:hover
{
    opacity: 1;
}
div.imagecontainer {
    margin: 0 auto;
}

.actionbar, 
body.repost-iframe #repost-container {
    width: 520px;
    margin-top: 15px;
    margin-right: 249px;
    right: 0 !important;
    border: #fff 1px solid;
    border-radius: 0px !important;
    display: block;
}

.post .actionbar li.visible a,
.post .actionbar li.open a {
    background: url("http://soup.io/images/black50.png") repeat scroll 0% 0% transparent;
    border-radius: 0px;
    margin-top: 15px;
}

#sidebar
{
    display: none;
}
}
