css:
@-moz-document domain("xetown.com") {
.fixed_header .header_wrap {
    background-color: #333;
}

.body .sub_desc {
    padding: 0px;
    margin: 0px;
    background: rgba(0,0,0,0);
    border: none;
    box-shadow: none;
    font-weight: bold;
}

.visual.sub .sub_title {
    width: 1150px;
}

.gnb > ul > li > a:hover .guide, .gnb > ul > li > a:active .guide, .gnb > ul > li > a:focus .guide, .gnb > ul > li.on > a .guide, .gnb>ul>li>a:hover, .gnb>ul>li>a:active, .gnb>ul>li>a:focus, .gnb>ul>li.on>a {
    color: #333;
    background-color: #EEE;
}

.gnb > ul .depth2 {
    background-color: #EEE;
}

.gnb > ul .depth2 > li:focus, .gnb > ul .depth2 > li:active, .gnb > ul .depth2>li:hover {
    background-color: #666;
}

.gnb > ul .depth2:after {
    background-color: #999;
}

.fixed_header .header_wrap.shrink {
    background-color: #333;
}

.header > .side > ul .ly.ly_login ul {
    background-color: #999;
}

.header > .side > ul .ly a:hover {
    color: #333;
}

.ic_edge {
    border-color: transparent transparent #999;
}

.xe_updated_ver .btn_item:hover, .xe_updated_ver .btn_item:active .xe_updated_ver .btn_item:focus {
    background-color: #333;
	border-color: #333;
}

.xe_updated_ver .btn_item {
	border-color: #B3B3B3;
	border-radius: 0px;
    color: #333;
}

.body > .side .banner {
    margin-top: 0px;
}

.footer {
    border-bottom: 5px solid #333;
}

.footer .f_cr_area {
    background-color: #444;
}

.header .logo-item img {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
}
