@-moz-document regexp('(https?:\/\/)?(www.)?(scp-wiki|scpsandbox2)(\.net|\.wikidot\.com)(?!\/(osanshouo|ADD_MORE_EXCEPTIONS_USING_THIS_FORMAT)).*'){
    
/* 
 * Fix for extended rating information userscript
 * on heritage rating modules
 */
  
    div.heritage-rating-module > div[style*="font-size: 8pt;"]{
        margin-top: 0px !important;
        margin-bottom: 0px !important;
    }
    
    
#page-title, body {
font-family: "Fira Sans", "DejaVu Sans Condensed", verdana, arial, helvetica, sans-serif !important;
  background: #191919 url("data:image/png;base64,") repeat !important;
}
    
select {
  background-color: #191919;
  color: #942828;
  border: 1px solid #808080;
  -moz-appearance: none;
  -webkit-appearance: none;
    background-image: url("data:image/png;base64,");
    background-repeat: no-repeat;
    background-position: 99% center;
    background-size: 13px;
    background-origin: padding-box;
}
    
input[type="button"], input[type="submit"] {
  border: 1px solid #808080;
  background-color: #191919;
  padding: 0px 8px 0px 8px;
  line-height: 19px;
  color: #942828;
}
    
input[type="text"] {
  background-color: #191919;
  color: #942828;
}
    
.pager .current{
  color: #191919;    
}

#page-title {
  font-family: "Junction", "Fira Sans", "DejaVu Sans Condensed", verdana, arial, helvetica, sans-serif !important;
  color: #942828;
}

a {
  color: #942828;
}

body {
  color: #808080;
}

div#container-wrap {
  background: url(http://scp-wiki.wdfiles.com/local--files/roget-s-workbench/header.png) top left repeat-x;
}

/* ------------------------------------------------------------- */

#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
  background-color: #191919;
  color: #808080;
}

/* ------------------------------------------------------------- */
/* ----------------------------TOP-BAR-------------------------- */
/* ------------------------------------------------------------- */

#top-bar a {
  color: #bbb;
  background: transparent;
}

#top-bar ul li.sfhover a, #top-bar ul li:hover a {
  background: #191919;
  color: #808080;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
  border-top: 1px solid #191919;
}

#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
  background: #191919;
}

#top-bar ul li ul a, #top-bar a:hover {
  color: #808080;
}

/* ------------------------------------------------------------- */
/* -----------------------RATING-WIDGET------------------------- */
/* ------------------------------------------------------------- */

.page-rate-widget-box {
  display: inline-block;
  margin-bottom: 10px;
  margin-right: 2em;
  color: #808080;
}

.page-rate-widget-box .rate-points {
  background-color: #191919 !important;
  border: solid 1px #808080;
  border-right: 0;
  border-radius: 5px 0 0 5px;
  color: #808080;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
  background-color: #808080;
  border-top: solid 1px #808080;
  border-bottom: solid 1px #808080;
  font-weight: bold;
  color: #191919;
}

.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
  background: transparent;
  color: #191919;
  padding: 0 4px;
  margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
  background: #191919;
  color: #808080;
}

.page-rate-widget-box .cancel {
  background-color: #191919;
  border: solid 1px #808080;
  border-left: 0;
  border-radius: 0 5px 5px 0;
  color: #808080;
}

.page-rate-widget-box .cancel a {
  background: transparent;
  color: #808080;
}

.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #191919;
  color: #808080;
}

/* ------------------------------------------------------------- */

#side-bar .side-block {
padding: 10px;
border: 1px solid #808080;
border-radius: 10px;
margin-bottom: 15px;
background-color: #191919 !important;
color: #808080;
}
#side-bar .heading {
border-bottom: solid 1px #808080;
background-color: #191919;
color: #808080;
}
#side-bar .collapsible-block-unfolded-link {
border-bottom: solid 1px #808080;
background-color: #191919;
color: #808080;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
margin-top: 10px;
margin-bottom: 5px;
color: #808080;
background-color: #191919;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
background-color: #191919;
color: #808080;
}

