css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* AGENT_SHEET */
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-top"],
scrollbarbutton[sbattr="scrollbar-up-bottom"],
scrollbarbutton[sbattr="scrollbar-down-bottom"] {
-moz-appearance: none !important;
display: none !important;
}
scrollbar[orient="vertical"] {
-moz-margin-start: -/*[[asshole]]*/px !important;
min-width: /*[[asshole]]*/px !important;
max-width: /*[[asshole]]*/px !important;
-moz-appearance: none !important;
position: relative !important;
background-color: transparent !important;
background-image: none !important;
border: 0 !important;
z-index: 2147483647 !important;
-moz-box-align: start !important;
padding:1px 0px 1px 6px!important;
}
scrollbar[orient="horizontal"] {
margin-top: -/*[[asshole]]*/px;
min-height: /*[[asshole]]*/px;
max-height: /*[[asshole]]*/px;
-moz-appearance: none!important;
position: relative !important;
background-color: transparent !important;
background-image: none !important;
border: 0 !important;
z-index: 2147483647 !important;
-moz-box-align: start !important;
padding:6px 11px 0px 1px!important;
}
scrollbar thumb {
-moz-appearance: none !important;
background-color: rgba(113, 124, 126, 0.6) !important;
border-radius: 8px !important;
border: 0 !important;
  transition: background-color 0.55s linear 0s, box-shadow 0.55s linear 0s !important;
box-shadow: 0px 0px 3px 0px /*[[asshole2]]*/ !important;
}
scrollbar[orient="vertical"] thumb {
min-height: 20px!important;
min-width: 15px!important;
max-width: 15px!important;
}
scrollbar[orient="horizontal"] thumb {
min-width: 20px!important;
min-height: 15px!important;
max-height: 15px!important;

}
scrollbar thumb:hover, scrollbar thumb:active {
background-color: rgba(43, 54, 56, 0.6) !important;

box-shadow: 0px 0px 3px 0px /*[[asshole2]]*/ !important;
}
html div scrollbar {
-moz-margin-start: 0!important;
}
