css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
- Background: #2D2D2D
- Primary: #1A1A1A
- Light border color: #575757
*/
/*to do - party stuff*/
/*only apply theme to new pages, because the old ones are a pain to style*/
@-moz-document
  url-prefix("http://www.roblox.com/home"),
  url-prefix("http://www.roblox.com/games"),
  regexp("http://www\\.roblox\\.com/users/[0-9]+/profile.*"),
  regexp("http://www\\.roblox\\.com/users/[0-9]+/Inventory.*"),
  regexp("http://www\\.roblox\\.com/users/[0-9]+/friends.*") {
  body, .content {
    background-color: #2D2D2D !important;
    color: #C5C5C5 !important;
  }
  .container-footer {
    background-color: #1A1A1A !important;
  }
  a, a:link, a:visited, a:active {
    color: #EDEDED !important;
  }
  h1,h2,h3 {
    color: #FFF !important;
  }
  .section {
    background-color: #1A1A1A !important;
    box-shadow: unset !important;
  }
  .game .game-item {
    background-color: #1A1A1A !important;
    box-shadow: unset !important;
  }
  .game .game-item .rbx-votes .vote-bar .voting-container .mask .segment {
    background-color: #1A1A1A !important;
  }
  .game .game-item .rbx-game-title {
    background-color: #1A1A1A !important;
  }
  .rbx-text-overflow {
    background-color: #1A1A1A !important;
  }
  .rbx-text-overflow::after, 
  .blog-news .news .news-link::after,
  .game .game-item .rbx-game-title::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #1A1A1A 90%) !important;
  }
  .blog-news .news .news-link {
    background-color: #1A1A1A !important;
  }
  .rbx-btn-control-xs {
    background-color: #2D2D2D !important;
    border-color: #2D2D2D !important;
  }
  .rbx-btn-control-xs:hover, .rbx-btn-control-xs:focus {
    background-color: #2D2D2D !important;
    border-color: #C5C5C5 !important;
    box-shadow: unset !important;
  }
  .vlist .list-item {
    border-color: #111 !important;
  }
/* Navigation Panel */
  .rbx-left-col {
    background-color: #1A1A1A !important;
  }
  span[class^="rbx-icon"] {
   /* filter: brightness(4); 
    -webkit-filter: brightness(4);*/
  }
  span[class^="rbx-icon"]:hover {
    filter: brightness(1);
    -webkit-filter: brightness(1);
  }
  .rbx-icon-default-navigation, .rbx-icon-nav-menu, .rbx-icon-nav-robux, .rbx-icon-nav-tix, .rbx-icon-  nav-settings, .rbx-icon-nav-search, .rbx-icon-nav-home, .rbx-icon-nav-profile, .rbx-icon-nav-message, .rbx-icon-nav-friends, .rbx-icon-nav-charactercustomizer, .rbx-icon-nav-inventory, .rbx-icon-nav-trade, .rbx-icon-nav-group, .rbx-icon-nav-forum, .rbx-icon-nav-blog, .rbx-icon-nav-search-white {
    background-image: url("http://imgh.us/navigationDark.svg") !important;
  }
  
/* GAMES PAGE */
  .games-list-container {
    background-color: #2D2D2D !important;
  }
  .dropdown-menu, .rbx-dropdown-menu {
    background-color: #141414 !important;
    color: #C5C5C5;
  }
  .rbx-input-group-btn .rbx-input-dropdown-btn {
    background-color: #141414;
    color: white !important;
    border-color: #575757;
  }
  .dropdown-menu > li > a:hover, .rbx-dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, .rbx-dropdown-menu > li > a:focus {
    color: #FFF !important;
    background-color: #1A1A1A !important;
  }
  
/* GAME PAGE */
  .rbx-para-overflow {
    background-color: #1A1A1A !important;
  }
  .rbx-para-overflow::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #1A1A1A 90%) !important;
  }
  .game-main-content .game-play-buttons {
    border-color: #575757 !important;
  }
  .rbx-tabs-horizontal .nav-tabs { 
    background-color: #141414 !important;
  }
  .rbx-tabs-horizontal .rbx-tab-heading {
    color: #C5C5C5 !important;
  }
  .nav-tabs > li.active > a,
  .nav-tabs > li.active > a:focus {
    background-color: #141414 !important;
    color: white !important;
  }
  .game-description {
    border-bottom: 1px solid #575757 !important;
    padding-bottom: 12px !important;
  }
  .game-stats-container {
    border: none !important;
    padding: 0 !important;
  }
  .game-stat-footer {
    padding: 0 !important;
  }
  .rbx-btn-control-sm {
    /*background-color: #2D2D2D !important;*/
  }
  .section-row {
    background-color: #1A1A1A !important;
    box-shadow: unset !important;
  }
  
/* Profile Page */
  .profile-about p {
    background-color: #1A1A1A !important;
  }
  .profile-about p::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #1A1A1A 90%) !important;
  }
  .profile-slide-item-right.groups,
  .profile-slide-item-right h2.groups,
  .slide-item-description.groups,
  .slide-item-my-rank.groups {
    background-color: #141414 !important;
  }
  h2.groups::after,
  .slide-item-description.groups::after,
  .slide-item-my-rank.groups::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #141414 90%) !important;
  }
  
  .profile-slide-item-right.games,
  .slide-item-description.games,
  .profile-slide-item-right h2.games,
  .slide-item-my-rank.games { 
    background-color: #141414 !important;
  }
  
  .profile-slide-item-right h2.games::after,
  .slide-item-description.games::after,
  .slide-item-my-rank.games::after{
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #141414 90%) !important;
  }
  .profile-container .asset-item img {
    border: none !important;
    border-color: #575757 !important;
  }
  .profile-container .asset-item span[title] {
    border: none !important;
  }
  
/* My Friends */
  .content .friends-container .friends-card {
    background-color: #1A1A1A !important;
    box-shadow: none !important;
  }
  .friends-card .friends-name  {
    background-color: #1A1A1A !important;
  }
  .friends-card .friends-name::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #1A1A1A 90%) !important;
  }
  .content .rbx-tab .rbx-tab-heading .rbx-lead {
    color: #C5C5C5 !important;
  }
  .content .friends-container .friends-card .friends-image {
    border: none !important;
  }
  .content .friends-container .friends-card .friends-image img {
    background-color: #2D2D2D !important;
  }
/* Inventory */
  .inventory-item .inventory-item-link,
  .recommended-item .recommended-item-link{
    background-color: #1A1A1A !important;
    box-shadow: 0 0 0 #2D2D2D;
  }
  .inventory-item .item-thumb,
  .recommended-item .recommended-thumb{
    border-color: #575757 !important;
  }
  .rbx-tabs-vertical .nav-tabs {
    background-color: #1A1A1A;
    box-shadow: unset !important;
  }
  .recommended-item .recommended-creator {
    color: white !important;
  }
  .recommended-item .recommended-creator-by {
    color:#757575; 
  }
  .recommended-item .recommended-name {
    color: #FFF;
  }
  .rbx-header {
    /*background-color: rgba(38, 190, 231, 0.67) !important;*/
  }
/* Chat */
    .chat-container, .chat-container a {
      color: black !important;
    }
  
/*==================================================
    OLD PAGES -- OLD PAGES SUCK
  ================================================== */
  

}
