css:
@-moz-document domain("slack.com") {
* { -webkit-text-stroke-width: 0.4px; }

.c-message_list pre {
    font-family: "Noto Sans Mono CJK JP", Monaco,Menlo,Consolas,"Courier New",monospace!important;
    line-height:110%;
    font-size: 14px;
}
#messages_container code {
    font-family: "BIZ UDGothic",Monaco,Menlo,Consolas,"Courier New",monospace!important;
}
@keyframes blink {
  10% { opacity: 1.0; }
  90% { opacity: 1.0; }
}

.p-channel_sidebar__channel--unread .p-channel_sidebar__name,
.p-channel_sidebar__link--unread .p-channel_sidebar__name {
  display: inline-block;
  color: #99CCFF;
  letter-spacing: .05em;
  text-shadow: 0 1px 30px #194BFF, 0 0 5px #fff, 2px 5px 60px #2684FF;
  animation: blink 3s infinite;
}
@keyframes blink {
  0%   { opacity: 0.2; }
  20%  { opacity: 1.0; }
  50%  { opacity: 1.0; }
  80%  { opacity: 1.0; }
  100% { opacity: 0.2; }
}

}
