css:
@-moz-document domain("pinboard.in") {

body {
	font-family: "Source Sans Pro", sans-serif;
  	font-size: 13px;
  	margin: 0;
  	padding: 1.7em;

  	/*border-top: 8px solid rgb(0, 0, 205);*/

    -moz-font-feature-settings: "kern", "liga";
    -ms-font-feature-settings: "kern","liga";
    -o-font-feature-settings: "kern","liga";
    -webkit-font-feature-settings: "kern","liga";
    font-feature-settings: "kern","liga";
}

#timer {
	font-family: inherit;
  	font-size: 0.84em;
}

a.url_display {
	transition: color 150ms linear;
    display: inline-block;
    margin-bottom: 0.8em;
}

a.bookmark_title:link,
a.url_display:hover,
#banner a,
#banner a.banner_username {
	color: rgb(0, 0, 205);
}

.url_link, .private a.url_link {
    display: inline-block;
    margin-top: 0.6em;
	padding: 1px 5px;
	border-radius: 3px;
}

.star {
	margin-left: -28px;
    font-family: Arial Unicode, Arial, sans-serif;
    line-height: 1.25;
}

.selected_star {
	color: #E4BC1D;
    /* MAKE IT SHINE~: text-shadow: 0 0 18px rgba(200, 200, 0, 0.6); */
}

.bookmark {
	margin: 0;
	padding: 1.2em 0.6em;
    border-color: #eee;
    border-style: solid;
	border-bottom-width: 1px;
}

#bookmarks div:first-child .bookmark {
	border-top-width: 1px;
}

a.tag {
	color: rgb(170, 85, 17);
    border-radius: 0.2em;
    transition: color 0.2s cubic-bezier(0.215, 0.610, 0.355, 1.000);
}

.bookmark {
	line-height: 1.4;
}

.bookmark a.tag {
    padding: 0.1em 0.3em;
	margin: 0 0.2em 0 -0.3em;
    line-height: 1.1 !important;
}

a.tag:hover {
    color: rgb(123, 61, 11);
    background-color: rgba(170, 85, 17, .05);
	text-decoration: underline;
}

.bookmark_title {
  	font-size: 1.2em;
	font-weight: 600;
}

.private {
	background: lightyellow;
	border-width: 0 4px 1px 1px;
	border-right-color: rgb(232, 215, 35);
  	border-bottom-color: #eee;
}

a.unread::after,
a.unread:visited::after {
	color: #b41;
  	padding-right: 18px;
	content: " \2666";
}

#bottom_next_prev,
#per_page_picker {
  margin-top: 1.3em
}

.description {
    margin: 0.2em 1em 1em 0;
	line-height: 1.32;
    color: #247;
}

	.description blockquote {
	    padding: 0.2em 0.8em;
	    margin: 0;
	    border-left: 3px solid #247;
	}

/* Flag emojis after lang:xx tags. Comment out if your browser doesn't support emojis */

a[href$="t:lang:de/"]:after {
	content: " \1F1E9\1F1EA";
}
a[href$="t:lang:en/"]:after {
	content: " \1F1EC\1F1E7";
	/* use " \1F1FA\1F1F8"; for an US flag instead */
}
a[href$="t:lang:zh/"]:after {
	content: " \1F1E8\1F1F3";
}
a[href$="t:lang:jp/"]:after {
	content: " \1F1EF\1F1F5";
}
a[href$="t:lang:kr/"]:after {
	content: " \1F1F0\1F1F7";
}
a[href$="t:lang:fr/"]:after {
	content: " \1F1EB\1F1F7";
}
a[href$="t:lang:es/"]:after {
	content: " \1F1EA\1F1F8";
}
a[href$="t:lang:it/"]:after {
	content: " \1F1EE\1F1F9";
}
a[href$="t:lang:ru/"]:after {
	content: " \1F1F7\1F1FA";
}

}
