css:
/* by alin1 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  domain("last.fm"), 
  domain("lastfm.es"),
  domain("lastfm.de"),
  domain("lastfm.fr"),
  domain("lastfm.jp"),
  domain("lastfm.it"),
  domain("lastfm.pl"),
  domain("lastfm.com.br"),
  domain("lastfm.se"),
  domain("lastfm.tr"),
  domain("lastfm.ru"),
  domain("cn.last.fm") {

html, 
body, 
.content-top, 
.page-content, 
.footer-wrapper,
.chartlist td, 
#adSkinLeft, 
#adSkinRight, 
textarea.comment-input, 
.img-thumbnail, 
.header .header-info-secondary:before, 
.widget_trends, 
.widget_listeningnow, 
.overlay-content, 
.gallery-image-container, 
.selectboxit-option-anchor, 
.content-top-lower, 
.date-range-picker-content, 
.col-sidebar, 
.adaptive-skin-right, 
.row, 
div.container.page-content, 
header, 
.shout--deleted, 
.large-header-layout .header.header--overview, 
.listening-report-promo {
  background:#101010 !important;
}

.two-column-layout .container {
  background:none !important;
}   

.fluid-container, 
div.overlay-side-rail, 
#recent-tracks-settings, 
#artist-chart-settings, 
#albums-chart-settings, 
#track-chart-settings, 
.user-dashboard-module,
#albums_range,
#tracks_range,
#shoutbox-sort,
.shout-more-actions-menu,
#top_tracks,
.navlist-hidden-list,
.chartlist-more-button,
.dropdown-menu-clickable,
.buylinks-dropdown-menu {
  background:#333333 !important;
}

body, 
.comment-text.clearfix, 
.chartlist-index, 
textarea.comment-input, 
p, 
.metadata-display, 
.nowslider_scrobbles, 
.gallery-image-vote-up, 
.gallery-image-vote-down, 
.overlay-control-bar-title, 
.gallery-image-vote-up-on, 
.gallery-image-vote-down-on, 
.gallery-image-vote-up-off, 
.gallery-image-vote-down-off {
  color:/*[[TextColor]]*/ !important;
}

a, 
.countbar-bar-value, 
li.pages, 
.next, 
.previous, 
button.btn-primary {
  color:/*[[LinksColor]]*/ !important;
}

a:hover, 
.selectboxit-text:hover {
  color:/*[[LinksHoverColor]]*/ !important;
}

.tag a {
  color:/*[[LinksHoverColor]]*/ !important;
}

.correction-vote-yes-off, 
.correction-vote-no-off, 
.suggest-correction-success-message, 
.gallery-image-vote-up-off, 
.gallery-image-vote-down-off {
  background:#333 !important;
}

div.username {
  color:/*[[LinksHoverColor]]*/ !important;
}

h2 a, 
h2 {
  color:/*[[HeadingsColor]]*/ !important;
}

.chartlist-timestamp, 
.header-info-secondary, 
.selectboxit-text {
  color:#999999 !important;
}

.btn_trendnav_day, 
.btn_trendnav_week, 
.btn_trendnav_month, 
.trendglobal {
  color:#000000 !important;
}

.context {
  background:#000 !important;
  box-shadow: 1px 0px #000 inset, -1px -1px #000 inset !important;
}

button {
  color:/*[[LinksHoverColor]]*/ !important;
}

img.avatar.tasteometer-avatar {
  border-radius:50% !important;
}

.header-background {
  background:none !important;
}

.content-top-lower:after, 
.content-top:after, 
div.overlay-content, 
.overlay-control-bar, 
.overlay-control-bar::after, 
.header-metadata-user-stats {  
  border:0 !important;
}

.widget_listeningnow, 
.widget_trends, 
.date-range-picker-content {
  border:1px solid #333333 !important;
}

.stationlink-list li, 
.chartlist td, 
.globalchart .globalchart-item, 
.events-list td, 
.events-filters, 
.inbox td {
  box-shadow:0px -1px #333333 inset !important;
}
  
.inbox-buttons {
  border-bottom:1px solid #333333 !important;
}

div.feedback-message, 
.copyright.col-xs-12, 
.col-xs-2, 
.features-footer, 
.subscribe-cta, 
.gpt-slot-1 {
  display:none !important;
}

.site-links {
  box-shadow:0px -1px #333333 inset !important;
}

.wiki-legal {
  border-top:1px solid #333333 !important;
}

.section-settings {
  background:#101010 !important;
  border:1px solid #333 !important;
}
  
select, input, 
textarea {
  color:#fff !important;
  background:#000 !important;
  border:1px solid #333 !important;
}

.search-field {
  border-radius:10px !important;
  background:none !important;
  border:2px solid !important;
  border-color:#666 !important;
}

.selectbox .selectboxit-options {
  background:#101010 !important;
  border:1px solid #333 !important;
}

.avatar, 
.auth-avatar-desktop, 
img.avatar-img, 
.header-metadata-avatar, 
.nowslider_img {
  border-radius:/*[[BorderRadius]]*/% !important;
}

.inbox-message--unviewed {
  background:#333 !important;
}
  
.masthead-nav-links a {
  color:#999999 !important;
}

th.metadata-title, 
.header-metadata-global-stats {
  color:#cccccc !important;
}

.chartlist-image, 
.image-overlay-playlink-link {
  display:none !important;
}
  
.chartlist-index, 
.chartlist.chartlist--has-image td, 
.chartlist-ellipsis-wrap, 
.chartlist-countbar, 
.chartlist-name, 
.chartlist-ellipsis-wrap, 
.chartlist-timestamp,
.chartlist-delete {
  padding-top:1px !important;
  padding-bottom:1px ! important;
}

.chartlist-play {
  height:100% !important;
}
  
.chartlist-name {
  height:100% !important;
}

.countbar-bar-slug {
  background:/*[[CountbarsColor]]*/ !important;
}

.hidden-xs.header-scrobble-since {
  color:#ffffff !important;
}

.nowslider_imgwrap {
  border-radius: 0 !important;
}

/*[[ListenSection]]*/

/*[[TopAlbums]]*/

/*[[HistoryGraph]]*/

}

