css:
@-moz-document url-prefix("http://www.ziarulargesul.ro/") {
body, body.layout-boxed {background-color: #292929 !important;}
.topbar, .copyrights-area, .topbar .top-nav > li ul.sub-menu li a:hover {background-color: #393939 !important;}
.header {background: #393939 !important;}

#navigation {background: #696969 !important;border-bottom: 1px solid #292929 !important;border-top: 1px solid #292929 !important; }
.clearfix {background: #292929 !important;}
.base-box, .sidebar .widget, .comment-form, div.bbp-template-notice, div.indicator-hint, #bbpress-forums fieldset.bbp-form legend {background: #393939 !important;}
.layout-boxed .base-box, .layout-boxed .sidebar .widget, .layout-boxed .comment-form, .layout-boxed div.bbp-template-notice, .layout-boxed div.indicator-hint, .layout-boxed #bbpress-forums fieldset.bbp-form legend, .layout-boxed .news-ticker {border-color: #656565 !important; }
.news-ticker {background:dimgrey !important; }
.news-ticker li a{color:#e4e4e4 !important;}
.news-ticker li i{color:#000000 !important;}
.widget ul li a {color: #F1F1F1 !important;}
.widget ul {color:#C7C7C7 !important;}
.widget .widget-head {    background: #292929 !important;border-bottom: 1px solid #292929 !important; }
.sidebar .widget .widget-title span { background: #292929 !important;color: #B1B1B1 !important;}
a:link, a:visited, a:active, a:focus, a:before, a:after {color: #F3F3F3 !important;}
.main-menu > li:hover > a, #navigation .main-menu > li.current-menu-item > a, .navigation_highlight_ancestor #navigation .main-menu > li.current-menu-ancestor > a, .navigation_highlight_ancestor #navigation .main-menu > li.current-menu-parent > a {background-color: #2F2F2F !important;}
#navigation .main-menu > li {border: 1px solid #8C8C8C !important; }
#navigation {border-bottom: 1px solid #8C8C8C !important; }
.fs-image-nav .fs-prev, .fs-image-nav .fs-next {background: #929292 !important;    border-color: #656565 !important; color:#000 !important;}

.fs-image-nav {border: 1px solid #656565 !important; }
ul.main-menu > li:not(.mom_mega) ul.sub-menu li a {background:dimgrey !important;}
.news-box .nb-header {background: #292929 !important;border-bottom: 1px solid #292929 !important; }
.news-box .nb-header .nb-title a, .news-box .nb-header .nb-title span {background: #292929 !important;    color: #B1B1B1 !important;}
.scrolling-box .owl-item { border:1px solid #656565 !important;}
.scrolling-box:before {background: #393939 !important;border-bottom: 1px solid #292929 !important; border-top: 1px solid #292929 !important; }
.scrolling-box:after {background: #656565 !important;}
.news-box .nb-item-meta span:last-child {    color: #BDBDBD !important;}
.news-box .news-summary p {    color: #F7F7F7 !important;}
.news-box .older-articles ul.two-cols {background: #393939 !important;}
.news-box .older-articles ul.two-cols li:nth-child(2n+1) {border:1px solid #656565 !important;}
.news-box .older-articles ul.two-cols li {border:1px solid #656565 !important;}
.news-box .nb-footer {border-top:1px solid #656565 !important;}
.nb-style2 .older-articles .two-cols:before {background: #656565 !important;}
.mom-post-meta > span, .mom-post-meta > a { color: #BDBDBD !important;}
p, ul, ol, dl, table, fieldset, blockquote, pre, code, address {color: #BDBDBD !important;}
.nb-style1 .nb1-older-articles {background: #393939 !important;}
.nb-style1 .recent-news {border-bottom:1px solid #656565 !important;}
.topbar input, .topbar select, .topbar textarea {background-color:#1b1b1b !important;}
.topbar input, .topbar select, .topbar textarea {color: #D8D8D8 !important;}
.topbar .search-form button {color:#000 !important;}
.topbar .search-form button {    background-color: #565656 !important;}
input, select, textarea, .mom-select {background-color:#1b1b1b !important;border:1px solid #656565 !important;color: #D8D8D8 !important;}
.current-day-weather .cdw-data {background: #292929 !important;border-right:1px solid #656565 !important;}
.current-day-weather .cd-temp {background: #292929 !important;}
.current-day-weather {border:1px solid #656565 !important;}
.current-day-weather .cd-temp span {color: #CCCCCC !important;}
.current-day-weather .cdw-data .cs-time {color: #989898!important;}
.weather-widget .next-days .day-summary .d-date {background: #393939 !important;border:1px solid #656565 !important;}
.weather-widget .next-days .day-summary .d-decs {background: #292929 !important;}
.weather-widget .next-days .day-summary {border:1px solid #656565 !important;}
.weather-widget .next-days .day-details li {border-bottom:1px solid #656565 !important;background: #292929 !important;}
.weather-widget .next-days .day-summary {border:1px solid #656565 !important;background: #292929 !important;}
.weather-widget .next-days .day-summary .d-handle {    background: #9C9C9C!important;    color: #000!important;}
.current-day-weather .cdw-data .city_name {    color: #F1F1F1 !important;}
.current-day-weather .cdw-data .cdw-meta {border-bottom:1px solid #656565 !important;}
th{ background: #080808 !important;color: #BFBFBF !important;}
td, th {border:1px solid #656565 !important;}
tr:nth-of-type(odd) {background: #292929!important;}
#footer {background: #292929 !important;border-top:none !important;}
ul.main-menu > li:not(.mom_mega) ul.sub-menu li {    border-bottom: 1px solid #8C8C8C !important;}
ul.main-menu > li:not(.mom_mega):not(.mom_mega_cats) ul.sub-menu {border: 1px solid #8C8C8C !important;}
p, ul, ol, dl, table, fieldset, blockquote, pre, code, address {color: #F7F7F7 !important;}
h1, h2, h3, h4, h5, h6 {    color: #D6D6D6 !important;}
.wp_rp_wrap .related_post_title {background: #292929!important;}
.navigation-inner  {background: dimgray !important; border-bottom: 1px solid dimgray!important; }
}
