css:
@-moz-document domain('academico.unemat.br') {
  :root {
    --topsagu: rgba(0,0,0,.3);
    --bottomsagu: rgba(0,0,0,1);
    --bgnull: rgba(0,0,0,0);
  }

  body {
    min-width: 370px;
    color: var(--bottomsagu);
  }

  .m-main-menu-navbar-item-clickable:hover,
  .m-main-menu-navbar-item-focused {
    background: var(--bgnull);
    color: white;
    border: none;
  }

  .m-main-menu-quickaccess,
  .m-scrollable-field div {
    display: none;
  }

  .m-scrollable-field table div {
    display: block;
    border-collapse: collapse;
  }

  .m-event-calendar {
    border-radius: 5px 5px 0px 0px;
    padding: 4px 0 4px 0;
    width: 100%;
  }

  .m-form-body {
    padding: 5px;
    border-color: var(--topsagu);
  }

  .m-form-row {
    vertical-align: middle;
  }

  div.m-box-box {
    background-color: var(--bgnull);
    margin: 0 2em 0 2em;
  }

  div.m-box-title,
  div.m-prompt-box-title,
  .m-tableraw-title,
  .mThemeContainerTop {
    border-radius: 5px 5px 0px 0px;
    padding: 5px 0 5px 0;
    background: -webkit-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -o-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -moz-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: linear-gradient(to bottom, var(--topsagu), var(--bottomsagu));
  }

  .m-prompt-box-box {
    width: 100%;
  }

  .m-prompt-box-error {
    border: 1px solid var(--topsagu);
  }

  div.m-panel-body {
    border-radius: 0px 0px 5px 5px;
    border-style: 1px dashed var(--topsagu);
  }

  input.m-text-field,
  select.m-combo {
    padding: 5px;
    border-radius: 5px;
  }

  .m-text-user-field,
  .m-text-passwd-field,
  .label {
    padding: 5px 5px 5px 20px;
    border-radius: 5px;
    vertical-align: middle;
    background-position: left center;
  }

  .m-link:hover {
    background: none;
  }

  button.m-button {
    width: 15em;
    padding: .2em;
    border-color: var(--topsagu);
    color: var(--bottomsagu);
  }

  .m-button:hover {
    border-color: var(--topsagu);
    color: var(--bottomsagu);
  }

  .m-form-row .label .m-caption,
  .m-form-row div .m-caption,
  .m-form-row span.label {
    width: 40px;
  }

  fieldset,
  fieldset fieldset {
    border-radius: 5px;
    box-shadow: 0 2px 5px var(--topsagu);
    border: 1px solid var(--topsagu);
    margin-top: 2em;
  }

  .m-tableraw-column-title,
  .m-grid-column-heading {
    background: -webkit-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -o-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -moz-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: linear-gradient(to bottom, var(--topsagu), var(--bottomsagu));
    border: none;
  }

  .m-main-menu-navbar,
  .menu-navbar-item {
    background: -webkit-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -o-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -moz-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: linear-gradient(to bottom, var(--topsagu), var(--bottomsagu));
    color: white;
    border: none;
    vertical-align: middle;
  }

  .m-main-menu-navbar-item-clickable,
  .m-main-menu-navbar-separator,
  .m-main-menu-navbar-item,
  .m-bookmark-statusbar,
  .m-mostaccessed-statusbar,
  .m-bookmark-statusbar:hover,
  .m-mostaccessed-statusbar:hover {
    color: white;
    border: none;
    background-color: var(--bgnull);
    vertical-align: middle;
  }

  .m-statusbar {
    background: -webkit-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -o-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: -moz-linear-gradient(bottom, var(--topsagu), var(--bottomsagu));
    background: linear-gradient(to bottom, var(--topsagu), var(--bottomsagu));
    height: 30px;
    line-height: 30px;
  }

  legend {
    border-bottom: 1px dashed var(--bottomsagu);
    color: var(--bottomsagu);
  }

  input[type="text"],
  input[type="password"] {
    border: 2px solid var(--bottomsagu);
  }

  input[type="text"]:hover,
  input[type="text"]:focus,
  .m-text-field:hover,
  .m-multiline-field:hover,
  .m-text-user-field:hover,
  .m-text-passwd-field:hover,
  textarea:hover,
  .m-combo:hover,
  .m-text-field:focus,
  .m-multiline-field:focus,
  .m-text-user-field:focus,
  .m-text-passwd-field:focus,
  textarea:focus,
  .m-combo:focus {
    background-color: var(--toptomsagu);
  }

  .m-text-field,
  .m-combo {
    border: 2px solid var(--bottomsagu);
  }

  .m-text,
  #txtPass,
  .m-label-text,
  .m-caption,
  .m-caption-required,
  .m-label,
  .m-link {
    color: var(--bottomsagu) !important;
  }

  .m-hr {
    border-color: var(--topsagu);
    border-bottom: none;
    border-top-width: 2px;
  }

  .m-scrollable-field {
    margin: 0px 10px 10px 10px;
    border-radius: 5px;
    padding: 0px 0px 0px 0px;
    box-shadow: 0 2px 5px var(--topsagu);
    border-color: var(--topsagu);
  }

  #mPopup {
    border: none;
    border-radius: 5px;
  }

  .m-popup-inner {
    padding: 2px 2px 15px 2px;
  }

  #m_hctPresence,
  #m_hctAbsence {
    margin: 0px 20px 10px 20px;
    width: 200px;
  }

  #m_m3181,
  #m_m3187 {
    width: 70px;
  }

  .m-image-link-label:hover,
  .m-image-link-label:active {
    text-decoration: none;
    color: var(--bottomsagu);
  }

  .m-pagenavigator-link:active,
  .m-pagenavigator-link:hover,
  .m-image-label:hover,
  .m-image-label:active {
    text-decoration: none;
  }

  .m-image-link-label:link,
  .m-image-link-label:visited,
  .m-image-link-label:hover,
  .m-image-link-label:active {
    text-decoration: none;
  }

  #m-loading-message-image {
    height: auto !important;
    width: auto !important;
    padding: 20px;
    margin: 0;
    border-radius: 50px;
    top: 48% !important;
    left: 48% !important;
  }

  #btnSearch {
    margin: 2px !important;
  }

  .m-statusbar {
    box-shadow: 0px 0px 5px var(--bottomsagu);
  }
}
