css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("channelawesome.com") {

.header-content,#menu-item-4577,#menu-item-2464,.sidebar-narrow,#youtube-widget-2,.textwidget,.widget_text,#calendar-2,.widget-counter,footer,.share-post,.post-tag,.post-meta-author,.post-cats,.flexslider,.sidebar,#menu-item-4578,#menu-item-75219,#menu-item-4565,#menu-item-4566,#theme-header,#comments,.more-link,#related_posts,#author-box,.post-meta,.content:before,.footer-bottom{
  display: none!important;
}
 .top-nav{
   border:none!important;
 }
  
  .background-cover {
    background-color: #2C2C2C !important;
}
  
  .more-link{
    width:208px;
  }
  
  .content .entry iframe, .entry .SWMPlayer {
    margin-top:130px;
    left:0px;
    right:0px;
    top:-30px;
    bottom:20px;
    margin: auto;
    width: 90%;
    height:85%;
    z-index: 99996;
    position: fixed;
}
  
  .content{width:auto!important;}
.content:before{
  font-size: 200%;
  padding-left:38%; 
  font-weight:bold;
  content: "Simply awesome";}
  
  .post-listing {text-align: center!important;}
  
 .ubermenu-skin-fire{
   background: #444;
 }
  .ubermenu-skin-fire .ubermenu-submenu .ubermenu-item-header > .ubermenu-target{
    color:#ddd!important;
  }
 .ubermenu-target{
   background: #222!important;
 }
   .ubermenu-target:hover{
   background: #444!important;
 }
  .post-listing {
   display: inline-block!important;
      padding: 0;
   margin: 0;
  }
 .item-list{
   width:25%;
   height: 25%;
   margin: 10px;
   display: inline-block!important;
   
 }
  
  #main-nav-uber{
    visibility: hidden;
  }


 .ubermenuitem{
    transition:all 5s;
}

 .ubermenu-submenu{   
height: 0;
} 
  
.ubermenu-submenu{   
    transition-delay:1s;
  height: 100%;
}
  
 .item-list img{
   width:100%;
 }
 .item-list h2{
   font-size: 100%!important;
 }
 .ubermenu-content-block,.ubermenu-submenu{
   background: transparent!important;
   border:none!important;
 }
  #menu-item-71395{
    float: right;
  }
  
 .ubermenu-submenu ul{
   background: #222!important;
 }
  
  .entry p{
    font-size: 0!important;
  }
}
