@-moz-document domain("spotify.com") {

/* Make top panel higher for player controls to fit */
header#header {
    height: 85px;
}

/* Player controls set to top & center */
#view-player .player-controls-container {
    position: fixed;
    top: 33px;
    left: 220px;
    width: 60%;
}

/* Profile Icons smaller width */
div#view-profile-menu {
    width: 30%;
}

/* Now Playing to top on the left */
#view-now-playing .caption {
    position: fixed;
    top: 33px;
}

/* Move Search textbox to the left, above player controls */
html.has-native-navbar #view-search {
    margin-left: 250px;
}

/* Upgrade button */    
div#upgrade-product {
    display: none;
}

/* Hide unneeded footer controls container */
footer#view-player {
	height: 0;
}

/* Nyan Cat Progress bar */

/* Handle */
.progress-bar .handle, .progress-bar-thick .handle {
   background: url("data:image/gif;base64,") !important;
	
    display: block;
    width: 34px !important;
    height: 21px;
    opacity: 0;
    position: absolute;
    right: -15px;
    top: -6px;
    border: 0;
}

/* Progress bar rainbow background */
.progress-bar .inner, .progress-bar-thick .inner { 
    background: url("data:image/png;base64,") repeat-x !important; 
    bottom: 0;
    background-size: 1px 4px !important;
    left: 0;
    position: absolute;
    right: auto;
}

/* Progress bar rainbow background bigger on hover */
#progressbar:hover .inner, .progress-bar-thick .inner {
	background-size: 1px 6px !important;
}
#volumebar:hover .inner, .progress-bar-thick .inner {
	background-size: 1px 6px !important;
}

/* Search Suggest Dropdown */
iframe#app-suggest {
position: fixed;
left: 265px;
}


/* Main sidebar menu to be fixed */

.main-menu.narrow-menu {
    position: fixed;
}

/* Album popup menu front z-index set */

#main>div.stacked>div.front {
    z-index: 100;
}

/* Hide big Save buttons on Controls */

#now-playing-widgets {
    display: none;
}

/* Set right sidebar to top toolbar */

#now-playing {
        position: fixed;
    left: 90px;
    z-index: 99;
    width: 100%;
    height:9.5em;
}

#playback-controls {
    display: none;
}

div.sp-image-img {
    width: 50%;
    height: 50%;
}

div.sp-image-placeholder {
    display: none;
}

div#progress {
    width: 20%;
    position: fixed;
    top: 20%;
    left: 15%;
}

#controls .extra {
    top: 10px !important;
}

div#controls {
        width: 20%;
    position: fixed;
    top: 5px;
    left: 15%;
}

div#player {
        position: fixed;
    top: 0;
    left: 0;
}

/* Move main container down by 150px to not overlap w/ new controls toolbar */

div.root {
    margin-top: 150px;
}

/* Nyan Cat Progress bar */

/* Handle */
#bar-click #position, #volume-bar #vol-position {
   background: url("data:image/gif;base64,") !important;
	
    display: block;
    width: 34px !important;
    height: 21px;
    position: absolute;
    right: -15px;
    top: -6px;
    border: 0;
    box-shadow: none;
}

/* Progress bar rainbow background */
#bar-click #bar-inner, #volume-click #vol-bar-inner { 
    background: url("data:image/png;base64,") repeat-x !important; 
    bottom: 0;
    background-size: 1px 4px !important;
    left: 0;
    position: absolute;
    right: auto;
}

/* Progress bar rainbow background bigger on hover */

#volume-click #vol-bar-inner {
    background-size: 1px 6px !important;
}

#volumebar:hover .inner, .progress-bar-thick .inner {
	background-size: 1px 6px !important;
}


#view-search {
    margin-left: 350px !important;
}

}
