css:
@namespace url(http://www.w3.org/1999/xhtml);


/* Blog */
@-moz-document domain("codinghorror.com") {

  html {
    background: #333;
  }
  
  body {
    color: #b4d455;
  }
  
  img {
    opacity: 0.3;
  }
  
  img:hover {
    opacity: 1;
  }
  
  .blog-logo {
    margin: -15px 24px 0 0;
  }
  
  pre {
    background: #111;
    border-color: #555;
  }
  
  .gsc-search-box-tools .gsc-search-box .gsc-input {
    padding-right: 0px;
  }
  
  .cse .gsc-search-button input.gsc-search-button-v2, input.gsc-search-button-v2 {
    width: 19px;
    height: 19px;
    padding: 4px 12px 4px 12px;
    margin-top: 4px;
    opacity: 0.55;
  }
  
  .site-search input.gsc-search-button, .site-search input.gsc-search-button:hover, .site-search input.gsc-search-button:focus {
    background-color: #111;
  }
  
  .gsc-control-cse {
    background-color: #333;
  }
  
  .gsc-input-box {
    background: #555;
    border-color: #777; 
  }
  .gsc-iw-id1 {
   
  }
  #gsc-i-id1 {
    background: none !important;
  }
  
  .blog-title a,
  h1,
  h1 a {
    color: #777;
  }
  
  h2, h2 a { color: #666; }
  
  a {
    color: hotpink;
  }
  
  a:hover {
    color: #FC9FDA;
  }
  
  code,tt {
    background: #555;
    border-color: #aaa;
  }

  .sidebar h3 {
    color: #777;
  }
  
  div#hireme.codinghorror, div#hireme.gray, div.clc-jobs.codinghorror, div.clc-jobs.gray {
    background-color: #555;
    border-color: #777;
    padding: 2px;
  }
  
  div#hireme > ul > li > a > div {
    color: #aaa;
  }
  
  div#hireme.codinghorror a {
    color: hotpink;
  }
  
  div#hireme > ul > li > a:hover, div.clc-jobs > ul > li > a:hover {
    color: #FFB9ED;
  }
  
  div#hireme > ul, div.clc-jobs > ul {
    background: none;
  }
  
  div#hireme > ul > li > a span.post-tag, div.clc-jobs > ul > li > a span.post-tag {
    background: none;
    border-color: #4988B9;
    color: #92BA5A;
   
  }

  .welovecodinghorror {
    background: #343;
    color: #b4d455;
  }
  
}


/* Embedded comments */
@-moz-document url-prefix("http://discourse.codinghorror.com/embed/") {
  html { 

  }  
  .replies {
    color: #333;
    font-size: 12px;
  }
  header.discourse {
    border: none;
    padding: 0 0 0 8px;
    background: #777;
  }
  header.discourse a {
   color: #FF69B4;
   font-size: 13px;
   line-height: 0;
   font-weight: bold;
   text-shadow: 0px 1px 1px #454545;
  }
  
  article.post a {
    color: hotpink;
  }
  
  article.post .username a {
    color: #F87202;
  }
  
  article.post .username a.new-user {
    color: #FFC46C;
  }
  
  article.post {
    background: #555;
    border-color: #333;
  }
 
  
  article.post .quote .title,
  article.post blockquote {
    border-color: #383838;
    background-color: #444;
  }
  
}

/* Discourse Forum */
@-moz-document domain("discourse.codinghorror.com") {
  html {
    background: #333;
    color: #aaa;
  }
  
  .d-header {
    
  }
}
