css:
@-moz-document domain("lurkmore.to"), domain("lukomore.org"), domain("lurkmo.re"), domain("lurkmirror.ml"), domain("lurkmore.co"), domain("lurklurk.com"), domain("lurkmore.net") {
#p-logo a{
       background:url(http://img-fotki.yandex.ru/get/66521/345655791.0/0_14687b_edfbfeb6_orig) left center no-repeat #B9F9FD !important;
    }

#content {
    background: #F6FFDD;
    border: 1px solid #61BCCA;
}

div#content {
    border: 1px solid #43C3BC;
    box-shadow: 0 0 0 1px #67BCD6;
    background-color: #F6FFDD;
}

body {
    background: #F4F9C3 url(http://img-fotki.yandex.ru/get/66745/345655791.0/0_14687a_3bc5f045_orig) 0 0 repeat;
}

#titlebox {
    vertical-align: middle;
    text-align: center;
    background: url(http://img-fotki.yandex.ru/get/66745/345655791.0/0_14687a_3bc5f045_orig) left center no-repeat #B9F9FD;
}


#p-cactions li a {
    background-color: #D6FEFF;
    color: #4E5001;
}

#footer {
    background: url(https://img-fotki.yandex.ru/get/66745/345655791.0/0_14687a_3bc5f045_orig) repeat-x left bottom #AFFBA5;
    border-top: 1px solid #14BB85;
}

.pBody {
    background-color: #E0FFB4;
}


#p-categories h5, #p-navigation h5, #p-tb h5, #p-lang h5, #p-advert h5, #p-counters h5 {
    background-color: #E9FFFF;
    border-color: #48B5CA;
}

#toc, .toc, .mw-warning {
    border: 1px solid #4EB59C;
    background-color: #FFFCC1;
}

#target-list {
    vertical-align: middle;
    background: url(https://img-fotki.yandex.ru/get/66745/345655791.0/0_14687a_3bc5f045_orig) right center no-repeat #fff;
}

#choise li a {;
    border: 1px solid #63B9C5;
}

#p-logo a, #p-logo a:hover {
    color: #EDF163;
}

body.body-jews #p-logo a, body.body-jews #p-logo a:hover {
    background-image: url(https://img-fotki.yandex.ru/get/64354/345655791.0/0_14687c_8ac4f8b8_orig) !important;
}




body {
    background-color: #F4F9C3;
}

.mw-body {
    border: 1px solid #2E86C3;
    background-color: #FCFFEC;
}

#toc, .toc, .mw-warning, .toccolours {
    border: 1px solid #408F92;
    background-color: #F4F9C3;
}

#mw-head, .vectorTabs:hover { background-color: #FCFFEC !important; 
}

div.vectorTabs ul li {
    background-image: url(https://img-fotki.yandex.ru/get/66521/345655791.1/0_14687d_e44d5eb_orig);
}

div.vectorTabs {
    background-image: url(https://img-fotki.yandex.ru/get/66958/345655791.1/0_14687e_6f9dca75_orig);
}
}
