css:
@-moz-document url-prefix("https://exs.lv") {
  body{
    background: url("https://i.imgur.com/xERaC9L.png") repeat scroll 0% 0%;
  }
  
  body p,div,span,h1, h2, h3, h4 {
    color: #C2C2C2 !important;
  }
  
  body a {
    color: #F0F0F0 !important;
  }
  
  blockquote{
    background-color: #333C36;
  }

  input[type="text"]{
    color: #333 !important;
  }
  
  /* headeris un navigācija */
  #header{
    background-color: #4F4E4E;
  }
  
  #intr{
    background-color:#4F4E4E;
  }
  
  header#header nav.navbar div.nav-collapse ul.nav li a{
    border-right: none !important;
  }
  
  header#header nav.navbar div.nav-collapse ul.nav li a:hover{
    background: #323232 !important;
  }
  
  #intr > div > .span9 > #user-menu > li > a {
    background-color: transparent;
    border-right: none;
  }
  
  .nav li.dropdown > ul {
    border-radius: 0 !important;
    background-color: #828282;
    border-bottom: #323232 3px solid; 
  }
  
  #intr > div > .span9 > #user-menu > li > a:hover {
    background: #323232 !important;
  }
  
  #user-menu > li.dropdown > ul {
    border-radius: 0 !important;
    background-color: #828282;
    border-bottom: #323232 3px solid; 
  }
  
  #user-menu > li.dropdown > ul > li > a{
    color: #F0F0F0 !important;
    text-shadow: none;
  }
  
  li.dropdown > ul > li > a:hover {
    background-color: #B8B8B9 !important;
  }
  
  #searchform p input#searchsubmit {
    background-color: #323232 !important;
    background-position: center !important;
    background: url("https://i.imgur.com/n3HXMLN.png") no-repeat;
  }
  
  /* boxu headeri */
  
  h2.title, h3.title {
    background-color: #2C2E30;
    border-bottom: 1px solid #8E888A !important;
  }
  
  .widget > h3.title {
    border-bottom: none !important;
  }
  
  h2.title a span{
    color: #EDEDED !important;
  }
  
  h2.title a span{
    background-color: transparent !important;
  }
  
  h3.title span{
    background-color: transparent !important;
  }
  
  /* contnents */
  #content {
   background-color: #424243;
  }
  
  /* tabi */
  ul.tabs{
    padding: 0 !important;
    background-color: #2C2E30;
    height: auto;
  }
  
  ul.tabs > li {
    border: none;
  }
  
  ul.tabs > li > a{
    margin: 0;
    border: none;
    border-radius: 0;
    padding: 4px 4px 3px;
    text-shadow: none;
    background: #464A4F;
  }
  
  ul.tabs > li > a.active {
    background: #666468;
    padding:4px 4px 3px !important;
  }
  
  ul.tabs > li > a.active:hover {
    background-color: #414141;
    color: #EDEDED;
  }
  
  
  /* pašreizejais tabs */
  .tabMain{
    padding-top: 0 !important;
    background-color: #5B5A5A;
  }
   
  /* sākuma lapa */
  .tabMain > .left{
    padding: 5px;
  }
  
  .tabMain > .right{
   padding: 5px;  
  }
  
  .tabMain * {
    border-color: #8E888A !important;
  }
  
  /* sākuma lapa (raksti) */  
  article.post > h2 {
    border-bottom: #8E888A solid 1px;
  }
  
  article.post > h2 > a{
    color: #EDEDED;
  }
 
   article.post > div.entry-meta{
     border-bottom: #8E888A solid 1px;
     margin-bottom: 5px;
   }
  
   article.post > div.entry-meta > ul > *{
    color: #EDEDED;
  }
  
  article.post > div.entry-meta > ul > li {
    border-right: 1px #8E888A solid;
  }
  
  /* sākuma lapa (visa huiņa pa labi)*/
  div#home-middle.right.span5 > h2.title {
    margin-bottom: 0;
  }
  
  #home-middle.right > div{
    background-color: #484848;
  }
  
   #home-middle.right > ul{
    background-color: #484848;
   }
  
  #home-middle.right ul > li:nth-child(1){
   margin-top: 0;
  }
  
  /* siena */
  
  /* sidebars ar widgetiem */
  
  #sidebar > .widget {
    background-color: #646364;;
  }
  
  #sidebar > .widget *{
    border-color: #8E888A;
  }
  
  #sidebar > .widget > .title {
    margin-bottom: 0 !important;
  }
  
  #sidebar > .widget > .box >.tabnav {
    margin-bottom: 0;
  }
  
  .mbox{
    background-color: #646364;
  }
 
  ul#mb-list li.group {
    border-bottom: 3px solid gold;
  }
  
  /* sidebara elementi kas mainās uz hovera */
  
  #mb-list > li > a:hover {
    background-color: #90908F;
  }
  
  div.widget div.mbox ul.blockhref.mb-col li a:hover{
    background-color: #90908F;
  }
  
  ul#user-notify{
    list-style: none;
  }
  
  ul#user-notify li a:hover {
    background-color: #90908F;
  }
  
  #mb-list.mb-col li a:hover {
    background-color: #90908F;
  }
  
  ul#latest-topics li a:hover {
    background-color: #90908F;
  }
  
  /* tabu navigācija, hz vai viņa ir tikai sidebarā */ 
  
  ul.tabnav > li > a {
    background-color: #656464;
  }
  
  ul.tabnav > li > a.active {
    background-color: #90908F;
  }
  
  ul.tabnav > li > a:hover {
    background-color: #90908F;
  }
  
  /* peidžeris */
  p.core-pager a.selected{
    border-bottom: 2px solid;
  }
  
  p.core-pager a{
    font-size: 14px;
  }
  
  /* minibloga lapa */
  
  div.tabMain ul#miniblog-list li {
    padding: 0;
  }
  
  div.tabMain ul#miniblog-list li div.mbox {
    background-color: #515051 !important;
  }
  
  .post-button {
    background: #515051;
  }
  
  /* plusiņi, mīnusi un citas ikonas */
  #user-notify li a .notification-icon{
    background-position: 0px 0px !important;
    background: url('https://i.imgur.com/5ObjCXN.png');
    background-size: 20px 20px;
    width: 20px;
    height: 20px;
  }
  a.plus{
    background-image: url('http://i.imgur.com/oMUGyLG.png');
    background-size: 16px 16px;
    opacity: 1;
  }
  a.plus:hover{
    background: transparent url('http://i.imgur.com/oMUGyLG.png');
    background-size: 16px 16px;
    
  }
  a.minus{
    background: transparent url('http://i.imgur.com/chvrWYH.png');
    background-size: 16px 16px;
  }
  a.minus:hover{
    background: transparent url('http://i.imgur.com/chvrWYH.png');
    background-size: 16px 16px;
  }
  .voted1{
    background: url('http://i.imgur.com/u7x6xfD.png');
    background-size: 16px 16px;
    opacity: 1;
  }
  .voted2{
    background: transparent url('http://i.imgur.com/RWGAt2Y.png');
  }
  
  .r-val.negative {
    color: #FD656D !important;
  }
  
  .r-val.positive {
    color: #96FD00 !important;
  }
  
  .mb-reply-to, .mb-reply-main{
    text-indent: -1000em;
    background: no-repeat url('https://i.imgur.com/G0rPeZL.png');
    background-size: 16px 16px;
  }
  .mb-rater{
    opacity: 1 !important;
  }
  .mb-reply-to{
   opacity: 1 !important;
  }
  .mb-reply-main{
    opacity: 1 !important;
  }
  
  /* spolera linku un teksta krāsas */
  
  .spoiler .spoiler-content a{
    color: #4677DE !important;
  }
  
  .spoiler .spoiler-content{
    color: #333 !important;
  }
  
  /* niku krāsas */
  
  .rautors {
    color: #8BE100 !important;
  }
  
  .admins{
    color: #FE2E2E !important;
  }
  
  .mods{
    color: #7B7BF1 !important;
  }
  
  .mods > a{
    color: #7B7BF1 !important;  
  }
  
  /* online zvaigzne */
  .r{
    color: #FF837C !important;
  }
  
  /* es īsti nezinu, kas tas par elementu */
  
  legend{
    color: #EDEDED;
  }
  /* profila info */
  
  dl#profile-info dt{
    color: #EDEDED;
  }
  
  /* vestules */
  
  table#pm-table tbody tr.read-1 td{
    background-color: #828282;
  }
  
  p#pm-read-footer a#reply-history.button{
    background-color: #323232;
  }
  
  table#pm-table tbody tr.read-0 td{
    background-color: #2C2E30;
  }
  
  /* rakstu/forumu/galeriju atblides */
 
 #comments dd{
   border-top: 4px solid #828282;
 }
 
 #comments dt{
   border-top: 4px solid #828282;
 }
  
 #comments dd p.inf{
   background-color: #2C2E30;
 }
 
 #comments dt a.username{
   background-color: #2C2E30;
 }
 
 #comments dd div.comment-text ul.rpl-list.mbox{
   background-color: #2C2E30;
 }
 
  /* footeris */
  
  #footer-widgets {
      background-color: #5B5A5A;
  }
  
  
  /* googles meklēšana */
  div.gsc-control-cse.gsc-control-cse-lv{
    background-color: #323232 !important;
  }

  /* haštagi */
  #mb-tags{
    background-color: #323232;
  }
  #mb-tags li{
    background: #2C2E30 !important;
  }
  #mb-tags li a{
    text-shadow: none;
  }
  /* brīdinājumi */
  .form > .notice{
    background-color: #828282;
  }
  .form > .notice .mods{
    color: blue !important;
  }
}
