css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://play.pokemonshowdown.com/") {
  
  /* This is my first Theme for PS written from scratch, i will keep on improving. For any bugs or suggestions please drop an Email at: aerosammann1702@gmail.com 
  
  This theme text is entirely "Comic Sans" because its the only font style i love */
  
  body {
    color: #FFF;
    font-size: 11pt;
    font-family: Comic Sans;
    background: #344B6C url("http://forum.cheatengine.org/files/1267407609667_888.jpg") no-repeat fixed left center / cover;
}
  
  .tabbar a.button i {
    display: block;
    text-align: center;
    margin: 1px auto 0px;
    font-style: normal;
    font-size: 14px;
    height: 14px;
}
  
  .tabbar a.button {
    white-space: nowrap;
    font-size: 100px;
}
  
  .tabbar a.button {
    white-space: nowrap;
    font-size: 11px;
    color: black;
}
  
  .maintabbarbottom {
    content: "";
    display: block;
    position: absolute;
    left: 0px;
    right: 0px;
    bottom: -6px;
    height: 6px;
    background: #FFFF00 none repeat scroll 0% 0%;
    border-width: 1px 1px 1px 0px;
    border-style: solid solid solid none;
    border-color: #AAA #AAA #AAA -moz-use-text-color;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    margin: -1px 0px 0px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2);
}
  .ps-room.ps-room-light {
    color: black;
}
 
  .roomlist a.ilink small {
    font-size: 9pt;
}
  
  .roomlist a.ilink {
    color: red;
    text-shadow: 0px -1px 0px #FFF;
    cursor: pointer;
    font-size: 10pt;
    white-space: nowrap;
}
  
  .roomlist {
    text-align: center;
}
  
  .roomcounters button {
    margin: 0px auto;
    width: 140px;
    height: 60px;
    padding: 3px 8px;
    text-align: center;
    font-size: 10pt;
    font-family: Comic Sans;
}
  
  .roomlist a.ilink {
    display: block;
    margin: 2px 7px 4px;
    padding: 1px 4px 2px;
    border: 1px solid #BCD;
    background: #CCFF33 none repeat scroll 0% 0%;
    border-radius: 15px;
    text-decoration: none;
    color: black;
    text-shadow: 0px -1px 0px #FFF;
    box-shadow: 1px 1px 1px #D5D5D5;
    cursor: pointer;
    font-size: 10pt;
    overflow: hidden;
    white-space: nowrap;
}
  
  .mainmenu .menugroup:nth-child(1) .button {
    background: transparent linear-gradient(to bottom, #FF9900, #FFCC33) repeat scroll 0% 0%;
    border-color: #8E443D;
    font-family: Comic Sans;
    font-size: 15px;
    color: black;
}
  
  .menugroup .button {
    box-sizing: border-box;
    font-size: 15px;
    display: inline-block;
    padding: 5px 7px 3px;
    width: 200px;
    text-shadow: 0px -1px 0px #0F1923;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(255, 255, 255, 0.8) inset;
    color: black;
    border: 1px solid #3D668E;
    background: transparent linear-gradient(to bottom, #FF9900, #5384B5) repeat scroll 0% 0%;
}
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button {
    background: transparent linear-gradient(to bottom, #FF9900, #B58453) repeat scroll 0% 0%;
    border-color: #8E663D;
}
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button {
    background: transparent linear-gradient(to bottom, #FF9900, #B58453) repeat scroll 0% 0%;
    border-color: #8E8E3D;
}
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button {
    background: transparent linear-gradient(to bottom, #FF9900, #B58453) repeat scroll 0% 0%;
    border-color: #3D8E4A;
}
  
  .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    background: transparent linear-gradient(to bottom, #FF9900, #B58453) repeat scroll 0% 0%;
    border-color: #663D8E;
}
  
  .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button {
    background: transparent linear-gradient(to bottom, #FF9900, #B58453) repeat scroll 0% 0%;
    border-color: #3D668E;
}
  
  .mainmenufooter a {
    color: red;
    font-size: 13px;
}
  .menugroup button.big {
    width: 230px;
    padding: 6px 7px;
    font-size: 14pt;
}
  
  .select, .team {
    cursor: pointer;
    width: 230px;
    height: 32px;
    padding: 0px 5px;
    font-size: 10pt;
    text-align: left;
    font-family: Comic Sans;
    border-radius: 10px;
    border: 1px solid #888;
    box-shadow: 0px 1px 0px #FFF inset;
    background: transparent linear-gradient(to bottom, #EDF2F8, #D7E3EC) repeat scroll 0% 0%;
    color: #000;
    box-sizing: border-box;
}
  
  .userlist li {
    font: 10pt Comic Sans;
    white-space: nowrap;
}
  
  .chat-log {
    font-size: 6pt;
    color: #000;
    word-wrap: break-word;
    font-family: Verdana;  /*Keeping it verdana for better reading */
}
  
  .ps-popup p, .ps-popup h3 {
    margin: 7px 0px;
    
}
  
  .userlist li button {
    box-sizing: border-box;
    display: block;
    overflow: hidden;
    background: transparent none repeat scroll 0% 0%;
    border: 0px none;
    padding: 1px 0px;
    margin: 0px;
    height: 19px;
    width: 100%;
    white-space: nowrap;
    font: 10pt Comic Sans;
    text-align: left;
    cursor: pointer;
}
  
  /* Stles for the top of PS where all the room names are located */
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 20px;
    background-color: #FFFF00;
}
  
  .tabbar li:first-child a.button {
    border-top-left-radius: 20px;
    margin-left: 20px;
    background-color: #FFFF00;
}
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border-bottom: 3px none;
    padding-top: 3px;
    padding-bottom: 5px;
    top: 0px;
    background-color: #FFFF00;
}
  
  .tabbar a.button {
    display: inline-block;
    height: 50px;
    min-width: 52px;
    white-space: nowrap;
    position: relative;
    padding: 3px 2px;
    margin: 0px -1px 0px 0px;
    top: 1px;
    border-radius: 0px;
    box-shadow: none;
    font-size: 11px;
}
  
  

}
