css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ustart.org") {

.flagMessage {
  display: none !important;
}

.bloc_main {
  background-color: black !important;
}

.content_text {
    color: #888 !important;
    text-shadow: 1px 1px 1px rgb(221, 221, 221) !important;
}

/*CABECERA*/
.bloc_header {
    background-color: #555 !important;
    text-shadow: none !important;
}

.funclink  {
    color: gold !important;
    font-size: 11pt !important;
    font-family: helvetica !important;
    font-weight: bold !important;
}

.bloc_header .unread{
    color: red !important; 
    background-color: black !important; 
}

.item_unread.rss_first_item.content_color {
    background-color: #c0c0c0 !important; 
}

.rss_main_list   {
    background-color: #222 !important; 
}

.rss_main_list > ul  {
    list-style-type: none !important;
    padding-left: 0px !important;
    margin-left: 5px !important;
    border-top: 1px solid !important;
    border-bottom: 1px solid !important;
    border-left: 1px solid !important;
    border-right: 1px solid !important;
    background-color: #333 !important; 
}

li {
    padding-top: 1px !important;
    padding-bottom: 2px !important;
    margin-top: 1px!important;
    border-top: 1px solid #202020;
}


.rss_main_list > ul  >li > a + span > a {
    color: lightgrey !important;
    font-family:verdana !important;
}

.rss_main_list > ul:hover   {
    background-color: #550000 !important; 
    border-top: 1px solid red !important;
    border-bottom: 1px solid red !important;
    border-left: 1px solid red !important;
    border-right: 1px solid red !important; 
}

.rss_main_list > ul:hover > li > a + span > a  {
    color:gold !important;
}

/* QUITAR LA CAPA BLANCA AL CONTENIDO */
#contentdiv  {
    background-image:none !important;
    background-color:transparent !important;
}

}
