css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("my.deviantart.com"){ 

/* ---------- Saving... ---------- */
#lub-saving {
background: url(data:image/png;base64,%3D)!important;
background-repeat:no-repeat!important;
z-index: 999!important;
font-size: 21px!important;
color: #ffffff!important;
border-width:0px!important;
padding-left: 7px!important;
padding-top: 8px!important;
top:3px!important;
right:-3px!important;
width:99px!important;
height:42px!important;
}

/* ---------- Full journal on hover ---------- */
div.mcbox-preview-journal div.mcb-body:hover {
height:auto!important;
max-height:9000px!important;
/*width:650px!important;
max-width:9000px!important;
width:auto!important; */
}

body.bubble div.talk-post div.previewbox, body.bubble div.talk div.text {
overflow:auto!important;
}

/* ---------- Makes the text the full width of your monitor ---------- */
.talkmessage td.f {
width:auto!important
}


div.pager-panes {
overflow:auto!important;
}

/* ---------- Gets rid of the mood junk when you reply ---------- */
div.talk-post table.f td.rr, div.talk-post div.pager-holder {
display:none!important;
}

/* ---------- Makes the 'Original' box width and height auto, but the width no smaller than 180 pixels and no bigger than 600 pixels.  Change the max-width to suit your needs ---------- */
div.popup2-mcbox-comment {
width:auto!important;
/*max-width:600px!important;*/
min-width:180px!important;
height:auto!important;
padding-right:15px!important;
background:#c8d0c5!important;
border:1px solid #3D6A51!important;
-moz-border-radius:8px!important;
margin-right:55px!important;
margin-top:5px!important;
}

/* ---------- I forget what this does ---------- */
div.mczone-select-heybox-active {
display:none!important;
}

/* ---------- The shadow behind the original box ---------- */
div.popup2-shadow, img.popup2-shadow {
display:none!important;
}

/* ---------- Alignes images to the bottom in replies and comments ---------- */
div.mcbox div.talkmessage div.mcb-body img {
vertical-align:bottom!important;
}
}
