css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("derpiboo.ru") {
  body {
   background-color: rgb(0, 0, 0);
   color: #AAA;
   font-family: verdana,arial,helvetica,sans-serif;
   font-size: 13px;
   margin: 0px 8px 8px;
   padding: 0px;
  }
  
  #header > div a, #navigation > div a {
    display: inline-block;
    padding: 5px 8px;
    border-right: 1px solid #CDCDCD;
    background: #EEE none repeat scroll 0% 0%;
    color: #C3C3C3;
}
  
  #header > div a, #navigation > div a {
    display: inline-block;
    padding: 5px 8px;
    border-right: 1px solid #CDCDCD;
    background: #4D4D4D none repeat scroll 0% 0%;
    color: rgb(195, 195, 195);
}
  
  #navigation .searchbox a.searchanchor, #navigation .searchbox a.searchanchor:hover {
    padding: 5px;
    background: #4D4D4D none repeat scroll 0% 0%;
    cursor: default;
  }
  
  #navigation .searchbox form #sbq {
    background: #808080 none repeat scroll 0% 0%;
    color: #FFF;
    padding-top: 0px;
    padding-bottom: 0px;
    vertical-align: bottom;
}
  
  #header > div a, #navigation > div a {
    display: inline-block;
    padding: 5px 8px;
    border-right: 1px solid #717171;
    background: rgb(77, 77, 77) none repeat scroll 0% 0%;
    color: rgb(195, 195, 195);
  }
  
  #navigation .searchbox a {
    border: 0px none;
    background: #4D4D4D none repeat scroll 0% 0%;
    color: #FFF;
  }
  
  div.metabar {
    background: #4D4D4D none repeat scroll 0% 0%;
    color: #DADADA;
  }
  
  div.metabar a {
    background: #666 none repeat scroll 0% 0%;
    color: rgb(195, 195, 195);
}
  
  div.metabar.metabar-sub {
    background: #666 none repeat scroll 0% 0%;
}
  
  #imagespns {
    text-align: center;
    border: 1px dotted #CDCDCD;
    padding: 2px 10px;
    background: #333 none repeat scroll 0% 0%;
}
  
  .image_description {
    display: block;
    clear: both;
    margin: 5px 0px;
    overflow: auto;
    background: #333 none repeat scroll 0% 0%;
    padding: 0px 5px 5px;
    border: 1px solid #4D4D4D;
}
  
  span.tag {
    color: #333;
    background: #B99AE6 none repeat scroll 0% 0%;
    border-color: #983CCF;
}
  
  span.tag a {
    color: #9C2BAD;
}
  
  span.tag.tag-system {
    background: #E2E0B1 none repeat scroll 0% 0%;
    border-color: #BDB44C;
}
  
  span.tag.tag-system a {
    color: #BD9C4C;
}
  
  a, a:active, a:visited {
    color: #57A3DB;
    text-decoration: none;
    border: 0px none;
}
  
  .reply-form {
    max-width: 900px;
    background: #333 none repeat scroll 0% 0%;
}
  
  .reply-form > * {
    border: 1px solid #383838;
    padding: 10px;
}
  
  button, input, select, textarea {
    color: rgb(218, 218, 218);
    font-size: 14px;
    font-family: monospace;
    padding: 3px;
    border: 1px solid #6C6C6C;
    background: #474747 none repeat scroll 0% 0%;
}
  
  .post:nth-child(2n), .post-prevue:nth-child(2n) {
    background: #333 none repeat scroll 0% 0%;
}
  
  .post, .post-prevue {
    max-width: 898px;
    border: 1px solid rgb(56, 56, 56);
    margin-bottom: 8px;
}
  
  .post .post-options, .post-prevue .post-options {
    border-top: 1px solid #4D4D4D;
    display: table;
    font-size: 80%;
    width: 100%;
}
  
  .purple-tag {
    background: #EDB9EA none repeat scroll 0% 0%;
    border: 1px solid #C95AC3;
    color: #C95AC3;
}
  
  .post:nth-child(2n+1), .post-prevue:nth-child(2n+1) {
    background: #333 none repeat scroll 0% 0%;
}
  
  input:focus, select:focus, textarea:focus {
    border-color: #848484;
    background: #2C2C2C none repeat scroll 0% 0%;
    outline: 5px auto #B3B3B3;
}
  
  .spoiler {
    background-color: #4B4B90;
    color: #4B4B90;
}
  

ads #imagespns {
  background: #343535;
  border: 1px solid #424242;
}
  
  .image-warning, #duplicate_check {
    margin: 1em;
    background: #333 none repeat scroll 0% 0%;
    width: 90%;
    padding: 0.618em;
    border: 1px solid #414141;
}
  
  #burger {
    font-size: 18px;
    position: fixed;
    top: 0px;
    left: 0px;
    width: 200px;
    height: 100%;
    display: none;
    overflow: auto;
    background: #333 none repeat scroll 0% 0%;
    padding: 5px;
}
  
  #burger a {
    background: #333 none repeat scroll 0% 0%;
    color: #C5C5C5;
    display: block;
    padding: 5px;
}
  
.dnp-warning {
    padding: 5px;
    margin: 5px 5px 5px 0px;
    border: 3px solid #4C0472;
    background: #776D83 none repeat scroll 0% 0%;
    color: #1B0635;
}
  
  .dnp-warning a {
    font-weight: bold;
    color: #5300DD;
}
  
  iframe {
    border: 1px solid #333;
    padding: 3px;
    margin: 5px 5px 5px 10px;
}
  
  div.tagsinput {
    border: 1px solid #4E4E4E;
    background: #333 none repeat scroll 0% 0%;
    padding: 5px;
    resize: both;
    overflow: auto;
}
  
  div.tagtable .tabs > a {
    display: inline-block;
    padding: 2px 4px;
    font-size: 13px;
    background: #333 none repeat scroll 0% 0%;
    color: #BABABA;
    border-left: 1px solid #6EAEDE;
}
  .first-time {
    font-size: 16px;
    background: #333 none repeat scroll 0% 0%;
    border: 1px solid #4d4d4d;
    padding-left: 10px;
    margin-bottom: 8px;
}
  
  div.image div.imageinfo {
    background: #505050 none repeat scroll 0% 0%;
    width: 100%;
    font-size: 10px;
    text-align: center;
    padding: 3px 0px;
}
  
  div.image {
    display: inline-block;
    overflow: hidden;
    margin: 4px 0px;
    border: 1px solid #4D4D4D;
    padding: 0px;
}
  
  .activity .activity-box {
    margin: 5px 5px 5px 0px;
    border: 1px solid #666;
}
  
  .activity .activity-side .activity-table {
    display: table;
    width: 100%;
    padding: 0px;
    margin: 0px;
    border-collapse: collapse;
    border-top: 1px solid #ABA1F2;
}
  
  .activity .activity-side .activity-table .row:nth-child(2n+1) {
    background: rgb(51, 51, 51) none repeat scroll 0% 0%;
}
  
  .activity .activity-side .activity-table .row:nth-child(2n) {
    background: #4D4D4D none repeat scroll 0% 0%;
}
  
  .activity .activity-side .activity-table .activity-border {
    border-bottom: 1px solid #666;
}
  
  .activity #activity-patreon {
    border-color: #69DC53;
    background: #B3EDA8 none repeat scroll 0% 0%;
    text-align: center;
    padding: 1px;
    font-size: 11px;
    color: black;
}
  
  .green-tag, .red-tag, .purple-tag {
    padding: 4px;
    background: #B3EDA8 none repeat scroll 0% 0%;
    border: 1px solid #69DC53;
    margin: 1px 1px 1px 2px;
    color: black;
}
  
  .comment-activitypage {
    border-bottom: 1px solid #666;
}
  
  div.metabar.metabar-light {
    background: #7A7A7A none repeat scroll 0% 0%;
    color: #333;
}
  
  div.metabar.metabar-light a {
    background: #B3B3B3 none repeat scroll 0% 0%;
    color: #333;
}
  
  div.image div.imageinfo.spoilered a.vote_down_link:not(.voted_down), div.image div.imageinfo.spoilered a.vote_down_link:hover:not(.voted_down) {
    cursor: default;
    color: #D59EA4;
    background: #505050 none repeat scroll 0% 0%;
}
  
  .red-tag {
    background: #FDD none repeat scroll 0% 0%;
    border: 1px solid #FF9191;
}

blockquote {
    margin: 1em 2em;
    border: 1px dotted #BCBCBC;
    padding: 1em;
    background-color: inherit;
}

table.pretty-table tbody {
  border: 1px solid #4D4D4D;
}

table.pretty-table tbody tr:nth-child(odd) {
  background: #666;
}

table.pretty-table tbody tr:nth-child(even) {
  background: #333;
}

.post-slim:nth-child(odd) {
  background: #666;
}

.post-slim {
  margin: 3px;
  padding: 4px;
  padding-left: 7px;
  border: 1px solid #4D4D4D;
}

.post-slim:nth-child(even) {
  background: #333;
}
  
  .important-block {
    max-width: 900px;
    font-size: 15px;
    padding: 5px 15px 15px;
    margin: 5px 5px 5px 0px;
    background: #444 none repeat scroll 0% 0%;
}
  
  .filter {
    background: #333 none repeat scroll 0% 0%;
    width: 70%;
    border: 1px solid #444;
    padding-left: 10px;
    margin-bottom: 8px;
}
  
  .filter .filter-options ul li a {
    color: #77B9E9;
    background: #777 none repeat scroll 0% 0%;
    padding: 5px;
}
}
