css:
 @-moz-document url-prefix("https://play.pokemonshowdown.com/"), url-prefix("http://psim.us/"), url-prefix("http://play.pokemonshowdown.com/"), url-prefix("http://replay.pokemonshowdown.com/"), url-prefix("https://replay.pokemonshowdown.com") { 
  .ps-room.ps-room-opaque {
      background: black;
      color: white;
  }

  .ps-room .battle-controls {
      background: black;
      color: white;
  }

  .battle-log {
      border: none;
      background: black;
      color: white;
  }

  .maintabbarbottom {
      background: black;
      border: none;
  }

  .battle-log h2 {
      margin: 0.5em -8px;
      padding: 4px 8px;
      border: none;
      background: black;
      border-left: 0;
      border-right: 0;
      font-family: Verdana,sans-serif;
      font-size: 13pt;
  }

  .battle-log-add {
      background: black;
  }

  .chatbox label {
      color: dodgerblue !important;
  }

  .leftbar {
      background-color: rgba(0,0,0,0.5);
      border-right: none;
      color: dodgerblue;
  }

  .rightbar {
      background-color: rgba(0,0,0,0.5);
      border-left: none;
      color: red;
  }

  .battle {
      border: none;
  }

  .battle-log .chat em {
      color: white;
  }

  .username {
      color: dodgerblue;
  }

  .header {
      background: black;
  }
}
