css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fireball.psim.us") {
 
  body {
    color: #FFF;
    font-size: 11pt;
    font-family: Verdana,Helvetica,Arial,sans-serif;
    background-image: url("http://www.arollofthedice.net/wp-content/uploads/2015/02/primal-groudon.png");
}
  
 .header .logo { 
   opacity: 0; 
 }
  
  .header { 
    height: 50px; 
    background: rgba(150, 0, 100, 0.1) url("http://i.imgur.com/UkHN2C3.png?1") no-repeat; 
  }
  
  .maintabbarbottom {
    content: "";
    display: block;
    position: absolute;
    left: 0px;
    right: 0px;
    bottom: -6px;
    height: 6px;
    background: #680000 none repeat scroll 0% 0%;
    border-width: 1px 1px 1px 0px;
    border-style: solid solid solid none;
    border-color: #AAA #AAA #AAA -moz-use-text-color;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    margin: -1px 0px 0px;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2);
}
  
  .tabbar a.button i {
    display: block;
    text-align: center;
    margin: 1px auto 0px;
    font-style: normal;
    font-size: 14px;
    height: 14px;
}
  
  .button {
    cursor: pointer;
    text-align: center;
    font-family: Comic Sans;
    color: #222;
    text-shadow: 0px 1px 0px #FFF;
}
  .userbar .username {
    color: #000;
    text-shadow: 1px 1px 0px #F8F8F8, 1px -1px 0px #F8F8F8, -1px 1px 0px #F8F8F8, -1px -1px 0px #F8F8F8;
    font-family: Comic Sans;
}
  
  .select, .team {
    cursor: pointer;
    width: 230px;
    height: 32px;
    padding: 0px 5px;
    font-size: 10pt;
    text-align: left;
    font-family: Comic Sans;
    border-radius: 4px;
    border: 1px solid #888;
    box-shadow: 0px 1px 0px #FFF inset;
    background: transparent linear-gradient(to bottom, #EDF2F8, #D7E3EC) repeat scroll 0% 0%;
    color: #000;
    box-sizing: border-box;
}
  
  .userlist li {
    font: 10pt Comic Sans;
    white-space: nowrap;
}
  
  .userlist li button {
    box-sizing: border-box;
    display: block;
    overflow: hidden;
    background: transparent none repeat scroll 0% 0%;
    border: 0px none;
    padding: 1px 0px;
    margin: 0px;
    height: 19px;
    width: 100%;
    white-space: nowrap;
    font: 10pt Comic Sans;
    text-align: left;
    cursor: pointer;
}
  
  .roomlist a.ilink {
    display: block;
    margin: 2px 7px 4px;
    padding: 6px 10px 5px;
    border: 1px solid #BCD;
    background: #FF6666 none repeat scroll 0% 0%;
    border-radius: 10px;
    text-decoration: none;
    color: black;
    text-shadow: 0px -1px 0px #FFF;
    box-shadow: 1px 1px 1px #D5D5D5;
    cursor: pointer;
    font-size: 10pt;
    overflow: hidden;
    white-space: nowrap;
    font-family: Comic Sans;
}
  
  .roomlist {
    text-align: center;
}
  
  .ps-room.ps-room-light {
    color: #000;
    font-family: Comic Sans;
}
  
  .pad {
    margin: 0px;
    padding: 5px 15px;
    background: black center no-repeat;
    background-image: url("http://roestudios.co.uk/Static/PkmnModels/HQImg/Groudon%20Head%20Side%20BG%20Black0000.jpg");
}

}
