css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.autocomplete-history-dropmarker.urlbar-history-dropmarker {
    display: none !important;
}
#autocomplete-history-dropmarker, #urlbar-history-dropmarker, #urlbar-reload-button {
    display: none !important;
}
#navigator-toolbox::after {
height: 0px !important;
}
#identity-box {
     margin-left: -117px !important;
    filter: grayscale(100%) !important;
    opacity: 0.5 !important;
    border-radius: 0px !important;
}


#main-window:not([chromehidden*="toolbar"])[sizemode="normal"]
{
 margin-top: -2px!important;
 margin-left: -5px !important;
 margin-right: -4px!important;
 margin-bottom: -5px !important;

 }

#urlbar {
    padding-bottom: 0px !important;
    padding-top: 0px !important;
    margin-bottom: 0px !important;
    border-style: solid !important;
    border-color: #f2f2f2 !important;
    border-radius: 0px !important;
    text-align: center;
    background: #f2f2f2 !important;
    color: #bdbdbd !important;
    border: none !important;
    margin-left: 117px;
    
   
}
#nav-bar {
    padding-top: 0px !important;
    background: #f2f2f2 !important;
    filter: grayscale(100%) !important;
    opacity: 1 !important;
    border: none !important;


   
}
    #tabbrowser-tabs tab .tab-close-button {
    display: none !important;
}




.tabbrowser-tab[pinned] {
 background: #f2f2f2 !important;
 border: none !important;

    
}
.tabbrowser-tab:not([pinned]) .tab-icon-image {
display: none !important;
}
.tabbrowser-tab[pinned][selected] .tab-icon-image {
    filter: invert(60%) !important;
}


#TabsToolbar .tabs-newtab-button:hover {
    background: #cacaca !important;
    color: #f2f2f2 !important;
    transition: all .4s !important;
    border: none !important;


}

#TabsToolbar .tabs-newtab-button {
    background: none !important;
    color: #f2f2f2 !important;
    transition: all .4s !important;
    border: none !important;


}
#TabsToolbar {
    background: none !important;

}

#TabsToolbar .tab-background[selected] {
    background: #f2f2f2 !important;
    
}
.tabbrowser-tabs:not([drag=detach]) > .tabbrowser-tab:not([pinned])[fadein] {

    background: #f2f2f2 !important; 
}
  #TabsToolbar .tabbrowser-tabs {
    transition: all .4s !important;
     margin-right: 0px !important;
      margin-left: 0px !important;
      margin-top: -1px !important;
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
      
    }
#TabsToolbar .tabbrowser-tab:not([selected]) {
      color: #f2f2f2 !important;

    background: none !important;
    background-clip: initial !important;
    margin-bottom: -1px !important;
    border: none;
    text-align: center !important;


}
#TabsToolbar .tabbrowser-tab[selected] {
    color: #8c8c8c !important;
    background: #f2f2f2 !important;
    background-clip: initial !important;
    margin-bottom: -1px !important;
    border: none;
      text-align: center !important;
    


}
#TabsToolbar .tabbrowser-tab:hover:not([selected]) {
transition: all .4s !important;
    background: #cacaca !important;
    color: #808080 !important;

    
    
}

.tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) > .tab-stack > .tab-content {
}
.tabbrowser-tab[pinned][selected="true"] > .tab-stack > .tab-content {

padding-bottom: 0px !important;
    margin-right: 0px !important;
    border: none!important;
        transition: all .2s !important;
}




#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    display: none !important;
}

#TabsToolbar .tab-background-middle {
    margin: 0px 0px !important;
    background: transparent !important;
}


.scrollbutton-up, .scrollbutton-down, #alltabs-button {
    display: none !important;
}
#dropmarker-icon, #historydropmarker, #autocomplete-history-dropmarker, #urlbar-history-dropmarker, #dropmarker {
    display: none !important;
}

#autocomplete-history-dropmarker, #urlbar-history-dropmarker, #urlbar-reload-button {
    display: none !important;
}

.autocomplete-history-dropmarker.urlbar-history-dropmarker {
    display: none !important;
}
