css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("https?://(www|encrypted)\.google\.(com|([a-z]{2}))(\.[a-z]{2})?.*") {

  /**
 * ::::::: Animation
 */
  .animated {
    -webkit-animation-duration: 2s;
    animation-duration: 2s;
    animation-fill-mode: both;
  }
  @-webkit-keyframes fadeInUp {from {
      opacity: 0;
      -webkit-transform: translate3d(0, 200%, 0);
      transform: translate3d(0, 200%, 0);
    }
    20% {
      opacity: 0.2;
    }
    80% {
      opacity: 0.6;
    }
    to {
      opacity: 1;
      -webkit-transform: none;
      transform: none;
    }
  }
  @keyframes fadeInUp {from {
      opacity: 0;
      -webkit-transform: translate3d(0, 200%, 0);
      transform: translate3d(0, 200%, 0);
    }
    20% {
      opacity: 0.1;
    }
    80% {
      opacity: 0.6;
    }
    to {
      opacity: 1;
      -webkit-transform: none;
      transform: none;
    }
  }
  .fadeInUp {
    -webkit-animation-name: fadeInUp;
    animation-name: fadeInUp;
  }
  .sfibbbc {
    -webkit-animation-duration: 2s;
    animation-duration: 2s;
    -webkit-animation-fill-mode: both;
    animation-fill-mode: both;
    animation-name: fadeInUp;
    -webkit-animation-name: fadeInUp;
  }
/**
 * ::::::: Search Input
 */
  .sbib_a {
    background: transparent !important;
  }
  .sbibod {
    background: rgba(255, 255, 255, 0.21) none repeat scroll 0 0 !important;
  }
  #sfdiv {
    border: 1px solid rgb(158, 158, 158);
  }

  /**
 * ::::::: Hide Sing Up Bar
 */
  #gb {
    display: none;
  }

  /**
 * ::::::: Hide Country
 */
  #hplogo > div {
    display: none;
  }

  /**
 * :::::: Hide Buttons
 */
  .jsb,
  #jsb {
    display: none;
  }

  /**
 * ::::::: Text and links
 */
  a {
    color: #5b6372;
  }
  .gb_ec.gb_g {
    color: #6c6c6c;
  }
  #als {
      color: #757C7E;
  }

  /**
 * ::::::: Hide Footer
 */
  .fbar {
    display: none;
  }

  /** ············································································
 * ::::::: Search Results
 */
  a:link,
  .w,
  #prs a:visited,
  #prs a:active,
  .q:active,
  .q:visited,
  .kl:active,
  .tbotu {
    color: #5c99e9;
  }
  .a,
  cite,
  cite a:link,
  cite a:visited,
  .cite,
  .cite:link,
  #_bGc > i,
  .bc a:link {
    color: #e67b7b;
  }
  #ires .kv {
    margin-bottom: 5px;
  }
  .g {
    margin-bottom: 40px;
  }
  #cnt {
    background: rgba(255, 255, 255, 0.95);
  }

  /**
 * ::::::: Change Logo
 */
  #hplogo  {
    display: none;
  }
  #lga {
    background-repeat: no-repeat;
    background-size: 40%;
    margin-left: 38%;
    background-position: 0% 64%;
    background-image: url(http://i.imgur.com/iatR7bQ.png);
  }
  @media (min-width: 1024px) { 
    #lga {
      background-size: 35%;
      margin-left: 38%;
      background-position: 2% 64%;
    }
  }
  @media (min-width: 1500px) { 
    #lga {
      background-size: 25%;
      margin-left: 38%;
      background-position: 10% 64%;
    }
  }
  #hplogo a img {
    display: none;
  }
  #hplogo a {
    display: block;
    height: 200px;
    background-repeat: no-repeat;
    background-size: 70%;
    margin-top: 90px;
    margin-left: 24%;
    background-image: url(http://i.imgur.com/iatR7bQ.png);
  }
  body {
    background-image: url(http://i.imgur.com/kF8vtcr.jpg);
    background-size: cover;
  }
}
