css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("recolor.me") {

body {
    background: url('http://doNOT.placeimage.URLhere.png') #A6A7A0 !important;
}
#content {
    height: auto !important;
    padding: 65px !important;
}
#global_container {
    position: relative !important;
    z-index: 1 !important;
    width: auto !important;
    max-width: 100% !important;
    background: url('http://PLACE_YOUR_WALLPAPER_SIZED_IMAGE_URL_RIGHT_HERE.JPG') no-repeat fixed center top transparent !important;
    background-size: cover !important;
    margin: 0px auto !important;
    padding: 0px !important;
    text-align: center !important;
    min-height: auto !important;
} 
    
#logo {
    position: fixed !important;
    display: block !important;
    z-index: 9 !important;
    top: 4px !important;
}
    h1#logo img {
    height: 32px !important;
}
    #logo a .widescreen_only {
    display: none !important;
}
    #logo a .widescreen_hide {
    display: block !important;
}

#header {
    position: fixed !important;
    display: block !important;
    z-index: 7 !important;
    width: 100% !important;
    max-width: 100% !important;
    height: 42px !important;
    background: rgba(50,50,50,0.75) !important;
    box-shadow: 0px 1px 2px 2px rgba(0, 0, 0, 0.42) !important;
}
    #header .small_head {
    position: fixed !important;
    width: 44px !important;
    height: 44px !important;
    top: -8px !important;
    right: 49px !important;
    border-radius: 6px !important;
}
    #header .small_head img.imgflip {
    top: -38px;
    left: -39px;
}
    #header #header_name {
    position: relative !important;
    font-size: 18px !important;
    top: -2px !important;
    right: 100px !important;
    line-height: 25px;
    text-shadow: 0px 0px 3px #000;
    margin: 0px 10px 0px 4px;
}
    #header #header_name:hover {
    border-bottom: none !important;
    color: #F0F0F0 !important;
}
    #header #header_bits {
    position: fixed !important;
    top: 24px !important;
    right: 110px !important;
    padding: 0px 0px 0px 0px !important;
}
    #header #mybits {
    font-size: 12px !important;
    color: #F0F0F0 !important;
    text-shadow: none !important;
}
    #header #header_bits a img {
    position: fixed !important;
    top: 6px !important;
    right: 5px !important;
    height: 31px !important;
}

#table_stats {
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.75) !important;
    max-width: 380px !important;
    border-radius: 10px !important;
    box-shadow: 0px 1px 1px 1px rgba(0, 0, 0, 0.25) !important;
}
    #table_stats td {  
    font-size: 20px !important;
    color: #F0F0F0 !important;
    text-shadow: 0px 0px 3px #111 !important;
    padding: 5px 6px 10px !important;
}
    
#rcmenu {  
    width: 121px !important;
    display: block !important;
    z-index: 7 !important;
    box-shadow: none !important;
    background: none !important;
}
    #rcmenu #chats {
    top: 60px !important;
    bottom: 40px !important;
}
    #rcmenu #chats .chat {
    width: 97px !important;
    height: 20px !important;
    margin-bottom: 50px !important;
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.5) !important;
}
    #rcmenu #chats .chat .small_head {
    left: 25px !important;
}
    #rcmenu #chats .chat .chatlink_content {
    display: none !important;
}
    #rcmenu #chats .chat .chat_time {
    right: 12px !important;
}
    #rcmenu #chats .chat.unread {
    background: none repeat scroll 0% 0% rgba(39, 131, 229, 0.8) !important;
}
    #rcmenu #chats .chat:hover {
    background: none repeat scroll 0% 0% rgba(255, 102, 255, 0.5) !important;
}
    #rcmenu #button_chat_friends {
    bottom: 5px !important;
    font-size: 14px !important;
    width: 100% !important;
    line-height: 10px !important;
    text-align: left !important;
}
    #rcmenu #rcmenu_top {
    position: fixed !important;
    display: flex !important;
    top: 4px !important;
    left: 202px !important;
    width: 232px !important;
    height: 30px !important;
    text-align: left !important;
}
    #rcmenu #rcmenu_top a {
    font-size: 14px !important;
    width: 65px !important;
    background: none !important;
    border: none !important;
    box-shadow: none !important;
}
    #rcmenu #rcmenu_top a.highlight {
    color: #FFD700 !important;
}
    #rcmenu #rcmenu_bottom {
    position: fixed !important;
    top: 2px !important;
    left: 60px !important;
    height: 42px !important;
    text-align: left !important;
    width: 150px !important;
    max-width: 24% !important;
}
    #rcmenu #rcmenu_bottom a {
    margin: 0px 0px 0px 5px !important;
}
    #rcmenu #rcmenu_bottom a img.rcmenu_img {
    height: 36px;
}
    #rcmenu #rcmenu_bottom .tinyhead {
    border: none !important;
    background: url('https://recolor.me/images/forum/9.png') no-repeat !important;
    background-size: contain !important;
    width: 34px !important;
    height: 34px !important;
    opacity: 0.8 !important;
}
    #rcmenu #rcmenu_bottom .tinyhead:hover {
    opacity: 1 !important;
}
    #rcmenu #rcmenu_bottom .tinyhead img {
    display: none !important;
}
    #rcmenu #button_chat_friends {
    color: #F0F0F0 !important;
}
    #rcmenu #button_chat_friends:hover {
    color: #FFD700 !important;
}
    #rcmenu #button_chat_friends img {
    display: none !important;
}
    #button_rcmenu {
    display: none !important;
}
    
    /*--Forum Codes--*/
    
table.topic_list, table.sell_list {
    opacity: 1 !important;
}
    .text_container.centered.limited.text_left {
    opacity: 1 !important;
    margin-bottom: 10px !important;
}
    .forum_topic_container h1 { 
    font-size: 32px !important;
    text-shadow: 0px 0px 7px #222222 !important;
    padding: 5px 0px 2px !important;
    line-height: 35px !important;
    max-width: 98% !important;
}
    .forum_watching a {
    color: #F0F0F0 !important;
    font-size: 14px !important;
    opacity: 1 !important;
    width: 185px !important;
    height: 22px !important;
    border: none !important;
}
    .post_content {
    margin: 0px 0px 0px 210px;
    opacity: 1 !important;
}    
    .forum_topic .post .post_content .post_id a {
    color: #F0F0F0 !important;
} 
    .post_content .speech_bubble_message {
    margin-top: -8px !important;
    max-height: none !important;
    min-height: 70px !important;
}
    #editpost_buttons {
    color: #FFF !important;
}   
    .post_background {
    border-radius: 55px !important;
    border-top-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
    width: 435px !important;
    height: 192px !important;
    margin-left: -32px !important;
    background-color: #F0F0F0 !important;
    opacity: 0.3 !important;
} 
    .forum_topic .post .post_avatar, .forum_topic .post .post_avatar a {
    color: #F0F0F0;
    font-size: 15px !important;
}
    .sub_icon_container {
    position: absolute !important;
    top: 48px !important;
    left: 155px !important;
}
    #newtopic_content .bbcode_toolbar {
    position: relative !important;
    text-align: right !important;
    left: 0px !important;
    top: 0px !important;
}
    
    /*--reply box--*/
   
.forum_topic_reply h3 {
    color: #FFFFFF !important;
    margin-left: -160px !important;
}
    .bbcode_toolbar {
    position: absolute !important;
    text-align: center !important;
    width: 100% !important;
    top: 13px !important;
    left: -96px !important;
}   
    #emote_list {
    width: 580px !important;
}
    .post_color_container label {   
    color: #FFF !important;
    text-shadow: 0px 0px 7px #000 !important;
}   
    
#footer {
    background: url('http://put.footer.imageHERE.png') repeat scroll 0px 0px #000 !important;
    width: 100% !important;
    margin: auto !important;
    padding: 8px 0px !important;
}
    #footer #item_subscription a {
    font-size: 20px !important;
}
    #snowbattle_game {
    background: url('https://recolor.me/images/site/bg_snowground_long.png') !important;
    border-radius: 12px !important;
    padding: 30px 30px 30px !important;
}
    div.blockUI.blockOverlay {
    opacity: 1 !important;
    background-color: rgba(0,0,0,0.66) !important;
}
    /*--PROFILE CODE, delete if you prefer the other profiles--*/
    
    .profile_container h2 {  
    padding: 16px 15px 16px 0px !important;
    color: #FFF !important;
    text-shadow: none !important;
    background: #999 !important;
    border-top-left-radius: 8px !important;
    border-top-right-radius: 8px !important;
    border-bottom: 5px dashed #FFF !important;
}
    .profile_container .profile_avatar {
    width: 230px !important;
    top: 70px;
    left: 10px;
}
    .profile_container .profile_options {
    top: 296px !important;
    font-size: 14px !important;
    text-align: center !important;
    width: 230px !important;
}
    .profile_container .profile_bio {
    width: 290px !important;
    top: -30px !important;
    float: right !important;
    background: none !important;
}
    .profile_bio_content iframe.bbcode_youtube {
    width: 100% !important;
    height: 175px !important;
}
}
    /*--GAMES--*/

@-moz-document url("https://recolor.me/ghost") {
    #ghost_container {
    background: none repeat scroll 0% 0% rgba(120, 120, 120, 0.9) !important;
    max-width: 470px !important;
    border-radius: 14px !important;
    padding: 15px 40px 15px !important;
    }
    .itemset {
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0) !important;
    box-shadow: none !important;
    }
}
@-moz-document url("https://recolor.me/darkworld?explore") {
    #darkworld_container {
    background: none !important;
}
    body.zone_4 #darkworld_zone {
    padding-top: 100px !important;
}
}
@-moz-document url-prefix("https://recolor.me/slide") {
    #header {
    display: none !important;
    }
    #rcmenu {
    display: none !important;
    }
    #logo {
    top: 128px !important;
    left: 75px !important;
    }
    #slide_quest {
    position: absolute !important;
    display: block !important;
    max-width: 100% !important;
    }
    #content {
    padding: 0px !important;
    height: 100% !important;
    }
    .slide_room .background.background_sky {
    background: url('http://recolor.me/images/site/clouds.jpg') no-repeat fixed center !important;
    background-size: cover !important;
}
}
@-moz-document url-prefix("https://recolor.me/quads/game/") {
    #header {
    display: none !important;
    }
    #logo {
    display: none !important;
    }
    #rcmenu {
    display: none !important;
    }
    #content {
    height: auto !important;
    padding: 0px !important;
}
}
@-moz-document url-prefix("https://recolor.me/play/") {
    #minigame_container #div_game #div_game_playzone {
    background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.6) !important;
    border-radius: 14px !important;
    padding: 0px 0px 30px !important;
}
    #game_message_scoreboard {
    box-shadow: none !important;
    line-height: 45px !important;
    opacity: 0.9 !important;
    border-bottom-right-radius: 14px !important;
    border-bottom-left-radius: 14px !important;
}
    #div_game h2 img {
    border-radius: 7px !important;
    margin-right: 15px !important;
}
    #game_invert {
    background: none !important;
}
    #matchnone_objects div.conflict {
    background: #000 !important;
}
}
    /*--Landing Pages--*/

@-moz-document url("https://recolor.me/") {
    body.plains_dark #global_container {
    position: absolute !important;
    height: 100% !important;
    width: 100% !important;
}
}
@-moz-document url-prefix("https://recolor.me/?name=") {
    body.plains_dark #global_container {
    position: absolute !important;
    height: 100% !important;
    width: 100% !important;
}
}
@-moz-document url-prefix("https://recolor.me/market") {
.text_container.text_left {
    position: absolute !important;
    top: 470px !important;
    left: 10% !important;
    width: 82% !important;
}
    #avatar_item_details {
    position: absolute !important;
    top: 120px !important;
    left: 60% !important;
}
    #item_details.text_container.text_right {
    position: absolute !important;
    width: 375px !important;
    right: 60% !important;
    top: 78px !important;
}
    .text_center {
    margin-bottom: 230px !important;
}
}
@-moz-document url-prefix("https://recolor.me/friends") {

    .limited.centered.text_container.text_left {
    width: 90% !important;
    max-width: 90% !important;
}
    .database_friend {
    width: 275px !important;
    font-size: 14px !important;
}
    .database_friend:nth-of-type(2n) {
    background: none !important;
}
    .bbcode_selfie.big {
    border-radius: 8px !important;
}
}
@-moz-document url("https://recolor.me/world") {
#global_container {
    position: absolute !important;
    height: 100% !important;
    width: 100% !important;
}
    #world {
    background: none !important;
}
    #map {
    background: none !important;
    box-shadow: none !important;
    width: 82% !important;
    height: 100% !important;
    }
    .absolute.legend_left img {
    display: none !important;
    }
    .absolute.legend_middle {
    display: none !important;
    }
    #map .legend {
    width: 270px !important;
    height: 35px !important;
    font-size: 16px !important;
    position: static !important;
    line-height: 36px !important;
    display: inline-block !important;
    margin-bottom: 5px !important;
    }
    #map .legend br {
    display: none !important;
    }
    #map a.legend.legend_middle_left {
    margin-top: 20px !important;
    }

}
@-moz-document url("https://recolor.me/polls") {
    .repoll_option {
    width: 450px !important;
    max-width: 450px !important;
}
    .repoll_option_count {
    left: 440px !important;
    font-size: 28px !important;
}
    #repoll_poll_container #repoll_option1 {
    right: 0px !important;
    top: 90px !important;
}
    #repoll_poll_container #repoll_option2 {
    left: 0px !important;
    top: 190px !important;
}
    #repoll_option_count2.repoll_option_count {      
    top: 200px !important;
}
    #repoll_option_goldcount1 {
    top: 120px !important;
}
    #repoll_option_goldcount2 {
    top: 220px !important;
}
    #repoll_poll_container #repoll_magnet {
    display: none !important;
} 
    #repoll_poll_container #repoll_avatar {
    display: none !important;
}
}
@-moz-document url-prefix("https://recolor.me/faction") {

    #faction_header {
    max-width: 100% !important;
    padding-bottom: 90px !important;
}
    #faction_header #bank {
    position: relative;
    z-index: 2;
    top: 20px !important;
}
#faction_header #bank img {
    width: 150px !important;
    top: 100px !important;
}
    .speech_bubble {
    width: 78% !important;
    }        
}
