css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://getmusicbee.com/forum/")
{

/* Color convention
black: #000
dark grey: #222
light grey: #666
orange: rgb(253,196,35)
*/


    /* Block Facebook and Twitter (uncomment this to hide facebook link)*/
    /*div#footerarea,
    #twitterB:link,
    a#facebook
       {display: none !important;}*/


    /* Wider */
    body
    {width: 100% !important;}


    /*Black BG */
    .tborder,
    headertitles,
    .titlebg,
    tr.titlebg th,
    tr.titlebg td,
    .titlebg a:link,
    .titlebg a:visited,
    .titlebg2,
    tr.titlebg2 th,
    tr.titlebg2 td,
    .titlebg2 a:link,
    .titlebg2 a:visited
       {background: #000 !important;}


    /* Dark Grey BG */

    body,
    .headertitles
    {background: #090909 !important;}


    #bodyarea,
    .windowbg,
    .windowbg2,
    .headerbodies,
    td.catbg
    {background-color: #090909 !important;}

    .catbg,
    .catbg2 /* Home page forum headers */
    {background: #333}
    .catbg3
    {background-image: none !important;}
        

    /* Fonts light grey */
    body,
    td,
    th
        {color: /*[[textFontColor]]*/ !important;}


    /* Fonts links yellow rgb(253,196,35) */
    a,
    a:link,
    #tabs a span
    {color: /*[[linkFontColor]]*/ !important;}


    /*  Borders Black */
    .tborder,
    .headerbodies
    {border: 1px solid #444 !important;}
    
    #headerarea
    {border-bottom: 1px solid #444 !important;}
    
    .bordercolor
    {background-color: #444 !important;}

    /* Text Box (Search, Quick Reply)*/
    input, textarea, button
    {
        color: #FFF !important;
        background: #000
    }
    
    /*Footer at bottom of page */
    #footerarea
    {
    margin-top: 10px;
    color: white;
    background-color: black;
    border-top: 1px solid #AAA; 
    }
    
    /*Personal Messages*/
    .windowbg {
    color: #FFF !important;
    
}
    .windowbg2 {
    color: #FFF !important;
    background: #111 !important;
}
    /*Forum Search Results*/
    .content, .roundframe {
    color: #FFF;
    }
    
}
