css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hplusmagazine.com")

/*

Singularity Utopia, @2045singularity, created 14 Sept 2015.
Last update: 16 Oct 2015

*/   
      
{
           
#logo
        
        {
            box-shadow: black 0px 0px 35px 35px;
            padding: 0px !important;
            margin: 40px !important;
            border-radius: 1px;           
            
            animation-name: SUani;
            animation-duration: 12s;
            animation-timing-function: ease-in-out;
            animation-iteration-count: infinite;
            -webkit-animation-name: SUani;
            -webkit-animation-duration: 12s;
            -webkit-animation-timing-function: ease-in-out;
            -webkit-animation-iteration-count: infinite;           
        }
        
@-webkit-keyframes SUani
        
        {
            0% {opacity: 1;}
            25% {opacity: 0.8; border-radius:20px; box-shadow: black 0px 0px 30px 25px, purple 0px 10px 35px 35px; }
            40%{transform: scale(1);}
            45%{transform: scale(0.84);}
            49%{transform: scale(1);}
            50% {opacity: 0.6;}
            75% {opacity: 0.8;}
            100% {opacity: 1;}
        }
                
@keyframes SUani
        
        {
            0% {opacity: 1;}
            25% {opacity: 0.8; border-radius:20px; box-shadow: black 0px 0px 30px 25px, purple 0px 10px 35px 35px; }
            40%{transform: scale(1);}
            45%{transform: scale(0.84);}
            49%{transform: scale(1);}
            50% {opacity: 0.6;}
            75% {opacity: 0.8;}
            100% {opacity: 1;}
        }
           
.fa-rss-square
 
        {
            color: orange !important;
            background: white;
            padding: 0px 2px 0px 2px;  
            border-radius: 6px;
            border: 1px solid black !important;                    
        }

#header

        {
            background-image: url("https://archive.today/THlgS/ac57aea13cff193e36109c17fdf7c4d3fd8e0dc0.png");
            box-shadow: inset white 0px -8px 25px -18px, inset #161B3F 0px 0px 30px 10px, inset black 0px 0px 70px 20px;
            border-width: 1px 1px 0px 1px;
            border-style: solid;
            border-color: black !important;
        }

#nav

        {
            background: #444;
            box-shadow: inset lightgrey 0px 0px 1px, inset #696969 0px 10px 10px;
            border-width: 0px 1px 0px 1px;
            border-style: solid;
            border-color: black !important;
        }
    
#nav .sub-menu a:hover 

        {
    
            padding-left: 25px;
            padding-right: 25px;
            margin-left: -25px;
            margin-right: -25px;
            width: auto;
            box-shadow: inset grey 0px 0px 28px;
            border-radius: 2px;
        }    
        
#nav .sub-menu
        
        {
            border:6px solid #696969;
            box-shadow: grey 0px 14px 25px;
        }

#header #searchform


        {
            box-shadow: black 0px 3px 30px 4px !important;
            border-radius: 0px 2px 2px 0px;
        }
    
.SearchSidebarWidget #searchbar #searchform
    
        {
            overflow: visible !important;
        }

.responsive #page-wrapper
        
        {
            width: 98% !important;
            max-width: 1400px !important;
        }
        
#page-wrapper 
        
        {
            box-shadow: 0px 0px 30px 1px #DDD, grey 0px 16px 17px -14px;
        }
        
#content-wrapper
        
        {
            border-width: 0px 1px 0px 1px;
            border-style: solid;
            border-color: #8F9194 !important;
        }
        
#footer
       
        {
            border-width: 0px 1px 2px 1px;
            border-style: solid;
            border-color: #8F9194 !important;
            border-radius: 0px 0px 8px 8px;
            background: #E8E4E1;
        }
    
.post-grid-container-main
    
        {
            padding: 0px !important;
            margin-top: -20px !important;
        }
    
.post-grid-items .title
        
        {
            font-family: arial;
            font-size: 13pt !important;
            background: #EDF2F7 !important;
            border-radius: 7px 7px 0px 0px;
            box-shadow: inset white 0px 1px 1px;
        }
    
.post-grid-items .title:hover
    
        {
            transition: ease-in-out 0.3s;
            background: white !important;
        }
        
.post-grid-items .meta
        
        {
            background: #EDF2F7 !important;
            border-radius: 0px 0px 7px 7px;
            box-shadow: inset white 0px -1px 1px;
        }
    
        
.grid-single    
        
        {
            background: #EDF2F7 !important;
            border-radius: 7px;
            border: 1px solid #C9C9C9;
            border-bottom-width: 2px;
            box-shadow: lightgrey 0px 7px 10px -5px;
            transition: ease-in-out 0.25s;
        }
       
.grid-single:hover

        {
            box-shadow: grey 0px 7px 30px 5px;
            border-color: #8D99A8;
            transition: all ease-in-out 0.46s;
        }
    
.entry-header .entry-title

        {
            text-shadow: #fff 0px 3px 4px;
            background: #FFF5E3;
            font-style:italic;
            border-radius: 5px 5px 3px 3px;
            padding: 9px 18px 13px 18px !important;
            border-color: #C9C7C5 #C9C7C5 #BAB6B3 #C9C7C5 !important;
            border-width: 1px 1px 2px 1px !important;
            border-style: solid;
            margin-bottom: 15px !important;
            box-shadow: inset white 0px 0px 2px 1px, grey 0px 7px 12px -10px;
            line-height: 1.25 !important;
        }
    
img.size-full, .post-title.entry-title img, img.aligncenter 
    
        {
            border-radius: 4px !important;        
        }
    
a.bbp-reply-to-link, a.bbp-topic-reply-link
    
        {
            color: #4749BF !important;
        }
    
#profile-button
    
        {
            color: #A1F2FF !important;
        }

.widgettitle  
    
        {
            background: #F2F2F2;
            border-radius: 3px 40px 3px 3px;
            padding: 8px 15px 8px 17px;
            border: 1px solid #E3E3E3;
            box-shadow: inset white 0px 0px 1px;
        }
    
p, .post-body.entry-content 
        
        {
            line-height: 1.75 !important;
        }
        
.entry-content 
        
        {
            font-size: 12pt;
            font-family: georgia !important;
            color: #292929;
        }
                
 body 
        
        {
            background: #EAEEF3
        }
        
}
