#tabs {
	width: 48%;
	margin-left: 50%;

	position: absolute;
	top: 0;
}

.ui-widget-header {
	background-color: #e74c3c; 
	border: 0;
	width: 100%;
	font-size: 13px !important;
}

#mission_aao_no_category, #mission_vehicle_driving, #mission_vehicle_at_mission, #mission_replies, #new_mission_reply, #aao-tabs  {
	width: 48%;
	position:relative;
}

#aao_without_category, #amount_of_people {
	margin-right: 52%;
}

#missionH1 {
	width: 48%;
}

#mission_window_frame .alert {
	width: 44.5%;
}

#mission_window_frame h2 {
	font-size:28.5px;
}

#mission_window_frame .progress {
	width: 48%;
	height: 20px;
}

#all, #feuerwehr_lf, #feuerwehr_other, #rettungsdienst, #polizei, #thw {
	width: 95%;
}

.alarm_bottom {
	background-color: #e74c3c; 
}

.btn-group>.btn:first-child {
	-webkit-border-top-right-radius: 0 !important; 
	border-top-right-radius: 0 !important; 
	-webkit-border-bottom-right-radius: 0 !important; 
	border-bottom-right-radius: 0 !important; 
	-moz-border-radius-topright: 0 !important; 
	-moz-border-radius-bottomright: 0 !important; 
}

.btn-group>.btn:last-child {
	-webkit-border-top-right-radius: 0 !important; 
	border-top-right-radius: 0 !important; 
	-webkit-border-bottom-right-radius: 0 !important; 
	border-bottom-right-radius: 0 !important; 
	-moz-border-radius-topright: 0 !important; 
	-moz-border-radius-bottomright: 0 !important; 
}

.btn-group>.btn:first-child {
	-webkit-border-bottom-left-radius: 0 !important; 
	border-bottom-left-radius: 0 !important; 
	-webkit-border-top-left-radius: 0 !important; 
	border-top-left-radius: 0 !important; 
	-moz-border-radius-bottomleft: 0 !important; 
	-moz-border-radius-topleft: 0 !important; 
}

#lightbox_background {
	opacity: 0.5;
}

#lightbox_box {
	-webkit-border-radius: 0;
	-moz-border-radius: 0;
	border-radius: 0;
}

.alarm_bottom .btn-mini {
	height: 25px !important;
	margin: 0 auto !important;
	padding-top: 2px;
	padding-bottom: 2px;
}
