css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(irccloud.com) {
    /* Global Styles */
   span.content {
        font-family: "/*[[font]]*/";
        font-size: /*[[size]]*/;
    }
	div.log div.row span .bufferLink.author,
	.buffernick .nick {
    	padding-right: 5px;
	}    
    /* Ash Theme */
    body.theme-ash span.content {
    	color: #fff;
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash div#timeContainer, body.theme-ash div.log span.date {
        color: #e5e5e5
    }
    body.theme-ash li.user {
        color: #eee;
        font-size: 11pt;
    }
    body.theme-ash a.buffer.bufferLink.user {
        color: #fff;
    }
    body.theme-ash .status.bufferHead {
        background-color: #333;
    }
    body.theme-ash {
        color: #FFF;
    }
    body.theme-ash div#sidebar {
        background-color: #333;
    }
    body.theme-ash span.black {
        color: #000;
        text-shadow: 0px 0 7px #FFF;
    }  
    body.theme-ash span.red {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.white {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.blue {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.green {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.navy {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.maroon {
        color: #CA5648;
    }
    body.theme-ash span.yellow {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.lime {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.teal {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.cyan {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.magenta {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.grey {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.silver {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.purple {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.orange {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash span.reset {
        text-shadow: 1px 1px 0 #000;
    }
    body.theme-ash .inputcell textarea {
        color: #FFF;
        font-family: "/*[[font]]*/";
        font-size: 12px;
        padding: 0.8px 20px 0 0;
        text-shadow: 1px 1px 0 #000;  
    }
    body.theme-ash .bufferList h2 a.buffer, body.theme-ash .bufferList h2.buffer a.buffer {
        background-color: #272727;
        border-color :#484848;
    }
    body.theme-ash div.memberwrapper {
        background-color: #333;
        border-color: #272727;
    }
	body.theme-ash .bufferList h2 span.buffer {
		background-color: #222;
    }  
    body.theme-ash .extras span.extrasBar {
        background-color: #222;
    }  
    /* Dawn Theme */
    body.theme-dawn span.content {
      text-shadow: 0 0 0 #000;;
    }
    body.theme-dawn .inputcell textarea {
      font-family: "/*[[font]]*/";
      font-size: 12px;
      padding: 0.8px 20px 0 0;
      text-shadow: 0 0 0 #000;
    }
    body.theme-dawn li.user {
        color: #444;
        font-size: 11pt;
    }      
    /* Dusk Theme */
    body.theme-dusk span.content {
      color: #fff;
      text-shadow: 1px 1px 0 #000;
    }
    body.theme-dusk span.content span.black {
      text-shadow: 0 0 0 #000;
    }  
    body.theme-dusk .inputcell textarea {
