css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("birdz.sk") { 
  
.status_first-letter {
  display:none;
}

.status_inner {
  margin:0 !important;
  width:100% !important;
  border-radius:0 !important;
  border:0 !important;
  padding:0 !important;
  background:#ffffff !important;
  background-color:#ffffff !important;
  height:auto !important;
  transform:none !important;
  position:initial;
}

.status--skin-5 .status_content {
  margin-top:0;
}

.status_content {
  padding:5px 8px !important;
  border:none !important;
  position:initial !important;
  font-size:14px !important;
  color:#666666 !important;
  background-color:#ffffff !important;
  font-weight:100 !important;
}

.status--skin-9 .status_inner {
  background:#ffffff !important;
}

.status--skin-2 .status_content, .status--skin-2 .status_content > span {
  background:none;
}

.status {
  position:initial;
  max-width:none;
  text-align:left;
}

.status::before,
.status_inner::before,
status_content::before,
.status--skin-9:after,
.status--skin-9 .status_inner:after {
  display:none !important;
}

.status--skin-1,
.status--skin-2,
.status--skin-3,
.status--skin-4,
.status--skin-5,
.status--skin-6,
.status--skin-7,
.status--skin-8,
.status--skin-9,
.status--skin-10,
.status--skin-11,
.status--skin-12 {
  font-family:inherit;
  color:#666666;
  font-weight:100;
  font-style:normal;
}
  
.status--skin-3 .status_content {
  text-align:left;
}

.stena-listing .listing_item {
  float:none;
  width:100%;
  margin-bottom:30px;
  padding:10px 0;
  border-bottom:1px #2599cb solid !important;
}

.status--skin-11, .status--skin-12 {
  text-shadow:none;
}

.status--skin-10,.status--skin-10 .status_inner,.status--skin-9 .status_inner {
  text-align:left;
}
  
.status--skin-2 {
  text-transform:none;
}

.micro-listing_list--statuses .micro-listing_item {
  width:100%;
  border-bottom:1px #2599cb solid;
  padding:0;
  margin:0;
}

.main.bprofile .status {
  background:purple !important;
  width:100%;
  display:block !important;
  height:auto;
  margin-bottom:30px;
}

.main.bprofile .status .status_content {
  font-size:20px !important;
  font-weight:500 !important;
}
  
.comment_content > img, .comment_img {
  width:100%;
}

.birdz.layout-1 .content-main, .bprofile.layout-1 .content-main {
  padding:10px 0 0 10px;
}

.birdz.layout-1 .content-main .sub-nav, .bprofile.layout-1 .content-main .sub-nav {
  margin-left:0;
}

.sub-nav{
  margin:0;
} 

.status img {
  max-width:100%;
}

.comment {
  width:99%;
}
  
.skin-changer_link {
  height:100px;
}

.status_content a {
  color:#2599CB;
  text-decoration:underline;
}
  
.feed .item:hover,.feed .item:hover .status_content {
  background:#eeeeee !important;
}
  
@media all and (max-device-width:1000px) {
  .status_content {
    font-size:25px !important;
  }
  
  .main.bprofile .status .status_content {
    font-size:30px !important;
  }
}  
  
}
