css:
@-moz-document domain("birdz.sk") {
.stena-listing > li {
	clear: inherit !important;
}

.status_first-letter {
  display:none;
}

.status_inner {
  margin:0 !important;
  width:100% !important;
  border-radius:0 !important;
  border:0 !important;
  padding:0 !important;
  /*background:#fafafa !important;*/
  background-color:transparent !important;
  min-height:150px !important;
  transform:inherit !important;
  position:initial;
}

.status--skin-5 .status_content {
  margin-top:0;
}

.status_content {
  border:none !important;
  /*position:relative !important;
  top: 50%;
  transform:translateY(-50%);*/
  font-size:14px !important;
  color:#666666 !important;
  background-color:transparent !important;
  font-weight:100 !important;
  position: inherit !important;
}

.status--skin-2 .status_content, .status--skin-2 .status_content > span {
  background:none;
}

.status {
  position:initial;
  max-width:none;
  padding:40px;
  border: none;
  background: transparent !important;
  text-transform: inherit !important;
}

.status::before,
.status_inner::before,
status_content::before,
.status--skin-9:after,
.status--skin-9 .status_inner:after {
  display:none !important;
}

.status--skin-1,
.status--skin-2,
.status--skin-3,
.status--skin-4,
.status--skin-5,
.status--skin-6,
.status--skin-7,
.status--skin-8,
.status--skin-9,
.status--skin-10,
.status--skin-11,
.status--skin-12 {
  font-family:inherit;
  color:#666666;
  font-weight:100;
  font-style:normal;
}

.stena-listing .listing_item {
	border:1px solid #ededed;
	background:#fafafa;
	width:48% !important;
	margin:0 2% 20px 0 !important;
	padding:0px !important;
	
	background: -webkit-linear-gradient(60deg,#c0e4f8 0,#34aadc 30%,#c0e4f8 90%);
    background: linear-gradient(30deg,#c0e4f8 0,#34aadc 30%,#c0e4f8 90%);
	position:relative;
}
.stena-listing .listing_item .item_status {
	background: transparent;
}
.stena-listing .item_footer {
	padding: 10px 15px !important;
	background: rgba(255,255,255,0.8) !important;
	position:initial !important;
	text-align: center;
}
.stena-listing .item_footer .avatar {
	width: 50px;height: 50px;
	position:absolute;
	top:-15px;left:50%;
	margin-left:-25px;
}
.status_content {
	font-size: 14px !important;
	color: #222 !important;
	text-shadow: 0px 0px 55px white !important;
	padding: 0 !important;
}
.status_content .video-player {
	width:84px;height:84px;
	margin: 10px auto;
}
.status_content .video-player_icon {
	width:84px;height:84px;
	margin-top:-42px;margin-left:-42px;
	background:url("http://rbg.wz.cz/birdz/player3.png");
	opacity:0.3;
}

.sidebar.cf .status {
	padding:5px;
}
.sidebar.cf .item_status:before {
	content:"";
	position:absolute;
	bottom:0;left:0;
	width:100%;height:150px;
		/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#ffffff+0,fafafa+100&amp;0+59,1+100 */
background: -moz-linear-gradient(top,  rgba(255,255,255,0) 0%, rgba(252,252,252,0) 59%, rgba(250,250,250,1) 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255,255,255,0)), color-stop(59%,rgba(252,252,252,0)), color-stop(100%,rgba(250,250,250,1))) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(255,255,255,0) 0%,rgba(252,252,252,0) 59%,rgba(250,250,250,1) 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(255,255,255,0) 0%,rgba(252,252,252,0) 59%,rgba(250,250,250,1) 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(255,255,255,0) 0%,rgba(252,252,252,0) 59%,rgba(250,250,250,1) 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  rgba(255,255,255,0) 0%,rgba(252,252,252,0) 59%,rgba(250,250,250,1) 100%) !important; /* W3C */
/* filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00ffffff', endColorstr='#fafafa',GradientType=0 );  IE6-9 */
	z-index:9999;
}
.sidebar.cf .status .status_inner {
	height: 180px !important;
	max-height: 180px !important;
}
.sidebar.cf .status .status_content {
	transform: inherit !important;
	padding:5px;
	top:0;
}


#feed_data .status {
	background:#fafafa !important;
	padding: 30px;
}
#feed_data .status .status_inner {
	min-height: inherit !important;
}
#feed_data .status .status_content {
	font-size: 20px !important;
	text-align: left !important;
	color: #999 !important;
}
}
