@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("torrentleech.pl") {

body,html{
    background: url("/*[[bg]]*/") !important; 
    background-attachment: fixed !important;
    background-size: 100% 100% !important;
    font-family:sans-serif!important;
    margin-left: 0px;
    margin-right: 0px;
    color: #ffffff;
}
 
td.clear{border: 0px!important;}


table.main, table.mainouter, table.bottom{
    background: transparent !important;
    padding: 0px !important; 
    border-spacing: 0px;
    border: 0px !important;    
    
}


 td.embedded{background: transparent !important;}
    
 
tbody tr td {    border: solid #0a4400 0px;
}

tr.darmowy{background-color:#0c5200!important}
td.comment{padding:10pt;font-size:8pt;text-align:left;color:#FFF;}

td.outer{
    background: transparent !important;
    border: 0px solid #272727 !important;
    padding: 0px !important;          
}    

table{border-collapse:collapse;border:none;
    background:#323232!important;
}
table.main,table.mainouter{background-color:#fff;}
table.bottom{background: linear-gradient(#323232,#282828)!important;}

tr.alt {background:#323232}
tr.noalt {background:#282828}
table.main {background-color:#323232!important;} 
body > table.mainouter > tbody > tr:nth-child(1) > td > table  {width: 70% !important;} 

body > table.mainouter > tbody > tr:nth-child(2) > td > table:nth-child(13) > tbody > tr:hover{
color: #eea303!important;}



font[color*="black"],font[color*="#000000"],font[color*="#871F78"],font[color*="#220000"],font[color*="#003333"],font[color*="#330066"],font[color*="#330033"],font[color*="#000066"],font[color*="#990033"]{color:#ffffff!important} 
font[color*="#770000"],font[color*="#aa0000"],[color*="#990000"]{color:red!important}
font[color*="#330099"]{color:#2add00!important} 
font[color*="#006400"]{color:#0f8bfe!important} 
font[color*="brown"]{color:#fff000!important} 
tr[bgcolor*="#c5dcb9"]{background-color: rgba(70,180,0,0.5)!important;}
font[color*="blue"]{color:#caab1d!important}
a:link,a:visited{text-decoration:none;color:#00adee!important;}
b {color:#00adee!important;}


/* ------------Logo------------ */ 

img[src*="XYZ.jpg"],img[src*="llogo.JPG"],img[src*="logo.JPG"]{
    width:0;
    height:55px;
    padding-right:550px;
    background:transparent url(http://oi62.tinypic.com/13ykxtx.jpg) top left no-repeat;
    border: 0px!important;
}


/* ------------Input------------ */ 

input[type="text"],select,textarea{
  width:90%!important;
  background:#fff;
  border:1px solid #ccc;
  padding:1%;
  margin: 1%;
  font-size:95%;
  color:#555;
}

input[type="submit"],input[type="button"][value="Zaznacz wszystkie"],input[type="reset"]{
  
  background:#377ef0!important;
  border:0!important;
  padding:1%!important;
  margin: 1%!important;
  font-size:100%!important;
  color:#fff!important;
  cursor:pointer!important;
  transition:background .3s!important;
  -webkit-transition:background .3s!important;
}

input[type="submit"],input[type="button"][value="Zaznacz wszystkie"],input[type="reset"]:hover{
  background:#518def!important;
}



/* ------------Fixy------------ */ 

img[src*="JQEdLes.png"]{display: none !important}  
tr.messageboxback{background:#323232!important;}
iframe[name="shout_frame"] {height:290px!important; background:#FFF!important;}
body > br {display: none !important} 
a[class="tab"] {background:282828!important}


body::-webkit-scrollbar {
    width: 1em;
}
 
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
}
 
body::-webkit-scrollbar-thumb {
  background: #f7c63d;
  outline: 1px solid slategrey;
}

