@namespace url(http://www.w3.org/1999/xhtml);

/*
Version: 1.4
Author: @professordace
Website: web.fix-bug.ru
Date: 06 May 2014
Last Update: 15 September 2015
*/

@-moz-document domain("pikabu.ru") {

body{background-color: #333333 !important;color: #ccc !important;}
.pg_click {background-color:#333333 !important;}
.text_story_div {background-color:#111 !important;border:2px solid #222 !important;color:#ccc !important;}
.visited, .visited h1, .visited h2{color:#ccc !important;}
.rating{color:#5da130 !important;}
.rating .down{background-image:url("http://web.fix-bug.ru/pikabu/rating_n_down.png") !important;}
.rating .up{background-image:url("http://web.fix-bug.ru/pikabu/rating_n_up.png") !important;}
.rating .up a:hover, .rating .up_active{background-image:url("http://web.fix-bug.ru/pikabu/rating_t_up.png") !important;}
.rating .down a:hover, .rating .down_active{background-image:url("http://web.fix-bug.ru/pikabu/rating_t_down.png") !important;}
#hidebar span{background-color: #111 !important;border:1px solid #222;padding:5px !important;color:#ccc;}
a{color:#5da130 !important;}
a:hover{color:#66ff00 !important;}
#blk-upcoming .a-selected{background-color:#111 !important;}
.menu a{color:#ccc !important;}
.menu a:hover{color:#fff !important;}
div.story_head a, .info a{color:#5da130 !important;}
div.story_head a:hover{color:#66ff00 !important;}
.short, .info, div.story_head span{color:#ccc !important;}
.info a:hover{color:#66ff00 !important;}
#branding_ad_comment, .rating_bl{border-bottom-color:#000 !important;;}
.tag, .b-comments-reply__attach-files, .b-comment-toggle_type_expand{background-color:#111 !important;border-color:#222 !important;border-radius:0px !important;color:#5da130 !important;text-shadow:none;}
.tag:hover, .b-comments-reply__attach-files:hover, .b-comment-toggle_type_expand:hover{background: -webkit-linear-gradient(top,#75b54b 0,#5da130 100%);background: -o-linear-gradient(top,#75b54b 0,#5da130 100%) transparent;background: -moz-linear-gradient(top,#75b54b 0,#5da130 100%) repeat scroll 0 0 transparent;color:#fff !important;border-color:#5da130 !important;}
#set_autohide{border:none !important;}
.slide_up_container{background-color:#333 !important;}
.pb_close{background-color:#333 !important;color:#5da130 !important;}
.pb_close:hover{color:#66ff00 !important;}
.paral{background-image:url("http://web.fix-bug.ru/pikabu/header_bg13.jpg") !important;background-repeat:repeat-x;background-color:#111 !important;}
table#header_t{background:none !important;}
table#header_t .menu .active a, table#header_t .menu .active a:hover{background: -webkit-linear-gradient(top,#75b54b 0,#5da130 100%);background: -o-linear-gradient(top,#75b54b 0,#5da130 100%) transparent;background: -moz-linear-gradient(top,#75b54b 0,#5da130 100%) repeat scroll 0 0 transparent;border-bottom:none;}
table#header_t .menu li a{background-color:#333 !important;}
.search_block{background:url("http://web.fix-bug.ru/pikabu/search_area.png") repeat scroll 0 0 rgba(0, 0, 0, 0);}
input[type="text"]:active, input[type="password"]:active, textarea:active, input[type="password"]:focus, input[type="text"]:focus, textarea:focus{color:#eee !important;}
table#header_t .menu li a:hover{background-color:#4d4d4d !important;}
.rating_bl{background-color:#222222 !important;color:#5da130 !important;text-shadow:none;font-weight:bold;}
.login_f{border-color:#000 !important;box-shadow:0 0 3px #5da130 !important;}
.comment_msg{color:#ccc !important;}
input[type="text"]:active, input[type="password"]:active, textarea:active, input[type="password"]:focus, input[type="text"]:focus, textarea:focus{color:#eee !important;}
.textarea_new textarea:focus{border-color:#5da130;box-shadow:0 1px 4px #5da130;}
.textarea_new textarea{border-color:#222 !important;background-color:#111111 !important;}
.upl_comp{color:#5da130 !important;}
.upl_comp:hover{text-shadow:0 0 3px #457823 !important;}
.info_c a{color:#eee !important;}
.best_comm tr{background-color:#333 !important;}
.best_comm td{border-top:1px dashed #111 !important;}
.name{background-image:url("http://web.fix-bug.ru/pikabu/menu_top2.png");}
.name h4{background:none;border-bottom:1px dashed #5da120 !important;width:180px;margin-left:2px;} 
.login_f{height: 36px;}
.personals{background-image:url("http://web.fix-bug.ru/pikabu/menu_bg.jpg");}
.pmenu tbody tr:last-of-type td{background-image:url("http://web.fix-bug.ru/pikabu/menu_elements_top_bottom.jpg") !important;background-position: 0 -1px !important;}
.pc-line-sec{background-color:#111;border:2px solid #222;}
.pc-panel-bl{background-color:#222;}
.addlink_button{border-radius:2px !important;height:36px !important;background-image:url("http://web.fix-bug.ru/pikabu/addbutton2014.png");}
.active-item-sh-time:after{background-image:url("http://web.fix-bug.ru/pikabu/arr-show-t.png");}
.com_up{background-image:url("http://web.fix-bug.ru/pikabu/rating_z_up.png");}
.com_down{background-image:url("http://web.fix-bug.ru/pikabu/rating_z_down.png");}
.com_up_active, .com_up:hover{background-image:url("http://web.fix-bug.ru/pikabu/rating_y_up.png");}
.com_down_active, .com_down:hover{background-image:url("http://web.fix-bug.ru/pikabu/rating_y_down.png");}
.drop-d-show-time{background-color:#222 !important;}
.drop-d-show-time:hover{background-color:#333 !important;}
.show-time-open .active-item-sh-time,
.show-time-menu-pc a:hover {background-color:#333 !important;transition-duration:1s !important;}
.show-time-open,.calendar-ins{box-shadow:0 0 5px #5DA130 !important;border:1px solid #5DA130 !important;}
.calendar-btn{border-bottom:1px solid #5DA130 !important;border-top:1px solid #5DA130 !important;}
#datepicker,.calendar-btn a{background-color:#222 !important;}
.calendar-btn a:first-child{border-color:#5DA130;}
.ui-datepicker .ui-datepicker-title,.ui-datepicker th{color:#5DA130;}
.ui-datepicker td a:hover, .ui-datepicker td a.ui-state-active{background-color:#333;}
.ui-datepicker .ui-datepicker-prev{background-image:url("http://web.fix-bug.ru/pikabu/arr2-cal.png") !important;}
.comment-save{background-image:url("http://web.fix-bug.ru/pikabu/comm_save_nactive.png") !important;}
.submit_button, .submit_button_freshitems, .comment_desc_edit_submit_button, .moderator_add_tag_init{background: -webkit-linear-gradient(top,#75b54b 0,#5da130 100%);background: -o-linear-gradient(top,#75b54b 0,#5da130 100%) transparent;background: -moz-linear-gradient(top,#75b54b 0,#5da130 100%) repeat scroll 0 0 transparent;border-bottom:none;border:1px solid #6bae41;color:#77f707;text-shadow:0 0 1px #5da130;}
.submit_button:hover, .submit_button_freshitems:hover, .comment_desc_edit_submit_button:hover, .moderator_add_tag_init:hover{border-bottom:none;border:1px solid #63f203;box-shadow:0 0 3px #63f203;color:#63f203;text-shadow:0 0 1px #63f203;}
.footer_links{background-color:#555 !important;color:#999 !important;}
.footer_links2{background-color:#444 !important;color:#999 !important;}
.footer_links span{color:#999 !important;}
.footer_links #footer_links_td:after{content:"Редизайн «Pikabu» подготовлен студией «Web FixBug»";}
div.story_head h1:hover, .info a:hover{color:#66ff00 !important;}
.personals ul li span{color:#ccc;}
.grey{color:#ccc !important;background-color:#222 !important;}
table.editprofile_news td{color:#ccc !important;}
h2{color:#ccc !important;}
.comment_desc{color:#d4d4d4 !important;}
.gif-link-bl:hover{background: -webkit-linear-gradient(top,#75b54b 0,#5da130 100%);background: -o-linear-gradient(top,#75b54b 0,#5da130 100%) transparent;background: -moz-linear-gradient(top,#75b54b 0,#5da130 100%) repeat scroll 0 0 transparent;}
.gif-link-bl{text-shadow:none;border-color:#5DA130 !important;}
.pc-line-frst{border-bottom:1px dotted #5da130 !important;background:none !important;}
#story_title, #story_desc, #story_tags,#story_url_input,#story_video_input,#text_story_title,#text_story_desc{background-color:#333 !important;}
.tag_suggest_value{text-shadow:0 1px 0 #4d4d4d !important;color:#ccc !important;}
.uploadbutton{background: -webkit-linear-gradient(top,#75b54b 0,#5da130 100%);background: -o-linear-gradient(top,#75b54b 0,#5da130 100%) transparent;background: -moz-linear-gradient(top,#75b54b 0,#5da130 100%) repeat scroll 0 0 transparent;border-bottom:none;border:1px solid #5da130 !important;color: #eee !important;text-shadow:none !important;}
.uploadbutton:hover{color:#ddd !important;background: -webkit-linear-gradient(top,#5da130 0,#75b54b 100%);background: -o-linear-gradient(top,#5da130 0,#75b54b 100%) transparent;background: -moz-linear-gradient(top,#5da130 0,#75b54b 100%) repeat scroll 0 0 transparent;}
.add_story_cn_text:focus, .add_story_cn_textarea:focus{border-color:#5da130 !important;box-shadow: 0 1px 6px #75b54b !important;}
#tag_suggest_wrap div.tag_suggest:hover{background-color:#777 !important;}
.comment_msg_new{background-color: #434343 !important;}
.textarea_reply textarea, .comment_desc_edit textarea{background-color: #434343 !important;}
.tx_c{background-color:#333 !important;}
.b-comments{background-color: #3a3a3a;}
.b-comment{color:#777777 !important;}
.b-comment__body_highlight_newest::before{background-color: #4d4d4d !important;}
blockquote{background-color: #222;color: #5da130 !important}
.b-comments-reply__editable{border:2px solid #5da130 !important;}
.b-comments-reply__editable{background-color: #222 !important;}
.b-comments__reply{color:#ccc !important;}
.b-comment-toggle__icon{background-color: #393939;color: #5da130 !important;}
.b-social, .b-feed-panel, .b-story__content_type_text, .profile_wrap{background-color: #222222;box-shadow: 0 0 3px #5da130 !important;}
.b-story__content_type_text, .profile_wrap{color: #ccc !important;border: 1px solid #000 !important;text-shadow: none !important;}
.header-cont{/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#333333+29,222222+94,5da130+100 */
background: rgb(51,51,51); /* Old browsers */
background: -moz-linear-gradient(top, rgba(51,51,51,1) 29%, rgba(34,34,34,1) 94%, rgba(93,161,48,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(29%,rgba(51,51,51,1)), color-stop(94%,rgba(34,34,34,1)), color-stop(100%,rgba(93,161,48,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(51,51,51,1) 29%,rgba(34,34,34,1) 94%,rgba(93,161,48,1) 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, rgba(51,51,51,1) 29%,rgba(34,34,34,1) 94%,rgba(93,161,48,1) 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top, rgba(51,51,51,1) 29%,rgba(34,34,34,1) 94%,rgba(93,161,48,1) 100%); /* IE10+ */
background: linear-gradient(to bottom, rgba(51,51,51,1) 29%,rgba(34,34,34,1) 94%,rgba(93,161,48,1) 100%);}
.b-user-menu__main{border:1px solid #000 !important;box-shadow: 0 0 3px #5da130 !important;}
.b-user-menu__header{border-bottom: 1px dashed #5da130 !important;}
}
