@-moz-document domain('www.tumblr.com') {

a.new_post_label > i {
  height: 90px;
}

.new_post_label {
  /*padding: 61px 1px 18px 0 !important;*/
  /*text-transform: lowercase;*/
  /*color: midnightblue !important;*/
}
.new_post_label_text {font-size: 0}

.new_post_label > I {
  background: url('http://i.imgur.com/LVTn7Yd.png') no-repeat;
  color: transparent !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 2px -2px}
#new_post_label_photo > I {background-position: -73px -2px}
#new_post_label_quote > I {background-position: -148px -2px}
#new_post_label_link > I {background-position: -223px  -2px}
#new_post_label_chat > I {background-position: -298px -2px}
#new_post_label_audio > I {background-position: -373px -2px}
#new_post_label_video > I {background-position: -448px  -2px}

#new_post_label_text:hover > I {background-position: 2px -6px}
#new_post_label_photo:hover > I {background-position: -73px -6px}
#new_post_label_quote:hover > I {background-position: -148px -6px}
#new_post_label_link:hover > I {background-position: -223px  -6px}
#new_post_label_chat:hover > I {background-position: -298px -6px}
#new_post_label_audio:hover > I {background-position: -373px  -6px}
#new_post_label_video:hover > I {background-position: -448px  -6px}

#new_post_label_text:active > I {background-position: 2px -2px}
#new_post_label_photo:active > I {background-position: -73px -2px}
#new_post_label_quote:active > I {background-position: -148px -2px}
#new_post_label_link:active > I {background-position: -223px -2px}
#new_post_label_chat:active > I {background-position: -298px -2px}
#new_post_label_audio:active > I {background-position: -373px -2px}
#new_post_label_video:active > I {background-position: -448px -2px}

}
