css:
@-moz-document url-prefix("http://www.madwin.com") {
#header {
    width:900px;
    
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#da679e+0,9e3a6c+100 */
background: #da679e; /* Old browsers */
background: -moz-linear-gradient(top,  #da679e 0%, #9e3a6c 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#da679e), color-stop(100%,#9e3a6c)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #da679e 0%,#9e3a6c 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #da679e 0%,#9e3a6c 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #da679e 0%,#9e3a6c 100%); /* IE10+ */
background: linear-gradient(to bottom,  #da679e 0%,#9e3a6c 100%); /* W3C */
}

#header .zone_cadeau { display:none; }
#header a.lien_hompage {
 background:url('http://www.vedie.fr/dce/logo_madwin.png');  
    width:288px;
    height:104px;
    left:10px;
}

#header .zone_iframe { left:310px; }

#header #ss_menu {
    height: auto;
    width: 100px;
    left: 790px;
    top: 30px;
}

#header #ss_menu a { 
    display:inline-block; 
    margin-bottom:3px; 
    background:rgb(161,55,106); 
    width:100%; 
    height:auto !important; 
    position:relative; 
    text-decoration:none; 
    border-radius: 5px;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
	padding:5px 0 !important;
    box-shadow:0 0 5px 1px rgba(0, 0, 0, 0.4) inset,0 1px 3px 0 rgba(255,255,255,0.4);
}
#header #ss_menu a:hover {
    top:-1px;
    box-shadow:0 2px 3px 0 rgba(255, 255, 255, 0.4) inset,0 1px 3px 0 rgba(0,0,0,0.4);
}
#header #ss_menu a:before { 
    display:block; 
    width:100%; 
    text-align:center; 
    color:rgb(255,205,0); 
    font-family:roboto;
    font-size: 13px;
    text-transform: uppercase;
    position:relative;
}
#header #ss_menu a:hover:before { top:1px; }
#header #ss_menu a.mur:before { content:'Mur'; }  
#header #ss_menu a.buzz:before { content:'Coupe Buzz'; }  
#header #ss_menu a.parrainage:before { content:'Parrainage'; }  
#header #ss_menu a.gagnants:before { content:'Gagnants'; }  

@-webkit-keyframes header_jeux_gratuis {
 	0%, 100% {text-indent:-230px;}
 	99.99% {text-indent:468px;}
 }

#header #jeux_gratuits {
    width:468px;
    background:rgb(84,28,64); 
    border-radius:5px;
    -webkit-border-radius:5px;
	-moz-border-radius:5px;
        font-family: roboto;
    text-transform: uppercase;
    font-size: 13px;
    overflow:hidden;
    text-align:left;
    	-webkit-animation:linear header_jeux_gratuis 10s infinite ;
	-moz-animation:linear header_jeux_gratuis 10s infinite ;
	-o-animation:linear header_jeux_gratuis 10s infinite ;
	-ms-animation:linear header_jeux_gratuis 10s infinite ;
	animation:linear header_jeux_gratuis 10s infinite ;
}


#header + table td[colspan="2"]:first-of-type {
	background:#9e3a6c;   
	padding-left: 29px;
}

#header + table td[colspan="2"]:first-of-type > table {
    background:rgb(251,176,24) !important;
    width:871px;
}

#header:before, #header:after {
 content:'';
    display:block;
    position:absolute;
    top:100%;
    left:0;
    background:red;
    width:29px;
    height:31px;
}

#header:before {
    background:rgb(251,176,24);
}

#header:after {
    background:#9e3a6c;
    border-radius:0 0 30px 0;
}

img[src="/madwin/playbox/img/en-tete/cadre-gauche-haut.jpg"] { display:none; } 

#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(9) { padding-left:15px; } 
#header + table td[colspan="2"]:first-of-type > table tr > td:last-of-type { text-align:right; padding-right:20px; } 
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(2) img,
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(6) img,
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(13) img{
    display:none;
}
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(2):before,
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(6):before,
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(13):before{
 content:'';
 display:block; 
}
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(2):before {
    width:26px;
    height:24px;
    background:url('http://www.vedie.fr/dce/picto_points.png');   
}
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(6):before {
    width:14px;
    height:23px;
    background:url('http://www.vedie.fr/dce/picto_credits.png');   
}
#header + table td[colspan="2"]:first-of-type > table tr > td:nth-of-type(13):before {
    width:27px;
    height:23px;
    background:url('http://www.vedie.fr/dce/picto_parties.png');   
}
#titre_news {
 position:relative;  
    left: -120px; 
    height:30px;
    margin-bottom:20px;
}

#titre_news:before, #titre_news:after {
    content:'';
    position:absolute;
    top:0;
    left:0;
	right:-120px;
    height:1px;
    background:rgb(252,205,33);
    z-index:2;
    
}
#titre_news:after { top:100%; }
#titre_news .center .text {
    line-height:7px;
}
#titre_news .center {
 box-sizing:border-box;
-webkit-box-sizing:border-box;
    -moz-box-sizing:border-box;
    padding-top:2px;
    width: 800px;
    position:absolute;
    left:100px;
}

#titre_news .left {
 overflow:hidden;   
    background-position:-5px -5px;
}

#titre_news .left:before {
    content: '';
    height: 100%;
    width: 100px;
    position: absolute;
    top: -7px;
    left: 0;
    background: rgb(251,176,24);
    transform-origin: left bottom;
    transform: rotate(-30deg);
    
}

#zone_gauche_profil { position:relative;z-index:3; }


}
