css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bikepost.ru") {

  
body {
    background: #EEEEDD !important;
}

html body div div#header ul.pages a{
  color: #000!important;
}  
  
  html body div#container div#wrapper div#content div#content-inner div.topic h2.title a.title-topic {
    color: #000!important;
  }
  
  #content {
  background: #fff none repeat scroll 0 0!important;

  width: 100% !important; min-width: 750px !important;
  
  border: 1px solid #999 !important;
  -webkit-border-radius: 10px;border-radius: 10px
}  
  
#sidebar  {
  visibility: hidden!important;
  display: none!important;
}  

.active{
  background: #D6E4F4!important;
}
  
#header {
background: #D6E4F4!important;
    width: 96% !important; min-width: 750px !important;
  border-color: #000!important;
  border-width: 1px!important;
 border: 1px solid #999 !important;

} 
  
  html body div div#header a img.logo{
    display: none!important;
  }
  

  
}
