css:
@-moz-document regexp("(http://www.nextgenupdate.com/forums/|http://www.nextgenupdate.com/forums/forum.php|http://www.nextgenupdate.com/forums/index.php)") {
/* #2ba5f3, you're needed on your planet */
.jb_footer_clearboth {
    clear: both;
}

.jb_footer {
    height: 300px;
    background-color: #d6d9dc;
    margin-bottom: 10px;
    padding: 10px;
}

.jb_footer_col {
    float: left;
    margin-bottom: 10px;
}

.jb_footer_col.scol {
    width: 25%;
    background-color: #EBEBEB;
    height: 220px;
}

.jb_footer_col.lcol {
    width: calc(50% - 20px);
    background-color: #EBEBEB;
    height: 220px;
}

.jb_footer_col.sep {
    width: 10px;
    height: 220px;
}

.jb_footer_stats {
    width: 100%;
    height: 70px;
    background-color: #EBEBEB;
    text-align: center;
}

.jb_footer_col_header {
    width: 100%;
    background-color: /*[[jbcfbgcolor]]*/;
    color: #ecf0f1;
    padding-top: 8px;
    padding-bottom: 8px;
    text-align: center;
    font-weight: bold;
    text-transform: uppercase;
    font-family: 'Open Sans';
}

.jb_footer_col_body {
    height: calc(100% - 37px);
    overflow-y: scroll;
    margin-left: 6px;
}

.jb_footer_hot_forum {
    margin: 4px;
    padding: 6px;
    background-color: #F7F7F7;
    color: #2ba5f3;
    font-family: 'Open Sans';
}

.jb_footer_hot_forum.right {
    text-align: right;
    font-weight: bold;
}

.jb_footer_wollist {
    margin: 4px;
}

ol#wgo_onlineusers_list {
    background-color: #F7F7F7;
    padding: 6px;
}

.jb_footer_staffuser {
    margin: 4px;
    padding: 6px;
    background-color: #F7F7F7;
    color: #2ba5f3;
    font-family: 'Open Sans';
}

.jb_footer_staffuser.right {
    text-align: right;
    font-weight: bold;
}

.jb_footer_stats_box {
    display: inline-block;
    padding: 5px 15px 5px 15px;
    background-color: /*[[jbcfbgcolor]]*/;
    width: 80px;
    color: #ecf0f1;
    text-align: center;
    margin: 10px;
    text-transform: uppercase;
    font-family: 'Open Sans';
}

.jb_footer_stats_box.left {
    float: left;
}

.jb_footer_stats_box.right {
    float: right;
}

.jb_footer_stats_value {
    font-size: 20px;
}

}
