css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com"){
/* channel page */
ytd-browse[page-subtype="channels"] ytd-two-column-browse-results-renderer ytd-subscribe-button-renderer tp-yt-paper-button:not([subscribed]){
    display: none !important;
}
/* player page */
ytd-watch ytd-subscribe-button-renderer tp-yt-paper-button:not([subscribed]){
    visibility: hidden !important;
}
ytd-watch div#player div.branding-context-container-inner div.iv-branding-context-subscribe{
    display:none !important;
}
ytd-watch-flexy ytd-subscribe-button-renderer tp-yt-paper-button:not([subscribed]){
    visibility: hidden !important;
}
ytd-watch-flexy div#player div.branding-context-container-inner div.iv-branding-context-subscribe{
    display:none !important;
}
/* search page */
ytd-search ytd-subscribe-button-renderer tp-yt-paper-button:not([subscribed]){
    visibility: hidden !important;;
}
/* playlist page */
ytd-browse[page-subtype="playlist"] ytd-playlist-sidebar-secondary-info-renderer ytd-subscribe-button-renderer tp-yt-paper-button:not([subscribed]){
    visibility: hidden !important;
}
}
