css:
@-moz-document url-prefix("http://www.toro-league.com/forum") {
html, body {
	color: #000000;
	background: #2B2E36 url("http://i.imgur.com/qTGXpGB.png") fixed repeat;
}

div#wrap { border: 1px solid #583020; }

div#inside {
 	background-color: #424242;  
    border-color: #472A1F;
}

hr { border-color: #9C5532; 
}

h2 { color: #B65C30; }

h3 {
    border-bottom-color: #9C5532; 
}

div#wrap {
		background-color: #333333;
}

div.headerbar {
    background:
		url("http://i.imgur.com/4MQZGXy.png");
}

.ddcolortabs a {
    background: transparent url("http://i.imgur.com/DYRNm9b.png") no-repeat left top;
	background-color: #212121;
}

.ddcolortabs a span {
	background: transparent url("http://i.imgur.com/Wrt1vTi.png") no-repeat right top;
}

.ddcolortabs a:hover span {
    background-color: #342D2C;
}

.navbar {
	background: url("http://i.imgur.com/LZGiW56.png") repeat-x #9E373E;
}

.forabg {
	background-color: #212121;
	background-image: url("http://i.imgur.com/Tz0c39r.png");
}

.forumbg {
	background-color: #212121;
	background-image: url("http://i.imgur.com/Tz0c39r.png");
}


ul.forums {
    background-color: #F1BBA1;
}

li.row {
    background-color: #F1BBA1;
}

ul.topiclist {
	border-left-color: #342D2C;
}

li.row:hover {
	background-color: #EC9E78;
}

li.row:hover dd {
	border-left-color: #000000;
}

a:link	{ color: #B65C30; }
a:visited { color: #8E4E2E; }
a:hover { color: #B65C30; }
a:active { color: #B65C30; }

a.forumtitle:link	{ color: #B65C30; }
a.forumtitle:visited { color: #8E4E2E; }

a.topictitle { color: #8E4E2E; }
a.topictitle:hover { color: #B65C30; }
a.topictitle:active { color: #B65C30; }

.content h2, .panel h2 {
    border-bottom-color: #000;
}

.bg1	{ background-color: #F1CCB1; }
.bg2	{ background-color: #F3CFB5; }
.bg3	{ background-color: #F3CFB5; }


p.author {
	border-bottom: 1px solid #824A00;
}	
.signature {
	border-top: 1px solid #824A00;
}

div#postingbox.panel {
    background-color: #F1CCB1; }

#tabs a {
    color: white;
	background-image: url("http://i.imgur.com/hVMO6e6.png");
}

#tabs a span {
	background-image: url("http://i.imgur.com/O7fL9hS.png");
	color: #FFFFFF;
}

#tabs a:hover span {
	color: #ffffff;
}

#tabs .activetab a {
	border-bottom-color: #ffffff;
}

 .activetab a span {
	color: #ffffff;
}

#tabs .activetab a:hover span {
	color: #ffffff;
}

blockquote {
	background-color: #FAF1E2;
}


#navigation a {
	background-color: #8D502C;
    color: #ffffff;
}

#navigation a:hover {
	background-color: #FAF1E2;
	color: #000000;
}

#navigation #active-subsection a {
	color: #8D502C;
}

#navigation #active-subsection a:hover {
	color: #BF6A37;
}

.postlink	{ color: #B65C30; }
.postlink:visited { color: #8E4E2E; }
.postlink:hover { color: #B65C30; }
.postlinkactive { color: #B65C30; }

.panel {
	background-color: #F1CCB1;
}

label {
    color: #8E4E2E;
}
}
