css:
@-moz-document domain("yaplakal.com") {
body, table, tbody, td, tr  {
	background: #333 !important;
	color: #aaa !important;
}

.rating-short-value a{
	color: #000 !important;
}
a {color: #888899 !important;}
.subtitle {color: #308aaf !important;}
.darkrow1 {background: #222 !important;}
.drop-menu a{color: #cacaca !important;}
#user_box a{color: #cacaca !important;}

#top-line {display:none !important; height:0px !important;} 
#content, #ipbwrapper, #content-inner {
	background: #111111 !important;
	color: #888866 !important;}
.signature { color: #aa0 !important;}
.darkrow3{background: none !important;}
.row4{background: #5e5e5e !important;}
.row4 a{color: #ccc !important;}
.post-rank {background-color: #eee !important;}

/* Menu */
#main-menu a {
	color: #dfdf00 !important;
}

/* Form elements */
.forminput, .textinput {
    background-color: #cacaca !important;
}

/* Images */
.attach img, .basic-img img, center img, .rating-value, .rating-short-value{
	opacity: 0.6;
}
.attach img:hover, .basic-img img:hover, center img:hover, .rating-value:hover, .rating-short-value:hover{
	opacity: 1;
}
}
