css:
@-moz-document domain("panel.dreamhost.com") {
body {
    background-color: #303030;
    color: #f5f5f5;
}
.panel-main-navigation-group.is-open {
    background-color: #303030;
    border-bottom-color: #424242;
    border-top-color: #424242;
    box-shadow: none !important;
}
.panel-header{
    background-color: #212121;
}
.panel-page-header{
    background-color: #303030;
    border-bottom: 1px solid #424242;
    box-shadow: none;
}
h1, h2, h3, h4 {
    color: #f5f5f5;
}
.panel-main-navigation-toggle, .panel-main-navigation-button {
    color: #F5F5F5;
}
.panel-block {
    border-top: none;
    box-shadow: none;
}
.panel-account-list-active {
    color: #f5f5f5;
}
.global-alerts {
    display: none;
}
.panel-nav {
    border-right-color: #424242;
    color: #f5f5f5;
}
.panel-help-dropdown, .panel-search-results {
    background-color: #303030;
    border-color: #303030;
}
.ac_even, .panel-help-link {
    color: #f5f5f5 !important;
}
.ac_over, .panel-help-link:hover {
    color: #35AEE6 !important;
}
.panel-help-dropdown:after, .panel-search-results:after, .panel-help-dropdown:before, .panel-search-results:before {
    border-bottom-color: #303030;
}
#dashboard-side section {
    border-top-color: #424242;
}
#dashboard-side .wrap {
    border-left-color: #424242;
}
.fancytable tbody tr.alt td.actionsalt a, .fancytable tbody tr.alt td.actions a {
    background-color: #424242;
}
.btn, .buttonbig, .buttonbig div, .buttonback, .buttonback a span, .fancytable tbody tr td.actionsalt a, .fancytable tbody tr td.actions a {
    background-color: #424242;
    box-shadow: none;
    color: #F5F5F5;
    border: none;
}
.fancytable tbody tr td.actionsalt a, .fancytable tbody tr td.actions a, .fancytable tbody tr td.actionsalt a:hover, .fancytable tbody tr td.actions a:hover {
    background: none;
    background-color: #303030;
    border: none;
}
.btn:hover, .buttonbig:hover, .buttonbig div:hover, .buttonback:hover, .buttonback a:hover span, .fancytable tbody tr td.actionsalt a:hover, .fancytable tbody tr td.actions a:hover {
    background-color: #212121;
    color: #F5F5F5;
}
.btn:focus {
    color: #f5f5f5;
}
.buttonbig a div {
    background-color: #424242 !important;
}
.buttonbig a div:hover {
    background-color: #212121 !important;
}
.contentbox, #reg_box, .activecontentbox, .activecontentbox_body, .activecontentbox_head, .activecontentbox_head h1 {
    background-color: #303030 !important;
    color: #f5f5f5;
    background: none !important;
    border: none;
    box-shadow: none;
}
.contentbox_head p, .reg-text p, .tlds, .activecontentbox_head p {
    color: #f5f5f5;
}
.contentbox_head h1 {
    color: #35AEE6;
}
a {
    color: #35AEE6;
}
.fancyform {
    color: #f5f5f5;
}
.ac_input {
    background-color: #303030;
}
.fancytable {
    border: none;
}
.fancytable tbody tr th, .fancytable thead tr th, .fancytable tfoot tr th {
    background-color: #212121;
    color: #f5f5f5;
    border: none;
}
.fancytable tbody tr td {
	background-color: #424242;
    color: #f5f5f5;
    border: none;
}
.fancytable tbody tr.alt td {
	background-color: #303030;
    color: #f5f5f5;
    border: none;
}
.current_balance_box {
    background-color: #212121;
    border: none;
}
.button {
    background: none !important;
    border: none !important;
    background-color: #424242 !important;
    color: #f5f5f5 !important;
}
.button:hover {
    background-color: #212121 !important;
}
.registrations_table tbody tr:not(.alt) td input.button {
    background-color: #303030 !important;
}
.tab-content .tab-pane.active {
    background-color: #212121 !important;
    border: none !important;
}
.nav-tabs .active a, .nav-tabs li a:hover, .nav-tabs .active a:hover {
    background-color: #212121;
    color: #f5f5f5;
    border: none !important;
}
.buttonbox, .buttonbox_head, .buttonbox_head h1, .buttonbox_body {
    background: none;
    background-color: #424242;
}
.buttonbox_highlighted, .buttonbox_head_highlighted, .buttonbox_head_highlighted h1, .buttonbox_body_highlighted {
    background: none;
    background-color: #212121;
}
.buttonbox_head h1 label, .buttonbox_head_highlighted h1 label {
    color: #f5f5f5;
}
.historyheaderthem, .historyheaderus {
    background: none;
    background-color: #212121;
    border: none;
}
.historythem, .historyus {
    background-color: #424242;
    border: none;
}
.ui-dialog, .ui-dialog-buttonpane, .ui-dialog-content {
    background-color: #303030;
    border: none;
}
.mysql-info {
    background-color: #212121 !important;
    border: none !important;
}
.ui-dialog div strong {
    color: #f5f5f5;
}
.expanderclosed, .expanderopen {
    background: none;
    background-color: #212121;
    border: none;
}
.plan-price a {
    color: #f5f5f5;
}
.select-option {
    background: #303030;
}
}
