@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bhtu.com") {
    * {
        -webkit-box-sizing: border-box;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
    }
    html, body, div, span, applet, object, iframe,
    h1, h2, h3, h4, h5, h6, p, blockquote, pre,
    a, abbr, acronym, address, big, cite, code,
    del, dfn, em, img, ins, kbd, q, s, samp,
    small, strike, strong, sub, sup, tt, var,
    b, u, i, center,
    dl, dt, dd, ol, ul, li,
    fieldset, form, label, legend,
    table, caption, tbody, tfoot, thead, tr, th, td,
    article, aside, canvas, details, embed,
    figure, figcaption, footer, header, hgroup,
    menu, nav, output, ruby, section, summary,
    time, mark, audio, video {
        /* margin: 0;
        padding: 0;
        border: 0;*/
        font-size: 100%;
        font: inherit;
        vertical-align: baseline;
    }
    article, aside, details, figcaption, figure,
    footer, header, hgroup, menu, nav, section {
        display: block;
    }
    table {
        border-collapse: collapse;
        border-spacing: 0;
        font-size: 14px;
        line-height: 1.3;
    }
    body > div.appFrame {
        background: #e5e5e5 url("http://subtlepatterns.com/patterns/notebook.png") repeat left top;
    }
    #ieTable {
        background: #fff;
    }
    .ieTable > tbody > tr {
        border-left: 1px solid #fff;
        border-right: 1px solid #fff;
    }
    .ieTable > tbody > tr > td {
        padding: 4px;
    }
    td.ieNormal, td.ieUneditable {
        border: none;
        border-top: 1px solid #DDD;
    }
    .ieUneditable {
        background: transparent;
        padding: 4px;
        line-height: 1.1;
    }
    #ieTable tr:hover  {
        background: #f4f4f4;
        border-left: 1px solid #000;
        border-right: 1px solid #000;
    }
    #ieTable > tbody > tr:nth-child(1):hover {
        border-left: 0px none;
        border-right: 0px none;
        background: #fff;
    }
    html {
        font-family: sans-serif;
        -webkit-text-size-adjust: 100%;
        -ms-text-size-adjust: 100%;
    }
    body {
        font-size: 16px;
        line-height: 1.3;
        font-family: 'Open Sans','Helvetica Neue',helvetica,arial,clean,sans-serif !important;
    }
    .header {
        border-bottom: 3px solid #000;
    }
    #quickNavX {
        padding: 5px 10px 0 0;
    }
    #quickNavX input#cboText {
        border: 1px solid #A3CC40;
        border-radius: 4px;
        font-size: 14px;
        margin: 0px 10px;
        color: #FFF;
        background: #000 none repeat scroll 0% 0%;
        line-height: 1.3;
        padding: 6px 9px;
    }
    #quickNavX #searchTypeDisplay {
        padding: 6px 9px;
        border: 1px solid rgb(255, 255, 255);
        border-radius: 4px;
        line-height: 1.3;
        margin: 0px !important;
        font-size: 14px;
    }
    .siteMenu {
        border-top: 0px solid #000;
        border-bottom: 0px solid #000;
        background-color: #A3CC40;
        background: #30A943 url("http://iammatteblack.com/wp-content/themes/iamb/img/bg-green.jpg") no-repeat left center;
        /*    box-shadow: 0px 4px 20px -10px rgba(0, 0, 0, 0.5) inset;*/
        font-size: 14px;
        font-weight: bold;
        color: #FFF;
        line-height: 1;
        text-align: left;
        padding: 0px 15px !important;
    }
    .siteMenu > a:link,
        .siteMenu > a:active,
        .siteMenu > a:visited {
            color: #007913;
            margin: 8px 6px;
            padding: 8px;
            color: rgb(255, 255, 255);
            text-shadow: 1px 2px 2px rgba(0, 0, 0, 0.5);
        }
    .siteMenu > a:hover {
        color: #fff;
        text-decoration: none;
        background: #000;
        border-radius: 4px;
    }
    .siteMenu > span {
        margin: 5px 6px 8px 6px;

        padding: 5px;
        text-transform: uppercase;
        color: rgb(255, 255, 255);
        background: rgba(0, 0, 0, 0.5) none repeat scroll 0% 0%;
        border-radius: 4px;
    }
    .siteMenu .claro .dijitButtonText {
        line-height: 0px !important;
    }
    .appHeader .hierarchyMenu{
        color: #fff;
        font-style: italic;
    }
    .appHeader .hierarchyMenu > a{
        color: #a5a5a5;
        text-decoration: none;
    }
    .appHeader .hierarchyMenu > a:hover{
        text-decoration: underline;
    }
    .appHeader .hierarchyMenu > strong{
        color: #fff;
        font-style: normal;
        text-decoration: none;
    }
    .appHeader > .hotMenu {
        font-size: 12px;
        background: #fff;
        border-bottom: 1px solid rgb(0, 0, 0);
        padding: 10px;
        margin: 0;

        margin-bottom: 20px;
        box-shadow: 0px 10px 10px -10px #e5e5e5;
        background: #000;
        color: #fff;
    }
    .appHeader .hotMenu a.hotMenu {
        border: 0px none;
        margin: 0 0 0 15px;
        padding: 0;
        line-height: 1;
        text-transform: uppercase;
        font-weight: 900;
        color: #fff;
    }
    .appHeader > hr {
        display: none;
        clear: both;
        background:none;
        margin: 0px;
        padding: 0px;
    }
    .appBody {
        margin: 0 auto;
        width: 100%;
    }
    #actionForm > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(5) {
        background: #000;
        color: #fff;
    }
    #actionForm > table > tbody > tr:nth-child(2) > td,
    #actionForm > table > tbody > tr:nth-child(4) > td {
        padding: 10px 1%;
    }
    #actionForm > table > tbody > tr:nth-child(4) > td {
        border-top: 3px solid #ccc;
    }
    #actionForm > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > a{
        padding: 10px;
        display: inline-block;
        color: #000;
        background: #A3CC40;
        border-radius: 4px;
        text-transform: uppercase;
        font-weight: 700;
        text-decoration: none;
        letter-spacing: 1px;
    }
    #actionForm > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > a:hover{
        background: #000;
        cursor: pointer;
        color: #fff;
    }
    #actionForm > table > tbody > tr:nth-last-child(2) > td > table > tbody > tr > td > a{
        padding: 10px;
        display: inline-block;
        color: #000;
        background: #A3CC40;
        border-radius: 4px;
        text-transform: uppercase;
        font-weight: 700;
        text-decoration: none;
        letter-spacing: 1px;
    }
    #actionForm > table > tbody > tr:nth-last-child(2) > td > table > tbody > tr > td > a:hover{
        background: #000;
        cursor: pointer;
        color: #fff;
    }
    #inlineToolbar .inlineToolbar {
        width: 100%;
/*         border: 1px solid #202020;
        background-color: rgba(0,0,0,0.4); */
        border-radius: 4px;
/*         box-shadow: 0px 0px 10px rgba(0,0,0,0.5) inset; */
        margin: 0 0 10px 0px;
        padding: 10px;
    }
    .infoTableHead, .infoTableHead a, .infoTableHead a:visited, .infoTableHead a:hover, .yui-dt-table th {
        background-color: transparent;
    }
    .inlineToolbar {
        width: 98%;
        border: 1px solid #202020;
        background-color: rgba(0,0,0,0.4);
        background-color: rgba(255,255,255,0.9);
        border-radius: 4px;
        box-shadow: 0px 0px 10px rgba(0,0,0,0.5) inset;
        margin: 0px 1%;
        padding: 10px;
    }
    .infoTableHead {
        background-color: #000;
        color: #fff;
        border: 0px solid #000;
        border-bottom: 2px solid #DDD;
        border-top: 1px solid #000;
        padding: 10px 3px;
        text-transform: lowercase;
        letter-spacing: 2px;
        line-height: 0.8;
        vertical-align: middle !important;
        font-weight: 900;
        font-variant: small-caps;
    }
    #ieTable > tbody > tr:nth-child(1) > td.infoTableHead > span {
        font-size: inherit !important;
    }
    #ieTable > tbody > tr:nth-child(1) > td.infoTableHead > span a{
        background: rgba(255, 255, 255,0);
        border: 0px solid #000;
        color: #fff;
        border-radius: 4px;
        padding: 0px 4px;
        line-height: 1;
        text-decoration: none;
        vertical-align: middle;
    }
    #ieTable > tbody > tr:nth-child(1) > td.infoTableHead > span a:hover{
        background: rgba(255, 255, 255,1);
        color: #000;
    }
    #btnSubmitBottom, #btnCancelBottom {
        background: #fff;
        border: none;
        padding: 3px;
        border-radius: 3px;
        color: #000;
    }
    #btnSubmitBottom:hover, #btnCancelBottom:hover {
        background: #A3CC40;
        color: #000;
        cursor: pointer;
    }
}
