css:
@-moz-document domain("do4a.com") {
#content .pageContent {
    /* background: #333 !important; */
    /*background-image: url(http://darkwallpapers.ru/_ph/8/193093453.jpg);*/
    background-attachment: fixed;
    background-size:130%;
    background-color:#202020;
    background-image:url(http://2.firepic.org/2/images/2015-09/16/9zylud3mxoxz.jpg);
    background-repeat:no-repeat;
}

h1,h2,h3,p, .blockLinksList a, .blockLinksList label, body, .subHeading a, .messageUserBlock a.username{
    color:#989898 !important;
}

.secondaryContent{
    /*background-image:linear-gradient(to top, #111, #555);*/
    background:#333;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3{
    text-shadow:none;
    background:#222;
    color:#989898;
    border-top:0;
    background-image:linear-gradient(to top, #111, #444);
}

.blockLinksList a, .blockLinksList label{
    text-shadow:none;
}


.footerLegal .pageContent img:hover{
    box-shadow:0 0 10px blue;
}

.footerLegal .pageContent img{
    transition:0.5s;
}

/*.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label{
    transition: 0.15s;
    box-shadow: 0 0 5px #0EB9AE;
    background-image: linear-gradient(to top, #111, #555);
}*/

.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label{
    transition: 0.1s;
    background:rgba(250,232, 0, 0.2);
    color:white !important;
    border-bottom:2px solid rgba(250,232, 0, 0.7);
    border-radius:0;
    padding-bottom:3px;
}

.breadcrumb .crust .arrow{
     border-left-color: #333;
}

.breadcrumb .crust .arrow span{
   border-left-color: #777;
}

.breadcrumb .crust:last-child a.crumb{
    background-image: linear-gradient(to bottom, #555, #333,#555);
}

.breadcrumb .crust a.crumb, .ugc a:link, .ugc a:visited, .message .publicControls a, .secondaryContent a{
    color: #11B0A6;
}

.breadcrumb .crust a.crumb{
    box-shadow: inset 0 1px #777;
}

.breadcrumb{
    background-image: -o-linear-gradient(#555, #333, #555);
    background-image: -moz-linear-gradient(#555, #333, #555);
    background-image: -webkit-linear-gradient(#555, #333, #555);
    box-shadow: inset 0 1px #777, 0 1px rgba(0,0,0,0.06);
}

.PanelScroller .panel, .PanelScrollerOff .panel{
    background-image:linear-gradient(to top, #555, #111, #555);
}

.PanelScroller .scrollContainer, .PanelScrollerOff .panel{
    background-image:linear-gradient(to top, #555, #111, #555);
    border:1px solid #666;
}

body .navTabs .navTab.selected .tabLinks li a{
    color: #d9d9d9 !important;
}

.timeTo.timeTo-white div{
    background-image:linear-gradient(to top, #222, #777);
    color:white;
}

#loginBar a{
    color:white !important;
}

.primaryContent{
    background:#222;
}

body #NewsList div.message{
    border:1px solid #999;
}

.sectionMain, body #NewsList div.message .messageMeta{
    background:#333;
}

.ugc a:hover, .ugc a:focus{
    background:#555;
    box-shadow:0 0 10px #11B0A6;
}
.ugc a:link, .ugc a:visited{
    transition:0.3s;
}

.subHeading{
    background:none;
    background-image: linear-gradient(to top, #222, #555);
    text-shadow:none;
    margin: 0px auto 0;
    box-shadow:none;
    -webkit-box-shadow:none;
}

body #NewsList div.message{
    box-shadow:none;
    -webkit-box-shadow:none;
}

.breadcrumb .crust a.crumb, .breadcrumb .crust a.crumb:hover, .breadcrumb .crust a.crumb:active{
    background-color:transparent;
}

.breadcrumb .crust:hover .arrow span{
    border-left-color:#11B0A6;
}

.breadcrumb .crust:hover a.crumb{
    background:#11B0A6;
    color:white;
}

.sidebar{
    text-shadow:none;
}

img{
    opacity:0.7;
    transition-delay:1s;
}

img:hover{
    opacity:1;
    transition:0.3s;
}

.messageList{
    background-color:#222;
}

.primaryContent{
    border-top:none;
}

.message .likesSummary{
    background:#555;
    text-shadow:none;
}

body .messageSimple .comment{
    background:#131313;
}

.bbCodeQuote .quoteContainer{
    background:#252525;
}

img.mceSmilie, img.mceSmilieSprite, .avatar img, .avatar .img, .avatarCropper{
    opacity:1;
}

footer{
    background-image:url(http://5.firepic.org/5/images/2015-09/14/x3y7km0t1e53.png);
    background-repeat:repeat-x;
}

body #headerMover #header{
    background-image:url(http://5.firepic.org/5/images/2015-09/14/2r1bxfxvu60h.png);
    background-size:cover;
}


.messageUserBlock .arrow span{
    border-left-color:#555;
}

.messageUserBlock{
    background:#555;
}

.messageUserBlock h3.userText{
    text-shadow:none;
}

.bbCodeQuote .attribution{
        background-image: -webkit-linear-gradient(#ccc 50%, #c5c5c5 50%);
        border-bottom: 1px solid #999;
}

.PageNav a{
    background-color:#ccc;
    border: 1px solid #11B0A6;
}

.PageNav a.currentPage{
    background-color:#11B0A6;
    color:white;
}

.PageNav a:hover, .PageNav a:focus{
    background-color:rgba(17,176, 166, 0.7);
    color:white;
}

.bbCodeBlock{
    border:1px solid #ddd;
}

.userBanner.bannerStaff{
    background-color:#999;
}

.bbCodeQuote .quoteContainer{
    background-image: linear-gradient(to top, #333, #484848);
}

.bbCodeQuote .quoteContainer .quoteExpand{
    background: -webkit-linear-gradient(top, rgba(232, 241, 254, 0) 0%, #666 80%);
    color:#11B0A6;
}

.secondaryContent{
    transition:0.3s;
    opacity:0.7;
}

.secondaryContent:hover, .PanelScroller:hover .scrollContainer{
    opacity:1;
}

.PanelScroller .scrollContainer{
    transition:0.3s;
    opacity:0.5;
}

body #NewsList div.message .messageMeta{
    background:#222;
    margin:0 !important;
}

.sectionMain{
    background:transparent;
}

.sectionMain .message .privateControls{
    margin-left:7px !important;
}

.sectionMain .message .publicControls{
    margin-right:7px !important;
}

body .navTabs .navTab.selected .blockLinksList, #logo img, .Menu .blockLinksList, .Menu .secondaryContent, body .profilePage .primaryUserBlock .secondaryContent,  .profilePage .mast .section.infoBlock .secondaryContent:last-child, .node .nodeLastPost{
    opacity:1 !important;
}

body .profilePage .mast .section.infoBlock .secondaryContent, .profilePage .eventList:first-of-type li:first-child{
    background:transparent;
}

.Menu .primaryContent{
    background-color: rgba(34, 34, 34, 0.96);
}

.Menu{
    border: 1px solid #999;
    border-top: 5px solid #999;
}

.Menu .secondaryContent{
    background-color: rgba(45, 45, 45, 0.96);
}

a.PageNavPrev, a.PageNavNext{
    border:1px none black;
}

.messageList li.nestedContainer:hover{
    border-left-color:#393939 !important;
}

.messageList{
    border:1px solid #999;
}

.bbCodeQuote .quoteContainer .quote{
    color:#bbb;
}

#vk_groups{
    opacity:0.5;
    transition:0.3s;
}

#vk_groups:hover{
    opacity:0.9;
}

body .quickReply{
    background-color: rgba(34,34,34,0.7);
}

.pollBlock .pollOption label:hover{
    background: rgba(17,176, 166, 0.7);
    color: White;
}

.timeTo.timeTo-white div {
    background-image: linear-gradient(to top, #222, #777) !important;
    color: #ddd !important;
}

.nodeList .categoryStrip{
    color:#989898 !important;
    text-shadow:none;
    background-image: linear-gradient(to top, #111, #333);
    -webkit-box-shadow: inset 0 1px rgba(255,255,255,0.3);
     border-color: #ccc;
}

.nodeList .forumNodeInfo, body .nodeList .categoryForumNodeInfo{
     background-image: linear-gradient(to top, #222, #292929);
     border: 1px solid #999;
}

#QuickSearch.active, .formPopup, .formPopup .controlsWrapper{
    background:transparent;
    background-image: linear-gradient(to top, #222, #333);
}

.formPopup .controlsWrapper{
    border:1px solid #555;
}

.Popup .arrowWidget{
    background:transparent url('http://2.firepic.org/2/images/2015-09/16/j9g7qxitrygv.png') no-repeat -32px 0;
}

.node .nodeLastPost{
    background-image: linear-gradient(to top, #222, #333);
    text-shadow:none;
}

.textCtrl{
    background:#555;
    color:#ccc;
}

.textCtrl:focus, .textCtrl.Focus{
    color:#111;
}

body .formPopup .primaryControls input.textCtrl{
    background-image:linear-gradient(to bottom, #151515, #333);
    color:#ccc;
}

.discussionList .sectionHeaders, .sectionFooter, #DiscussionListOptionsHandle a, .tabs{
   background-image:linear-gradient(to top, #111, #333);
    text-shadow:none;
}

.discussionList .sectionHeaders a{
    color:#989898;
}

.discussionList .sectionHeaders dd a[href]:hover{
    background-image:linear-gradient(to bottom, #111, #333);
}

.discussionListItem {
    background-color: #222;
    border-top: 1px solid #999;
    border-bottom: 1px solid #999;
}

.discussionListItem .posterAvatar, .discussionListItem .stats{
    background:#303030;
}

a:link, a:visited{
    color:#3F7DE9;
}

body #headerMover #header{
    -webkit-box-shadow: inset 0 1px #777;
     box-shadow: inset 0 1px #777;
}

.LikeText a{
    color:#11B0A6;
}

.tabs li a{
    background-image:linear-gradient(to top, #222, #444);
    color:#989898;
}

.tabs li a:hover{
    background-image:linear-gradient(to bottom, #222, #444);
}
}
