css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("horriblesubs.info")
{
  /* Background */
  body,div,input {
    background-color: #262222 !important;
  }

  /*[[bannerImage]]*/

  /* Packlist */
  .anime0 {
    background: #333 !important;
  }
  .anime1 {
    background: #242424 !important;
  }
  .anime0:hover, .anime1:hover {
    background: #666 !important;
  }
  
  /* Access header list */
  .menu-item a {
    color: #D5D5D5 !important;
  }
  .menu-item:hover {
    background: #444 !important;
  }
  
  /* Text colors */
  h1, h3, h4 {
    color: #E7E7E7 !important;
    background: none !important;
  }
  body,div,h2,input, .rls-label a, .default {
    color: #C3C3C3 !important;
  }
  
  /* Text - Links */
  a, .entry-content a {
    color: #7E97AC !important;
    border: none !important;
    text-decoration: none !important;
  }
  
  /* darken text without link */
  .linkless {
    color: #3A3A3D !important;
  }
  
  /* release hover */
  .release-info:hover {
    background: #444 !important;
  }
  
  /* Header will not get bold when hover*/
  #access a:hover {
    font-weight: unset !important;
  }
  
  /* Hover any link */
  .res-link a:hover, .rls-label a:hover, .dl-link a:hover, a:hover, .entry-content a:hover {
    background: none !important;
    color: #fff !important;
    font-weight: bold !important;
  }
  
  /* Shows page letters navigation */
  .alpha-nav {
    transition-duration: .5s !important;
  }
  .alpha-nav:hover {
    background: #363333 !important;
  }
  
  /* Make iframe bigger for the bold text */
  .textwidget > iframe {
    width: 110px !important;
  }

  /* Schedule table alt color fix */
  .schedule-page-item:nth-child(2n+1) {
   background: none !important;
  }
  
  /* Twitter button */
  .menu-item-130418 {
    background: none !important;
  }
}
  /*********/
  /* Clock */
  /*********/

@-moz-document url-prefix("http://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1")
{
  
  /* Background */
  #c1 {
	   background: none !important;
  }
  
  /* Text */
  #t1, #t1a {
    color: #C3C3C3 !important;
    font-weight: bold !important;
  }
}

  /************/
  /* Comments */
  /************/

@-moz-document regexp("(https|http)://.*disqus.com/embed/comments/.*(horriblesubs.info).*")
{
  
  .highlighted {
    background: none !important;
  }
  
  /* Text */
  div.post-message p {
    color: #EBEBEB !important;
  }
  
  /* Load new comments buttons */
  .alert.info, .alert.realtime, .btn {
	  background: #123F66 none repeat scroll 0% 0% !important;
  }
  
  /* Links */
  a:link, .nav .dropdown-toggle, .nav > ul > li > a, .nav > ul > li > div a, .post-content footer .voting .vote-down, .post-content footer a, h2 {
    color: #7E97AC !important;
  }
  
  /* Number thingy next to recommend */
  .nav-secondary > ul > li.recommend .thread-likes a .label-count {
    background: #C3C3C3 !important;
    color: black !important;
  }
}
