css:
@-moz-document domain("monstercat.com") {
.catlogo {
    background: url('http://i.imgur.com/oneu1Dz.png')no-repeat left;
}

.main-logo {
    max-width: 500px;
}

img[src*="//cdn.shopify.com/s/files/1/0204/1478/t/14/assets/monstercat-logo.png?6936658636213776737"]{
    background-image: url("http://i.imgur.com/hh2L8GC.png");
    width:400px;
    display:inline-block;
    padding:28px 0 0 0;
    height: 0px
}

body {
    background-color: #151515;
}

.content {
    background: #151515;
}

.fullrow {
    background: #151515;
}

.drawer {
    background-color: #151515;
    border: 0px solid white;
}

.fullrowbar {
    background: #000;
}

.link a {
    color: white;
}

.links h6 {
    color: white;
}

.rightsocials .copyright h6 {
    color: white;
}

.fa {
    color: white;
}

.dividerbar {
    background-color: white;
}

.white {
    color: white;
}

.h1 {
    color: white;
}

.page-title>h1 {
    color: white;
}

h2 {
    color: white;
}

h2.title {
    color: white;
}

h3 {
    color: white;
}

h4 {
    color: white;
}

h4.title {
    color: white;
}

h5 {
    color: white;
}

h6 {
    color: white;
}

.light {
    color: white;
}

a h6.indent {
    color: white;
}

p {
    color: white;
}

.articles .article {
   background: #4A4A4A; 
}

.tag-heading {
    color: white;
}

ul.tags>li>a, .tag-button {
    color: black;
}

.sc-player a {
    color: white;
}

.sc-player ol.sc-trackslist li.active a {
    color: #4E4E4E;
}

.sc-track-duration {
    background-color: #151515;
    color: white;
}

.sc-scrubber .sc-time-indicators {
    color: white;
}

.sc-player ol, .sc-player li {
    border-bottom: 1px solid rgba(255,255,255,.25);
}

#livesched #airtimes {
    background: #000;
}

#livesched #airtimes #wrapper li {
    border-bottom: 1px solid rgba(255,255,255,.3);
}

span.price, .recommendify_product_price {
    color: white;
}

.product .quickshop {
    background: rgba(0,0,0,1);
}

.controls h4 {
    color: white;
}

.controls input[type="checkbox"]+label {
    color: white;
}

.top-bar {
    background: #000;
}

.top-bar-section li:not(.has-form) a:not(.button), .nav-link {
    color: #FFFFFF;
}

.secondary-bar .top-bar-section li:not(.has-form) a:not(.button), .nav-link {
    color: white;
}

a, a:link, a:visited {
    color: white;
}

.heapBox .holder {
    color: white !important;
}

.btn {
    color: white;
    border: 0px solid #eee;
    background: #262626;
    margin-right: 2em;
    height: 38px;
}

.breadcrumb strong {
    color: white;
}

.arrow-space {
	color: white;
}

#price-preview .money {
    color: white;
}

.product-tabs h4 {
    color: white;
}

.product-description>.product-desc-wrapper {
    color: white;
}

.recommendify_product_title>p {
    color: white;
}

table thead, th {
    background: #333;
}

.top-bar-section li:not(.has-form) a:not(.button):hover {
    color: #7D7D7D;
}

.nav-link:focus, .nav-link:hover {
    color: #999;
}

#cart .image {
    color: white;
}

table>thead>tr>th {
    color: white;
}

table tr:nth-of-type(odd) {
    background: #222;
}

table tr:nth-of-type(even) {
    background: #111;
}

textarea[rows] {
    background: #333;
    color: #FFFFFF;
}

label {
    color: #FFF;
}

table tr>td {
    color: #FFF;
}
}
