/*********** [ css for infobox templates ] ***************/

.infoboxtemp {
  clear: right; 
  border: 1px #ffff solid; 
  border-collapse: collapse;
  background: #000000; 
  border-bottom-right-radius: .8em; 
  border-bottom-left-radius: .8em; 
  -moz-border-radius-bottomright: .8em; 
  -moz-border-radius-bottomleft: .8em; 
  -webkit-border-bottom-right-radius: .8em; 
  -webkit-border-bottom-left-radius: .8em; 
  margin-top: -1.4em; 
  margin: 0 0 1em 1em; 
  font-size: 12px;
  color: #ffffff;
}
.header {
  border-top: 0px solid #111111; 
  text-align: center; 
  color: #e5e5ed; 
  background-color: #282828;
  font-size:8pt;
}
.name {
  border-top: 0px solid #111111; 
  border-style: none;
  text-align: center; 
  color: #e5e5ed; 
  background-color: #383838;
  font-size:larger;
  font-variant:small-caps;
}

.infoboxinfoheader {
  background: #222222; 
  padding-left:6px; 
  padding-right:6px;
  border-top: 0px solid #111111; 
  border-right: 0px solid #111111; 
  border: 0px;
  color: #cccccc; 
  font-size:smaller;
}
.infoboxinfodata {
  background: #2B2B2B; 
  padding-left:6px; 
  padding-right:6px; 
  border-top: 0px solid #111111; 
  border: 0px;
  font-size:smaller;
}
.infoboxinfodata ul{
  margin-top: 0px !important;
  margin-bottom: 0px !important;
}
.infoboxinfodata p{
  margin-top: 0px !important;
  margin-bottom: 0px !important;
}
.infoboximage {
  background: #000000; 
  border-top: 0px solid #111111; 
  border-right: 0px solid #111111; 
  border-style: none;
  color: #cccccc; 
  font-size:smaller; 
  text-align: center;
}
.infoboxfooter {
  border-bottom-right-radius: .8em; 
  border-bottom-left-radius: .8em; 
  -moz-border-radius-bottomright: .8em; 
  -moz-border-radius-bottomleft: .8em; 
  -webkit-border-bottom-right-radius: .8em; 
  -webkit-border-bottom-left-radius: .8em; 
  text-align: center; 
  color: #e5e5ed; 
  font-variant:small-caps; 
  font-size:smaller; 
  background-color: #383838;
  border-top: 0px solid #111111;
  border-style: none;
} 


