css:
@namespace "http://www.w3.org/1999/xhtml";

@-moz-document domain("v2ex.com") {
    #Bottom {
        box-shadow:0 0 4px rgba(0,0,0,0.14), 0 4px 8px rgba(0,0,0,0.28);
    }

    #Main .box:nth-child(-2n+4):not(.transparent) {
        box-shadow:0 0 6px rgba(0,0,0,.16),0 6px 12px rgba(0,0,0,.32);
    }

    #Search div {
        background-image:none!important;
        border-image-repeat:repeat;
        border-image-slice:27 27 27 50;
        border-image-source:url(https://v2ex.com/static/img/qbar_light@2x.png);
        border-image-width:27px 27px 27px 50px;
        width:auto!important;
    }

    #Search input {
        transition:width .2s cubic-bezier(.4,0,.2,1);
        width:100px;
    }

    #Search input:focus {
        width:200px;
    }

    #Top {
        background-color:#607D8B;
        background-image:none;
        box-shadow:0 0 4px rgba(0,0,0,0.14), 0 4px 8px rgba(0,0,0,0.28);
        width:100%;
    }

    #Top .content {
        width:100%;
    }

    #Top a {
        color:#fff;
        font-size:14px;
        font-weight:bold;
        outline:none;
    }

    #Top td:nth-child(1) {
        left:10px;
        position:absolute;
    }

    #Top td:nth-child(1) a {
        background-image:url("https://dn-cz-misc.qbox.me/v2ex/logo@2x.png");
        background-size:94px 30px;
        display:inline-block;
        height:30px;
        width:94px;
    }

    #Top td:nth-child(1) img {
        display:none;
    }

    #Top td:nth-child(2) {
        position:absolute;
        right:10px;
        top:7px;
    }

    #Top td:nth-child(3) {
        display:flex;
        margin:6px auto;
        text-align:left;
        width:970px;
    }

    #Wrapper {
        background-color:#ECEFF1;
        background-image:none;
        margin-top:10px;
    }

    .box,a.tab_current:link,a.tab_current:visited,a.tab_current:active,a.count_livid:link,a.count_livid:active,.vote:link,.vote:visited,.vote {
        border-radius:0;
    }

    .box:not(.transparent) {
        border-bottom:none;
        box-shadow:0 0 2px rgba(0,0,0,0.12), 0 2px 4px rgba(0,0,0,0.24);
    }

    .cell {
        border-bottom:1px solid #e5e5e5;
    }

    .collapsed {
        display:block;
    }

    .mll {
        background-color:#f6f6f6;
        border-bottom:1px solid #e5e5e5!important;
        border-style:none none solid!important;
        margin-left:-10px;
        margin-top:-10px;
        padding:10px;
        width:660px;
        transition:background-color .2s cubic-bezier(.4,0,.2,1);
    }

    .mll,.ml,.mle {
        border-radius:0;
        box-shadow:none!important;
        resize:none!important;
    }

    .mll:focus {
        background-color:#fff;
    }
    
    #topic_form {
        margin: -10px;
    }
    
    #topic_form td {
        padding: 0;
    }
    
    #topic_form input[type=submit] {
        margin: 10px;   
    }
    
    #topic_title, #topic_content {
        border:none;
        border-bottom: 1px solid #e5e5e5;
        width: 660px !important;
        padding: 10px;
    }

    .sl {
        border-radius:0;
        box-shadow:none!important;
    }

    .super.button {
        background-color:#78909c;
        background-image:none;
        border:none!important;
        border-radius:2px;
        box-shadow:0 0 2px rgba(0,0,0,.12),0 2px 4px rgba(0,0,0,.24);
        color:#fff!important;
        display:inline-block;
        font-size:13px;
        font-weight:bold;
        height:32px;
        line-height:30px;
        outline:none;
        padding:0 16px;
        text-shadow:none!important;
        transition:box-shadow .2s cubic-bezier(.4,0,.2,1),background-color .2s cubic-bezier(.4,0,.2,1);
    }

    .super.button:focus {
        background-color:#546e7a;
        box-shadow:0 0 6px rgba(0,0,0,.16),0 6px 12px rgba(0,0,0,.32);
        outline:none;
        transition:none;
    }

    .super.button:hover {
        background-color:#607d8b;
        box-shadow:0 0 4px rgba(0,0,0,.14),0 4px 8px rgba(0,0,0,.28);
        transition:none;
    }

    .tag:link,.tag:visited,img.avatar {
        border-radius:2px;
    }

    .topic_buttons {
        background:#eee;
        border-radius:0;
    }

    a.balance_area:link,a.balance_area:visited,.balance_area {
        background:none repeat scroll 0 0 #f5f5f5!important;
        border-radius:0!important;
    }

    a.tb:hover {
        background-color:#ccc;
        border-radius:0;
    }

    balance_area:hover {
        background:none repeat scroll 0 0 #f9f9f9;
    }

    body {
        background-color:#ECEFF1;
        font-family:'Noto Sans CJK SC', sans-serif !important;
        min-width:1200px;
    }
}
