css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://backpack.tf/"), url-prefix("https://backpack.tf/") {
#page-content {padding-top: 10px}
div.row {margin-bottom: 2px}
  
.profile .user-handle-container .handle {max-width:480px;display: inline-block;overflow: hidden;text-overflow: ellipsis; }
  .profile .user-handle-container {line-height: 24px}
#backpack div.page-number {margin-top:8px;margin-bottom: 2px}
.tag.bottom-right            { font-size:95%!important;  }
.tag.bottom-right > i.fa-tag { font-size:100%!important; color:green; opacity:0.7; }
i.fa-gift                { font-size:150%!important;  color:rgba(255,0,0,1.0); text-shadow: 2px 2px rgba(0,0,0,1.0); }
li.item.notrade {    box-shadow: 0px 0px 33px 5px black inset!important; border-color:black!important}
#filterslot  { display:none!important }
.row { padding:0px!important}
.item .icon-stack .killstreaker.killstreaker-professional { background-image:none!important; background-color: Orange }
.item .icon-stack .killstreaker.killstreaker-specialized { background-image:none!important; background-color: Green }
.item .icon-stack .killstreaker.killstreaker-basic { background-image:none!important; background-color: Gray ;}
.item .icon-stack > div.killstreaker { width:4px!important; height:42px!important; background-size: 36px!important;  box-shadow: 0px 0px 1px 1px rgba(0, 0, 0, 0.4) inset!important; }
.paint[style="background-color: #2d2d24"] { background-color:#343!important }
.paint[style="background-color: #3b1f23"] { background-color:#633!important }
.paint.secondary[style="background-color: #18233d"] { background-color:#337!important }
  
  .item > .tag.bottom-right > span[style="color: #D32CE6"] { display: none}
  
  .popover.right { margin-left:30px!important;}
  .popover.left { margin-left:-30px!important;}
  .popover .btn {line-height: 33px}
  .popover #search-bazaar ,
  .popover a.btn[href*=tf2wh] {display: none}

  #omg, #omg2, #sounds, .profile span.ingame, #blurb, #social {display: none!important;}
}
